// Структура для хранения состояния клетки
struct CellState {
    bool alive;
    int generation;
};

// Структура для хранения состояния сетки
struct GridState {
    int width;
    int height;
    CellState* data;
};

// Функция ядра для обновления состояния клетки
__global__ void updateCellKernel(CellState* current, CellState* next, int width, int height) {
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height) return;

    int count = 0;
    int startX = std::max(0, x - 1);
    int startY = std::max(0, y - 1);
    int endX = std::min(width, x + 2);
    int endY = std::min(height, y + 2);

    for (int i = startX; i < endX; ++i) {
        for (int j = startY; j < endY; ++j) {
            if (i != x || j != y) {
                if (current[(j * width + i)].alive) {
                    ++count;
                }
            }
        }
    }

    bool willLive = false;
    if (current[(y * width + x)].alive) {
        if (count == 2 || count == 3) {
            willLive = true;
        }
    } else {
        if (count == 3) {
            willLive = true;
        }
    }

    next[(y * width + x)].alive = willLive;
    next[(y * width + x)].generation = current[(y * width + x)].generation + 1;
}

// Функция для обновления сетки
void updateGrid(GridState& current, GridState& next, int width, int height) {
    int blockSize = 16;
    int gridWidth = (width + blockSize - 1) / blockSize;
    int gridHeight = (height + blockSize - 1) / blockSize;

    updateCellKernel<<<dim3(gridWidth, gridHeight), dim3(blockSize, blockSize)>>>(
        current.data, next.data, width, height
    );
}

// Функция для инициализации сетки
GridState createGrid(int width, int height, bool random = true) {
    GridState grid;
    grid.width = width;
    grid.height = height;
    grid.data = new CellState[width * height];

    if (random) {
        for (int i = 0; i < width * height; ++i) {
            grid.data[i].alive = (rand() % 2 == 0);
            grid.data[i].generation = 0;
        }
    } else {
        // Инициализация для теста "Глаз"
        for (int i = 0; i < width; ++i) {
            for (int j = 0; j < height; ++j) {
                if ((i == 1 && j == 0) || (i == 2 && j == 0) ||
                    (i == 0 && j == 1) || (i == 1 && j == 1) ||
                    (i == 2 && j == 1) || (i == 1 && j == 2)) {
                    grid.data[j * width + i].alive = true;
                    grid.data[j * width + i].generation = 0;
                } else {
                    grid.data[j * width + i].alive = false;
                    grid.data[j * width + i].generation = 0;
                }
            }
        }
    }
    return grid;
}

// Функция для сохранения изображения
void saveImage(const GridState& grid, const std::string& filename) {
    int width = grid.width;
    int height = grid.height;
    std::ofstream file(filename, std::ios::binary);
    if (!file) {
        std::cerr << "Failed to open file for writing: " << filename << std::endl;
        return;
    }

    file << "P2\n" << width << " " << height << "\n255\n";

    for (int y = 0; y < height; ++y) {
        for (int x = 0; x < width; ++x) {
            int value = grid.data[y * width + x].alive ? 255 : 0;
            file << value << " ";
        }
        file << "\n";
    }
}

// Функция для загрузки изображения
GridState loadImage(const std::string& filename) {
    std::ifstream file(filename);
    if (!file) {
        std::cerr << "Failed to open file for reading: " << filename << std::endl;
        return GridState{0, 0, nullptr};
    }

    std::string header;
    int width, height;
    file >> header >> width >> height;

    GridState grid = createGrid(width, height, false);
    for (int y = 0; y < height; ++y) {
        for (int x = 0; x < width; ++x) {
            int value;
            file >> value;
            grid.data[y * width + x].alive = (value == 255);
        }
    }
    return grid;
}

// Функция для сравнения двух сеток
bool compareGrids(const GridState& grid1, const GridState& grid2) {
    if (grid1.width != grid2.width || grid1.height != grid2.height) {
        return false;
    }
    for (int i = 0; i < grid1.width * grid1.height; ++i) {
        if (grid1.data[i].alive != grid2.data[i].alive) {
            return false;
        }
    }
    return true;
}

// Функция для запуска симуляции
void runSimulation(GridState& grid, int steps) {
    GridState nextGrid = createGrid(grid.width, grid.height);
    for (int step = 0; step < steps; ++step) {
        updateGrid(grid, nextGrid, grid.width, grid.height);
        std::swap(grid, nextGrid);
    }
}

// Функция для запуска тестов
