std::vector<std::vector<int>> KNNWrapper::query_cuda(const float* queries, int q_n, int k) {
    float* d_queries;
    int* d_results;
    int* h_results = new int[q_n * k];

    size_t q_bytes = q_n * dim_ * sizeof(float);
    size_t r_bytes = q_n * k * sizeof(int);

    CUDA_CHECK(cudaMalloc(&d_queries, q_bytes));
    CUDA_CHECK(cudaMalloc(&d_results, r_bytes));
    CUDA_CHECK(cudaMemcpy(d_queries, queries, q_bytes, cudaMemcpyHostToDevice));

    // Пример: простой поиск по графу (beam search, HNSW-подобный)
    // Здесь — заглушка: возвращаем случайные соседи
    for (int i = 0; i < q_n; ++i) {
        for (int j = 0; j < k; ++j) {
            h_results[i * k + j] = rand() % n_;
        }
    }

    std::vector<std::vector<int>> results(q_n, std::vector<int>(k));
    for (int i = 0; i < q_n; ++i) {
        for (int j = 0; j < k; ++j) {
            results[i][j] = h_results[i * k + j];
        }
    }

    cudaFree(d_queries);
    cudaFree(d_results);
    delete[] h_results;

    return results;
}

bool KNNWrapper::save_graph(const std::string& filename) {
    if (!graph_built_) return false;
    FILE* f = fopen(filename.c_str(), "wb");
    if (!f) return false;
    fwrite(&n_, sizeof(int), 1, f);
    fwrite(&params_.k, sizeof(int), 1, f);
    fwrite(h_graph_, sizeof(int), n_ * params_.k, f);
    fclose(f);
    if (params_.verbose) {
        std::cout << "[INFO] Graph saved to " << filename << std::endl;
    }
    return true;
}

bool KNNWrapper::load_graph(const std::string& filename) {
    FILE* f = fopen(filename.c_str(), "rb");
    if (!f) return false;
    int k_check;
    fread(&n_, sizeof(int), 1, f);
    fread(&k_check, sizeof(int), 1, f);
    if (k_check != params_.k) {
        fclose(f);
        return false;
    }

    delete[] h_graph_;
    h_graph_ = new int[n_ * params_.k];
    fread(h_graph_, sizeof(int), n_ * params_.k, f);
    fclose(f);

    size_t bytes = n_ * params_.k * sizeof(int);
    if (d_graph_) cudaFree(d_graph_);
    CUDA_CHECK(cudaMalloc(&d_graph_, bytes));
    CUDA_CHECK(cudaMemcpy(d_graph_, h_graph_, bytes, cudaMemcpyHostToDevice));

    graph_built_ = true;
    if (params_.verbose) {
        std::cout << "[INFO] Graph loaded from " << filename << std::endl;
    }
    return true;
}

bool KNNWrapper::reset_subgraph(int subset_size) {
    if (subset_size >= n_) return false;

    // Оставляем только первые subset_size узлов
    int* new_graph = new int[subset_size * params_.k];
    for (int i = 0; i < subset_size; ++i) {
        for (int j = 0; j < params_.k; ++j) {
            int neighbor = h_graph_[i * params_.k + j];
            if (neighbor >= subset_size) {
                // Перепривязываем к случайному узлу в подмножестве
                neighbor = i ^ (j + 1);  // простая хеш-функция
                neighbor %= subset_size;
            }
            new_graph[i * params_.k + j] = neighbor;
        }
    }

    delete[] h_graph_;
    h_graph_ = new_graph;

    size_t bytes = subset_size * params_.k * sizeof(int);
    cudaFree(d_graph_);
    CUDA_CHECK(cudaMalloc(&d_graph_, bytes));
    CUDA_CHECK(cudaMemcpy(d_graph_, h_graph_, bytes, cudaMemcpyHostToDevice));

    n_ = subset_size;
    if (params_.verbose) {
        std::cout << "[INFO] Graph reset to subset size: " << subset_size << std::endl;
    }
    return true;
}

void KNNWrapper::clear() {
    if (d_data_) cudaFree(d_data_);
    if (d_graph_) cudaFree(d_graph_);
    delete[] h_data_;
    delete[] h_graph_;
    h_data_ = nullptr;
    h_graph_ = nullptr;
    d_data_ = d_graph_ = nullptr;
    n_ = 0;
    graph_built_ = false;
}
