### 🛠️ `utils.h` — Вспомогательные функции (чтение `.fvecs`)

```cpp
// utils.h
#ifndef UTILS_H
#define UTILS_H

#include <cstdio>
#include <cstdlib>

// Чтение .fvecs: первые 4 байта — размерность, затем векторы
inline std::tuple<float*, int, int> load_vectors_from_fvecs(const char* filename, int nmax = -1) {
    FILE* f = fopen(filename, "rb");
    if (!f) return {nullptr, 0, 0};

    int dim;
    fread(&dim, sizeof(int), 1, f);

    fseek(f, 0, SEEK_END);
    size_t fsize = ftell(f);
    fclose(f);

    int n = (fsize / ((dim + 1) * sizeof(float)));
    if (nmax > 0 && nmax < n) n = nmax;

    float* data = new float[n * dim];
    f = fopen(filename, "rb");

    for (int i = 0; i < n; ++i) {
        int read_dim;
        fread(&read_dim, sizeof(int), 1, f);
        if (read_dim != dim) {
            fclose(f);
            delete[] data;
            return {nullptr, 0, 0};
        }
        fread(data + i * dim, sizeof(float), dim, f);
    }
    fclose(f);

    return {data, n, dim};
}

#define CUDA_CHECK(call) \
    do { \
        cudaError_t error = call; \
        if (error != cudaSuccess) { \
            fprintf(stderr, "CUDA error at %s:%d - %s\n", __FILE__, __LINE__, cudaGetErrorString(error)); \
            exit(1); \
        } \
    } while(0)

#endif
