### ✅ `build.js` — скрипт сборки

```js
const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

const ROOT = path.resolve();
const SRC = path.join(ROOT, 'src');
const PUBLIC = path.join(ROOT, 'public');

// Утилита: создать каталог, если не существует
function ensureDir(dirPath) {
  if (!fs.existsSync(dirPath)) {
    fs.mkdirSync(dirPath, { recursive: true });
    console.log(`✅ Создан каталог: ${dirPath}`);
  }
}

// Генерация парсера из grammar.ne с помощью nearley
function generateParsers() {
  const parserSrc = path.join(SRC, 'parsers', 'grammar.ne');
  const parserDest = path.join(SRC, 'generated', 'parser.js');

  if (!fs.existsSync(parserSrc)) {
    console.warn(`⚠️ Файл грамматики не найден: ${parserSrc}`);
    return;
  }

  ensureDir(path.dirname(parserDest));

  try {
    execSync(`npx nearleyc ${parserSrc} -o ${parserDest}`);
    console.log(`✅ Парсер сгенерирован: ${parserDest}`);
  } catch (err) {
    console.error(`❌ Ошибка при генерации парсера:`, err.stderr.toString());
    process.exit(1);
  }
}

// Компиляция шаблонов Pug
function compileTemplates() {
  const pug = require('pug');
  const templatesSrc = path.join(SRC, 'templates');
  const outputDir = PUBLIC;

  if (!fs.existsSync(templatesSrc)) {
    console.warn(`⚠️ Каталог шаблонов не найден: ${templatesSrc}`);
    return;
  }

  ensureDir(outputDir);

  fs.readdirSync(templatesSrc)
    .filter(file => path.extname(file) === '.pug')
    .forEach(file => {
      const srcPath = path.join(templatesSrc, file);
      const htmlName = path.basename(file, '.pug') + '.html';
      const destPath = path.join(outputDir, htmlName);

      const html = pug.renderFile(srcPath, { pretty: true });
      fs.writeFileSync(destPath, html);
      console.log(`✅ Шаблон скомпилирован: ${destPath}`);
    });
}

// Копирование статических ресурсов в public
function copyAssets() {
  const assets = path.join(SRC, 'assets');
  const dirs = ['css', 'js', 'img', 'fonts'];

  dirs.forEach(dir => {
    const srcDir = path.join(assets, dir);
    const destDir = path.join(PUBLIC, dir);
    if (fs.existsSync(srcDir)) {
      fs.cpSync(srcDir, destDir, { recursive: true });
      console.log(`✅ Скопировано: ${srcDir} → ${destDir}`);
    } else {
      ensureDir(destDir);
      console.log(`📁 Пустой каталог создан: ${destDir}`);
    }
  });
}

// Основная функция сборки
function build() {
  console.log('🚀 Запуск сборки...\n');

  // 1. Создаём публичный каталог
  ensureDir(PUBLIC);

  // 2. Генерируем парсеры
  generateParsers();

  // 3. Компилируем шаблоны
  compileTemplates();

  // 4. Копируем статику
  copyAssets();

  console.log('\n🎉 Сборка завершена успешно!');
}

// Запуск
if (require.main === module) {
  build();
}

module.exports = build;
```

