## ✅ 3. Поддержка нескольких GPU с MAPS-Multi (гипотетический интерфейс)

Предположим, что **MAPS-Multi** предоставляет:
- `maps::init()`, `maps::finalize()`
- `maps::DeviceManager` для управления GPU
- `maps::DataDistributor` для разделения сетки
- `maps::synchronize()` для синхронизации между GPU

```cpp
#include "maps_multi.h"  // Гипотетический заголовок

class GameOfLifeMultiGPU {
private:
    int width, height;
    int num_gpus;
    std::vector<int> local_widths;  // ширина для каждого GPU
    std::vector<unsigned char*> d_current;
    std::vector<unsigned char*> d_next;
    std::vector<size_t> pitch;
    std::vector<cudaStream_t> streams;

public:
    GameOfLifeMultiGPU(int w, int h) : width(w), height(h) {
        maps::init();
        num_gpus = maps::DeviceManager::getDeviceCount();
        d_current.resize(num_gpus);
        d_next.resize(num_gpus);
        pitch.resize(num_gpus);
        streams.resize(num_gpus);

        // Разделение ширины между GPU (по столбцам)
        local_widths = maps::DataDistributor::splitDomain(width, num_gpus);

        for (int dev = 0; dev < num_gpus; ++dev) {
            cudaSetDevice(dev);
            cudaStreamCreate(&streams[dev]);

            size_t w_local = local_widths[dev];
            cudaMallocPitch(&d_current[dev], &pitch[dev], w_local * sizeof(unsigned char), height);
            cudaMallocPitch(&d_next[dev], &pitch[dev], w_local * sizeof(unsigned char), height);
        }
    }

    ~GameOfLifeMultiGPU() {
        for (int dev = 0; dev < num_gpus; ++dev) {
            cudaSetDevice(dev);
            cudaFree(d_current[dev]);
            cudaFree(d_next[dev]);
            cudaStreamDestroy(streams[dev]);
        }
        maps::finalize();
    }

    void simulateStep(std::vector<std::vector<unsigned char>>& host_grids) {
        for (int dev = 0; dev < num_gpus; ++dev) {
            cudaSetDevice(dev);
            int w_local = local_widths[dev];

            // Копируем данные на GPU
            cudaMemcpy2DAsync(
                d_current[dev], pitch[dev],
                host_grids[dev].data(), w_local * sizeof(unsigned char),
                w_local * sizeof(unsigned char), height,
                cudaMemcpyHostToDevice, streams[dev]
            );

            // Запуск ядра
            dim3 blockSize(BLOCK_SIZE, BLOCK_SIZE);
            dim3 gridSize((w_local + BLOCK_SIZE - 1) / BLOCK_SIZE, (height + BLOCK_SIZE - 1) / BLOCK_SIZE);

            gameOfLifeKernel<<<gridSize, blockSize, 0, streams[dev]>>>(
                d_current[dev], d_next[dev], w_local, height, pitch[dev] / sizeof(unsigned char)
            );

            // Копируем результат обратно
            cudaMemcpy2DAsync(
                host_grids[dev].data(), w_local * sizeof(unsigned char),
                d_next[dev], pitch[dev],
                w_local * sizeof(unsigned char), height,
                cudaMemcpyDeviceToHost, streams[dev]
            );
        }

        // Синхронизация между GPU (если нужно обмениваться границами)
        maps::synchronize();

        // Ожидание завершения всех потоков
        for (int dev = 0; dev < num_gpus; ++dev) {
            cudaSetDevice(dev);
            cudaStreamSynchronize(streams[dev]);
        }
    }
};
