## ✅ 4. Регрессионные тесты и проверка

```cpp
bool runRegressionTest() {
    const int width = 64, height = 64;
    const int steps = 10;

    // Создаём эталонную версию (CPU)
    std::vector<unsigned char> ref_grid(width * height);
    initializeGrid(ref_grid, width, height);

    auto stepCPU = [&](std::vector<unsigned char>& grid) {
        std::vector<unsigned char> next = grid;
        for (int y = 0; y < height; ++y) {
            for (int x = 0; x < width; ++x) {
                int alive = 0;
                for (int dy = -1; dy <= 1; ++dy) {
                    for (int dx = -1; dx <= 1; ++dx) {
                        if (dx == 0 && dy == 0) continue;
                        int nx = (x + dx + width) % width;
                        int ny = (y + dy + height) % height;
                        alive += grid[ny * width + nx];
                    }
                }
                int idx = y * width + x;
                next[idx] = (grid[idx] && (alive == 2 || alive == 3)) || (!grid[idx] && alive == 3) ? 1 : 0;
            }
        }
        grid = std::move(next);
    };

    auto ref_copy = ref_grid;
    for (int i = 0; i < steps; ++i) {
        stepCPU(ref_copy);
    }

    // Запуск на GPU
    GameOfLifeMultiGPU sim(width, height);
    std::vector<std::vector<unsigned char>> host_grids(sim.num_gpus);

    // Разделение сетки по GPU
    int offset = 0;
    for (int dev = 0; dev < sim.num_gpus; ++dev) {
        int w_local = sim.local_widths[dev];
        host_grids[dev].resize(w_local * height);
        for (int y = 0; y < height; ++y) {
            std::copy(
                ref_grid.begin() + y * width + offset,
                ref_grid.begin() + y * width + offset + w_local,
                host_grids[dev].begin() + y * w_local
            );
        }
        offset += w_local;
    }

    for (int i = 0; i < steps; ++i) {
        sim.simulateStep(host_grids);
    }

    // Сборка результата
    std::vector<unsigned char> gpu_result(width * height);
    offset = 0;
    for (int dev = 0; dev < sim.num_gpus; ++dev) {
        int w_local = sim.local_widths[dev];
        for (int y = 0; y < height; ++y) {
            std::copy(
                host_grids[dev].begin() + y * w_local,
                host_grids[dev].begin() + (y + 1) * w_local,
                gpu_result.begin() + y * width + offset
            );
        }
        offset += w_local;
    }

    // Сравнение
    if (ref_copy != gpu_result) {
        saveGridToPGM(ref_copy, width, height, "expected.pgm");
        saveGridToPGM(gpu_result, width, height, "actual.pgm");
        std::cerr << "Тест провален! Изображения сохранены." << std::endl;
        return false;
    }

    std::cout << "Тест пройден!" << std::endl;
    return true;
}
