#ifndef KNN_WRAPPER_H
#define KNN_WRAPPER_H

#include <vector>
#include <string>

struct KNNQueryResult {
    std::vector<int> indices;   // индексы ближайших соседей
    std::vector<float> distances; // расстояния до них
};

class KNNWrapper {
public:
    KNNWrapper(int dim, int k, bool verbose = false, bool debug = false);
    ~KNNWrapper();

    // Загрузка данных: [num_vectors x dim] из бинарного файла
    bool load_data(const std::string& filename, int num_points = -1);

    // Построение графа KNN
    bool build_graph();

    // Уточнение графа (например, итеративное улучшение)
    bool refine_graph(int num_iterations = 5);

    // Генерация эталонных данных (ground truth) для оценки
    bool generate_ground_truth(const std::string& query_file, int k_gt = 100);

    // Оценка графа (например, recall@k)
    float evaluate_recall(int k_eval = 10);

    // Выполнение запросов
    std::vector<KNNQueryResult> query(const std::vector<float>& queries, int k_query);

    // Сохранение графа
    bool save_graph(const std::string& filename);

    // Загрузка графа
    bool load_graph(const std::string& filename);

    // Сброс графа для подмножества (например, первые N точек)
    bool reset_subset(int subset_size);

    // Очистка
    void clear();

private:
    int dim_;           // размерность векторов
    int k_;             // число соседей
    bool verbose_;
    bool debug_;

    float* d_data_;     // данные на GPU
    int* d_graph_;      // граф: [num_points x k]
    float* h_data_;     // данные на CPU
    int* h_graph_;      // граф на CPU
    int num_points_;    // количество точек

    // Ground truth для оценки
    std::vector<std::vector<int>> ground_truth_;

    // Внутренние CUDA-функции (объявлены в .cu)
    bool build_graph_cuda();
    bool refine_graph_cuda(int iterations);
    std::vector<KNNQueryResult> query_cuda(const float* queries, int num_queries, int k_query);
};

#endif // KNN_WRAPPER_H
