### 🧠 `knn_wrapper.cu` — Реализация с CUDA

```cpp
// knn_wrapper.cu
#include "knn_wrapper.h"
#include "utils.h"
#include <cuda_runtime.h>
#include <thrust/device_vector.h>
#include <thrust/host_vector.h>
#include <thrust/sort.h>
#include <thrust/sequence.h>
#include <iostream>
#include <cmath>
#include <algorithm>

KNNWrapper::KNNWrapper(int dim, int k, bool verbose, bool debug)
    : dim_(dim), k_(k), verbose_(verbose), debug_(debug),
      d_data_(nullptr), d_graph_(nullptr), h_data_(nullptr), h_graph_(nullptr),
      num_points_(0) {}

KNNWrapper::~KNNWrapper() {
    clear();
}

void KNNWrapper::clear() {
    if (d_data_) cudaFree(d_data_);
    if (d_graph_) cudaFree(d_graph_);
    if (h_data_) delete[] h_data_;
    if (h_graph_) delete[] h_graph_;

    d_data_ = nullptr;
    d_graph_ = nullptr;
    h_data_ = nullptr;
    h_graph_ = nullptr;
    num_points_ = 0;
}

bool KNNWrapper::load_data(const std::string& filename, int num_points) {
    auto [data, loaded_dim, loaded_num] = load_vectors_from_fvecs(filename.c_str());
    if (loaded_dim != dim_) {
        std::cerr << "Error: dimension mismatch. Expected " << dim_ << ", got " << loaded_dim << std::endl;
        return false;
    }

    num_points_ = (num_points == -1) ? loaded_num : std::min(num_points, loaded_num);
    h_data_ = new float[num_points_ * dim_];
    std::copy(data.get(), data.get() + num_points_ * dim_, h_data_);

    // Копируем на GPU
    cudaMalloc(&d_data_, num_points_ * dim_ * sizeof(float));
    cudaMemcpy(d_data_, h_data_, num_points_ * dim_ * sizeof(float), cudaMemcpyHostToDevice);

    if (verbose_) {
        std::cout << "Loaded " << num_points_ << " vectors of dimension " << dim_ << std::endl;
    }

    return true;
}

bool KNNWrapper::build_graph() {
    h_graph_ = new int[num_points_ * k_];
    d_graph_ = nullptr;
    cudaMalloc(&d_graph_, num_points_ * k_ * sizeof(int));

    return build_graph_cuda();
}

bool KNNWrapper::build_graph_cuda() {
    // Простой пример: вычисляем попарные расстояния и находим k ближайших
    // В реальности используйте более эффективные методы (например, сортировку на GPU)

    thrust::device_vector<float> dists(num_points_ * num_points_);
    thrust::device_vector<int> indices(num_points_ * num_points_);

    float* d_dists = thrust::raw_pointer_cast(dists.data());
    int* d_indices = thrust::raw_pointer_cast(indices.data());

    // Заполняем индексы
    for (int i = 0; i < num_points_; ++i) {
        thrust::fill(d_indices.begin() + i * num_points_, d_indices.begin() + (i + 1) * num_points_, 0);
        thrust::sequence(d_indices.begin() + i * num_points_, d_indices.begin() + (i + 1) * num_points_);
    }

    // Вычисляем расстояния (упрощённо)
    for (int i = 0; i < num_points_; ++i) {
        float* d_query = d_data_ + i * dim_;
        for (int j = 0; j < num_points_; ++j) {
            float dist = 0.0f;
            for (int d = 0; d < dim_; ++d) {
                float diff = d_query[d] - d_data_[j * dim_ + d];
                dist += diff * diff;
            }
            dists[i * num_points_ + j] = dist;
        }

        // Сортируем по расстоянию
        auto begin = thrust::make_zip_iterator(thrust::make_tuple(dists.begin() + i * num_points_, d_indices.begin() + i * num_points_));
        auto end = thrust::make_zip_iterator(thrust::make_tuple(dists.begin() + (i + 1) * num_points_, d_indices.begin() + (i + 1) * num_points_));
        thrust::sort(begin, end, [] __device__ (auto a, auto b) {
            return thrust::get<0>(a) < thrust::get<0>(b);
        });

        // Копируем k ближайших (исключая самого себя, если dist == 0)
        int count = 0;
        for (int j = 0; j < num_points_ && count < k_; ++j) {
            int idx = thrust::get<1>(dists[i * num_points_ + j]);
            if (idx != i) { // исключаем самого себя
                h_graph_[i * k_ + count] = idx;
                count++;
            }
        }
    }

    // Копируем граф на GPU
    cudaMemcpy(d_graph_, h_graph_, num_points_ * k_ * sizeof(int), cudaMemcpyHostToDevice);

    if (verbose_) {
        std::cout << "KNN graph built with k=" << k_ << std::endl;
    }

    return true;
}

bool KNNWrapper::refine_graph(int num_iterations) {
    return refine_graph_cuda(num_iterations);
}

bool KNNWrapper::refine_graph_cuda(int iterations) {
    // Пример: улучшение графа через итеративное расширение соседей
    // В реальности — используйте NSG, HNSW и т.д.
