std::vector<KNNQueryResult> KNNWrapper::query(const std::vector<float>& queries, int k_query) {
    int num_queries = queries.size() / dim_;
    return query_cuda(queries.data(), num_queries, k_query);
}

std::vector<KNNQueryResult> KNNWrapper::query_cuda(const float* queries, int num_queries, int k_query) {
    std::vector<KNNQueryResult> results(num_queries);

    // Простой поиск: для каждого запроса ищем ближайших в графе
    for (int q = 0; q < num_queries; ++q) {
        const float* query = queries + q * dim_;
        thrust::device_vector<float> dists(num_points_);
        thrust::device_vector<int> indices(num_points_);

        // Вычисляем расстояния
        for (int i = 0; i < num_points_; ++i) {
            float dist = 0.0f;
            for (int d = 0; d < dim_; ++d) {
                float diff = query[d] - h_data_[i * dim_ + d];
                dist += diff * diff;
            }
            dists[i] = dist;
            indices[i] = i;
        }

        // Сортируем
        thrust::sort_by_key(dists.begin(), dists.end(), indices.begin());

        // Сохраняем результат
        results[q].indices.resize(k_query);
        results[q].distances.resize(k_query);
        for (int i = 0; i < k_query; ++i) {
            results[q].indices[i] = indices[i];
            results[q].distances[i] = dists[i];
        }
    }

    return results;
}

bool KNNWrapper::generate_ground_truth(const std::string& query_file, int k_gt) {
    auto [queries, qdim, qnum] = load_vectors_from_fvecs(query_file.c_str());
    if (qdim != dim_) {
        std::cerr << "Query dimension mismatch." << std::endl;
        return false;
    }

    ground_truth_.resize(qnum);
    auto results = query(std::vector<float>(queries.get(), queries.get() + qnum * dim_), k_gt);

    for (int i = 0; i < qnum; ++i) {
        ground_truth_[i] = results[i].indices;
    }

    if (verbose_) {
        std::cout << "Generated ground truth for " << qnum << " queries with k=" << k_gt << std::endl;
    }

    return true;
}

float KNNWrapper::evaluate_recall(int k_eval) {
    if (ground_truth_.empty()) {
        std::cerr << "Ground truth not generated. Cannot evaluate." << std::endl;
        return 0.0f;
    }

    int total = 0;
    int correct = 0;

    for (int i = 0; i < ground_truth_.size(); ++i) {
        auto result = query_cuda(/* dummy query */ h_data_, 1, k_eval)[0]; // Замените на реальные запросы
        std::vector<int> pred = result.indices;
        std::vector<int> truth = ground_truth_[i];

        // Пересечение
        std::sort(pred.begin(), pred.end());
        std::sort(truth.begin(), truth.end());

        std::vector<int> intersect;
        std::set_intersection(pred.begin(), pred.end(),
                              truth.begin(), truth.end(),
                              std::back_inserter(intersect));
        correct += intersect.size();
        total += k_eval;
    }

    float recall = static_cast<float>(correct) / total;
    if (verbose_) {
        std::cout << "Recall@" << k_eval << " = " << recall << std::endl;
    }
    return recall;
}

bool KNNWrapper::save_graph(const std::string& filename) {
    FILE* f = fopen(filename.c_str(), "wb");
    if (!f) return false;

    fwrite(&num_points_, sizeof(int), 1, f);
    fwrite(&k_, sizeof(int), 1, f);
    fwrite(h_graph_, sizeof(int), num_points_ * k_, f);
    fclose(f);

    if (verbose_) {
        std::cout << "Graph saved to " << filename << std::endl;
    }
    return true;
}

bool KNNWrapper::load_graph(const std::string& filename) {
    FILE* f = fopen(filename.c_str(), "rb");
    if (!f) return false;

    int saved_num, saved_k;
    fread(&saved_num, sizeof(int), 1, f);
    fread(&saved_k, sizeof(int), 1, f);

    if (saved_k != k_) {
        std::cerr << "Graph k mismatch: expected " << k_ << ", got " << saved_k << std::endl;
        fclose(f);
        return false;
    }

    num_points_ = saved_num;
    h_graph_ = new int[num_points_ * k_];
    fread(h_graph_, sizeof(int), num_points_ * k_, f);
    fclose(f);

    // Передаём на GPU
    if (d_graph_) cudaFree(d_graph_);
    cudaMalloc(&d_graph_, num_points_ * k_ * sizeof(int));
    cudaMemcpy(d_graph_, h_graph_, num_points_ * k_ * sizeof(int), cudaMemcpyHostToDevice);

    if (verbose_) {
        std::cout << "Graph loaded from " << filename << std::endl;
    }
    return true;
}

bool KNNWrapper::reset_subset(int subset_size) {
    if (subset_size > num_points_) {
        std::cerr << "Subset size exceeds current data size." << std::endl;
        return false;
    }

    num_points_ = subset_size;

    // Обрезаем граф
    int* new_h_graph = new int[num_points_ * k_];
    std::copy(h_graph_, h_graph_ + num_points_ * k_, new_h_graph);
    delete[] h_graph_;
    h_graph_ = new_h_graph;

    // Перераспределяем GPU-память
    cudaFree(d_graph_);
    cudaMalloc(&d_graph_, num_points_ * k_ * sizeof(int));
    cudaMemcpy(d_graph_, h_graph_, num_points_ * k_ * sizeof(int), cudaMemcpyHostToDevice);

    if (verbose_) {
        std::cout << "Graph reset to subset size: " << num_points_ << std::endl;
    }
    return true;
}
