```cpp
// utils.h
#ifndef UTILS_H
#define UTILS_H

#include <memory>
#include <cstdio>

// Чтение .fvecs файлов (часто используется в ANN benchmarks)
std::tuple<std::unique_ptr<float[]>, int, int> load_vectors_from_fvecs(const char* filename) {
    FILE* f = fopen(filename, "rb");
    if (!f) {
        throw std::runtime_error("Cannot open file");
    }

    int dim;
    fread(&dim, sizeof(int), 1, f);
    fseek(f, 0, SEEK_END);
    size_t fsize = ftell(f);
    rewind(f);

    int num = (fsize / sizeof(int)) / (dim + 1);
    auto data = std::make_unique<float[]>(num * dim);

    for (int i = 0; i < num; ++i) {
        fread(&dim, sizeof(int), 1, f);
        fread(data.get() + i * dim, sizeof(float), dim, f);
    }

    fclose(f);
    return std::make_tuple(std::move(data), dim, num);
}

#endif // UTILS_H
```

