Вот реализация метода `build` для структуры `TransportBuilder`, который создаёт HTTP-транспорт для взаимодействия с кластером Elasticsearch. Мы будем использовать библиотеку `reqwest` как HTTP-клиент, так как она широко используется в экосистеме Rust для подобных задач.

```rust
use reqwest::{
    Client, ClientBuilder, Proxy, Certificate, header::{HeaderMap, HeaderValue},
};
use std::time::Duration;
use std::collections::HashMap;

/// Учётные данные для аутентификации в Elasticsearch
pub enum Auth {
    Basic(String, String), // (username, password)
    Bearer(String),        // token
    // Можно расширить, например, для API-ключей
}

/// Строитель для создания HTTP-транспорта Elasticsearch
pub struct TransportBuilder {
    proxy: Option<String>,
    proxy_auth: Option<(String, String)>, // (username, password)
    no_proxy: bool,
    auth: Option<Auth>,
    ca_certificate: Option<Certificate>,
    headers: HashMap<String, String>,
    timeout: Option<Duration>,
}

impl TransportBuilder {
    pub fn new() -> Self {
        Self {
            proxy: None,
            proxy_auth: None,
            no_proxy: false,
            auth: None,
            ca_certificate: None,
            headers: HashMap::new(),
            timeout: None,
        }
    }

    /// Установить прокси (например, "http://proxy.example.com:8080")
    pub fn proxy(mut self, url: impl Into<String>) -> Self {
        self.proxy = Some(url.into());
        self
    }

    /// Установить аутентификацию для прокси (базовая аутентификация)
    pub fn proxy_auth(mut self, username: impl Into<String>, password: impl Into<String>) -> Self {
        self.proxy_auth = Some((username.into(), password.into()));
        self
    }

    /// Отключить использование прокси (включая системные настройки)
    pub fn no_proxy(mut self) -> Self {
        self.no_proxy = true;
        self
    }

    /// Установить аутентификацию для клиента Elasticsearch
    pub fn auth(mut self, auth: Auth) -> Self {
        self.auth = Some(auth);
        self
    }

    /// Добавить CA-сертификат для проверки HTTPS
    pub fn certificate(mut self, cert: Certificate) -> Self {
        self.ca_certificate = Some(cert);
        self
    }

    /// Добавить HTTP-заголовок, который будет добавлен ко всем запросам
    pub fn header(mut self, name: impl Into<String>, value: impl Into<String>) -> Self {
        self.headers.insert(name.into(), value.into());
        self
    }

    /// Установить глобальный тайм-аут для HTTP-запросов
    pub fn timeout(mut self, duration: Duration) -> Self {
        self.timeout = Some(duration);
        self
    }

    /// Построить итоговый HTTP-транспорт (reqwest::Client)
    pub fn build(self) -> Result<Client, Box<dyn std::error::Error>> {
        let mut client_builder = ClientBuilder::new();

        // Настройка прокси
        if !self.no_proxy {
            if let Some(proxy_url) = &self.proxy {
                let mut proxy = Proxy::all(proxy_url)?;

                // Добавляем аутентификацию к прокси, если указана
                if let Some((ref user, ref pass)) = self.proxy_auth {
                    proxy = proxy.basic_auth(user, pass);
                }

                client_builder = client_builder.proxy(proxy);
            } else {
                // Если прокси не задан явно, но `no_proxy` не включён — использовать системные прокси
                client_builder = client_builder.use_sys_proxy(true);
            }
        } else {
            // Отключаем все прокси
            client_builder = client_builder.no_proxy();
        }

        // Добавляем CA-сертификат, если указан
        if let Some(cert) = self.ca_certificate {
            client_builder = client_builder.add_root_certificate(cert);
        }

        // Устанавливаем тайм-аут
        if let Some(timeout) = self.timeout {
            client_builder = client_builder.timeout(timeout);
        }

        // Собираем заголовки
        let mut headers = HeaderMap::new();
        for (key, value) in &self.headers {
            let name = reqwest::header::HeaderName::from_bytes(key.as_bytes())
                .map_err(|e| format!("Invalid header name '{}': {}", key, e))?;
            let val = HeaderValue::from_str(value)
                .map_err(|e| format!("Invalid header value for '{}': {}", key, e))?;
            headers.insert(name, val);
        }

        // Добавляем заголовки аутентификации, если указаны
        if let Some(auth) = &self.auth {
            match auth {
                Auth::Basic(username, password) => {
                    let auth_value = format!("Basic {}", base64::encode(format!("{}:{}", username, password)));
                    let auth_header = HeaderValue::from_str(&auth_value)
                        .map_err(|e| format!("Failed to set Basic auth header: {}", e))?;
                    headers.insert(reqwest::header::AUTHORIZATION, auth_header);
                }
                Auth::Bearer(token) => {
                    let auth_value = format!("Bearer {}", token);
                    let auth_header = HeaderValue::from_str(&auth_value)
                        .map_err(|e| format!("Failed to set Bearer auth header: {}", e))?;
                    headers.insert(reqwest::header::AUTHORIZATION, auth_header);
                }
            }
        }

        client_builder = client_builder.default_headers(headers);

        // Собираем клиент
        let client = client_builder.build()?;
        Ok(client)
    }
}

