// build/generate-parsers.js
const fs = require('fs');
const path = require('path');
const nearley = require('nearley');
const grammar = require('nearley/lib/nearley-language-bootstrapped'); // компилятор грамматик

function generateParser(grammarPath, outputPath) {
    const rule = fs.readFileSync(grammarPath, 'utf-8');
    const parser = new nearley.Parser(grammar);
    parser.feed(rule);

    const generated = parser.results[0]; // AST грамматики
    const jsCode = nearley.generate(generated, path.basename(outputPath, '.js'));

    fs.writeFileSync(outputPath, jsCode);
    console.log(`✅ Парсер сгенерирован: ${outputPath}`);
}

module.exports = generateParser;
```

