// build/compile-templates.js
const fs = require('fs');
const path = require('path');
const nunjucks = require('nunjucks');

function compileTemplates(templatesDir, outputDir) {
    // Настройка окружения nunjucks
    nunjucks.configure(templatesDir);

    const files = fs.readdirSync(templatesDir);
    files.forEach(file => {
        if (path.extname(file) === '.njk') {
            const content = nunjucks.render(file);
            const outputPath = path.join(outputDir, file.replace('.njk', '.html'));
            fs.writeFileSync(outputPath, content);
            console.log(`✅ Шаблон скомпилирован: ${outputPath}`);
        }
    });
}

module.exports = compileTemplates;
```

