## ✅ 1. Ядро CUDA: Game of Life

```cpp
#include <cuda_runtime.h>
#include <iostream>
#include <vector>
#include <cmath>
#include <fstream>
#include <opencv2/opencv.hpp> // Для сохранения изображений

#define BLOCK_SIZE 16

// Ядро Game of Life
__global__ void gameOfLifeKernel(const unsigned char* current, unsigned char* next, int width, int height, int pitch) {
    int col = blockIdx.x * blockDim.x + threadIdx.x;
    int row = blockIdx.y * blockDim.y + threadIdx.y;

    if (row >= height || col >= width) return;

    int alive_neighbors = 0;
    int current_state = current[row * pitch + col];

    // Подсчёт живых соседей (включая wrap-around или граничные условия)
    for (int dy = -1; dy <= 1; ++dy) {
        for (int dx = -1; dx <= 1; ++dx) {
            if (dx == 0 && dy == 0) continue;

            int nx = col + dx;
            int ny = row + dy;

            // Граничные условия: тор (wrap-around)
            if (nx < 0) nx += width;
            if (ny < 0) ny += height;
            if (nx >= width) nx -= width;
            if (ny >= height) ny -= height;

            alive_neighbors += current[ny * pitch + nx];
        }
    }

    // Применение правил
    if (current_state == 1) {
        next[row * pitch + col] = (alive_neighbors == 2 || alive_neighbors == 3) ? 1 : 0;
    } else {
        next[row * pitch + col] = (alive_neighbors == 3) ? 1 : 0;
    }
}
