## ✅ 3. Реализация с несколькими GPU (с использованием MAPS-Multi)

Предположим, что **MAPS-Multi** предоставляет следующие абстракции:

- `maps::init()`, `maps::finalize()`
- `maps::getDeviceCount()`, `maps::setDevice(int)`
- `maps::allocateOnDevice()`, `maps::copyToDevice()`, `maps::copyFromDevice()`
- `maps::synchronizeDevices()`

```cpp
#include "maps_multi.h" // Условный заголовок для MAPS-Multi

void simulateGameOfLifeMultiGPU(int width, int height, int num_iterations, bool save_on_failure = true) {
    int num_gpus = maps::getDeviceCount();
    if (num_gpus == 0) {
        std::cerr << "Нет доступных GPU!" << std::endl;
        return;
    }

    int rows_per_gpu = height / num_gpus;
    int remainder = height % num_gpus;

    std::vector<int> offsets(num_gpus);
    std::vector<int> sizes(num_gpus);

    // Распределение строк по GPU (с учётом остатка)
    int offset = 0;
    for (int i = 0; i < num_gpus; ++i) {
        sizes[i] = rows_per_gpu + (i < remainder ? 1 : 0);
        offsets[i] = offset;
        offset += sizes[i];
    }

    // Хост-данные
    std::vector<unsigned char> h_grid(width * height);
    initGrid(h_grid.data(), width, height);

    // Векторы для GPU
    std::vector<unsigned char*> d_current(num_gpus);
    std::vector<unsigned char*> d_next(num_gpus);
    std::vector<size_t> pitch(num_gpus);

    // Инициализация каждого GPU
    for (int gpu = 0; gpu < num_gpus; ++gpu) {
        maps::setDevice(gpu);
        size_t p;
        maps::allocatePitched(&d_current[gpu], &p, width * sizeof(unsigned char), sizes[gpu]);
        maps::allocatePitched(&d_next[gpu], &p, width * sizeof(unsigned char), sizes[gpu]);
        pitch[gpu] = p;
    }

    // Копирование начальных данных
    for (int gpu = 0; gpu < num_gpus; ++gpu) {
        maps::setDevice(gpu);
        maps::copyToPitched(d_current[gpu], pitch[gpu],
                            h_grid.data() + offsets[gpu] * width,
                            width * sizeof(unsigned char), width, sizes[gpu]);
    }

    // Основной цикл симуляции
    for (int iter = 0; iter < num_iterations; ++iter) {
        for (int gpu = 0; gpu < num_gpus; ++gpu) {
            maps::setDevice(gpu);

            dim3 blockSize(BLOCK_SIZE, BLOCK_SIZE);
            dim3 gridSize((width + BLOCK_SIZE - 1) / BLOCK_SIZE,
                          (sizes[gpu] + BLOCK_SIZE - 1) / BLOCK_SIZE);

            gameOfLifeKernel<<<gridSize, blockSize>>>(
                d_current[gpu], d_next[gpu], width, sizes[gpu], pitch[gpu] / sizeof(unsigned char)
            );

            cudaDeviceSynchronize();
        }

        // Синхронизация между GPU (если нужно обмениваться границами — здесь упрощено)
        maps::synchronizeDevices();

        // Меняем местами буферы
        std::swap(d_current, d_next);
    }

    // Сбор результатов с GPU
    std::vector<unsigned char> h_result(width * height);
    for (int gpu = 0; gpu < num_gpus; ++gpu) {
        maps::setDevice(gpu);
        maps::copyFromPitched(h_result.data() + offsets[gpu] * width,
                              width * sizeof(unsigned char),
                              d_current[gpu], pitch[gpu], width, sizes[gpu]);
    }

    // Регрессионный тест (пример: сравнение с эталоном)
    std::vector<unsigned char> h_reference = h_grid; // Здесь должен быть эталонный результат
    // В реальности — запуск однопоточной версии или предварительно вычисленный результат
    if (!compareGrids(h_result.data(), h_reference.data(), width * height)) {
        std::cerr << "Регрессионный тест провален!" << std::endl;
        if (save_on_failure) {
            saveGridAsImage(h_result.data(), width, height, "failed_test_result.png");
            saveGridAsImage(h_reference.data(), width, height, "expected_result.png");
        }
    } else {
        std::cout << "Тест пройден!" << std::endl;
    }

    // Очистка
    for (int gpu = 0; gpu < num_gpus; ++gpu) {
        maps::setDevice(gpu);
        maps::free(d_current[gpu]);
        maps::free(d_next[gpu]);
    }
}
