void save_grid_to_image(const std::vector<int>& grid, const std::string& filename) {
  std::ofstream image_file(filename, std::ios::binary);
  if (image_file.is_open()) {
    for (int i = 0; i < GRID_SIZE * GRID_SIZE; ++i) {
