#include <iostream>
#include <vector>
#include <cuda_runtime.h>
#include <algorithm>
#include <chrono>
#include <fstream>

// Определения констант
const int GRID_SIZE = 256;
const int NUM_GENERATIONS = 100;
const int NUM_THREADS = 256; // Количество потоков на блок
const int BLOCK_SIZE = 16; // Размер блока

// Функция ядра CUDA для симуляции Игры Жизни
__global__ void game_of_life_kernel(int *grid) {
  int row = blockIdx.y * blockDim.y + threadIdx.y;
  int col = blockIdx.x * blockDim.x + threadIdx.x;

  if (row < GRID_SIZE && col < GRID_SIZE) {
    // Определяем количество живых соседей
    int neighbors = 0;
    for (int i = -1; i <= 1; ++i) {
      for (int j = -1; j <= 1; ++j) {
        if (i == 0 && j == 0) continue; // Не считаем саму клетку
        int neighbor_row = (row + i + GRID_SIZE) % GRID_SIZE;
        int neighbor_col = (col + j + GRID_SIZE) % GRID_SIZE;
        neighbors += grid[neighbor_row * GRID_SIZE + neighbor_col];
      }
    }

    // Применяем правила Игры Жизни
    if (grid[row * GRID_SIZE + col] == 1) { // Живая клетка
      if (neighbors < 2 || neighbors > 3) {
        grid[row * GRID_SIZE + col] = 0; // Умирает от одиночества или перенаселения
      }
    } else { // Мертвая клетка
      if (neighbors == 3) {
        grid[row * GRID_SIZE + col] = 1; // Оживает от размножения
      }
    }
  }
}

// Функция для инициализации сетки клеток случайными значениями
void initialize_grid(std::vector<int>& grid) {
  std::random_device rd;
  std::mt19937 gen(rd());
  std::uniform_int_distribution<> distrib(0, 1);

  for (int i = 0; i < GRID_SIZE * GRID_SIZE; ++i) {
    grid[i] = distrib(gen);
  }
}

// Функция для сохранения сетки клеток в файл изображения
void save_grid_to_image(const std::vector<int>& grid, const std::string& filename) {
  std::ofstream image_file(filename, std::ios::binary);
  if (image_file.is_open()) {
    for (int i = 0; i < GRID_SIZE * GRID_SIZE; ++i) {
      image_file << (grid[i] << 0); // Записываем 0 или 1 в файл
    }
    image_file.close();
  } else {
    std::cerr << "Не удалось открыть файл для сохранения изображения." << std::endl;
  }
}

// Функция для выполнения симуляции Игры Жизни
void simulate_game_of_life(int num_generations, const std::string& output_prefix) {
  // Выделяем память для сетки клеток на хосте
  std::vector<int> grid(GRID_SIZE * GRID_SIZE);
  initialize_grid(grid);

  // Выделяем память для сетки клеток на устройстве
  int *d_grid;
  cudaMalloc((void**)&d_grid, GRID_SIZE * GRID_SIZE * sizeof(int));

  // Копируем начальное состояние сетки на устройство
  cudaMemcpy(d_grid, grid.data(), GRID_SIZE * GRID_SIZE * sizeof(int), cudaMemcpyHostToDevice);

  // Цикл по поколениям
  for (int generation = 0; generation < num_generations; ++generation) {
    // Запускаем ядро CUDA
    game_of_life_kernel<<<2, NUM_THREADS>>>(d_grid);

    // Ожидаем завершения вычислений на устройстве
    cudaDeviceSynchronize();

    // Копируем обновленное состояние сетки с устройства на хост
    cudaMemcpy(grid.data(), d_grid, GRID_SIZE * GRID_SIZE * sizeof(int), cudaMemcpyDeviceToHost);

    // Сохраняем сетку в файл изображения
    std::string filename = output_prefix + "_" + std::to_string(generation) + ".png";
    save_grid_to_image(grid, filename);
  }

  // Освобождаем память на устройстве
  cudaFree(d_grid);
}


int main() {
  // Запускаем симуляцию Игры Жизни
  simulate_game_of_life(NUM_GENERATIONS, "gol_simulation");

  std::cout << "Симуляция Игры Жизни завершена." << std::endl;

  return 0;
}
