// Определения констант
const int GRID_WIDTH = 256;
const int GRID_HEIGHT = 256;
const int NUM_GENERATIONS = 100;

// Функция ядра CUDA для симуляции Игры Жизни
__global__ void game_of_life_kernel(int *grid) {
  int row = blockIdx.y * blockDim.y + threadIdx.y;
  int col = blockIdx.x * blockDim.x + threadIdx.x;

  if (row < GRID_HEIGHT && col < GRID_WIDTH) {
    // Определяем количество живых соседей
    int neighbors = 0;
    for (int i = -1; i <= 1; ++i) {
      for (int j = -1; j <= 1; ++j) {
        if (i == 0 && j == 0) continue; // Не считаем саму клетку
        int neighbor_row = (row + i + GRID_HEIGHT) % GRID_HEIGHT;
        int neighbor_col = (col + j + GRID_WIDTH) % GRID_WIDTH;
        neighbors += grid[neighbor_row * GRID_WIDTH + neighbor_col];
      }
    }

    // Применяем правила Игры Жизни
    if (grid[row * GRID_WIDTH + col] == 1) { // Клетка жива
      if (neighbors < 2 || neighbors > 3) {
        grid[row * GRID_WIDTH + col] = 0; // Умирает от одиночества или перенаселения
      }
    } else { // Клетка мертва
      if (neighbors == 3) {
        grid[row * GRID_WIDTH + col] = 1; // Оживает от размножения
      }
    }
  }
}

// Функция для инициализации сетки клеток случайными значениями
void initialize_grid(std::vector<int>& grid) {
  std::random_device rd;
  std::mt19937 gen(rd());
  std::uniform_int_distribution<> distrib(0, 1);

  for (int i = 0; i < GRID_WIDTH * GRID_HEIGHT; ++i) {
    grid[i] = distrib(gen);
  }
}

// Функция для сохранения сетки клеток в файл
void save_grid(const std::vector<int>& grid, const std::string& filename) {
  std::ofstream outfile(filename);
  if (outfile.is_open()) {
    for (int i = 0; i < GRID_HEIGHT; ++i) {
      for (int j = 0; j < GRID_WIDTH; ++j) {
        outfile << grid[i * GRID_WIDTH + j];
      }
      outfile << std::endl;
    }
    outfile.close();
  } else {
    std::cerr << "Не удалось открыть файл для записи: " << filename << std::endl;
  }
}

// Функция для выполнения симуляции Игры Жизни
void simulate_game_of_life(int num_generations, const std::string& output_filename) {
  // Выделяем память на хосте
  std::vector<int> grid(GRID_WIDTH * GRID_HEIGHT);

  // Инициализируем сетку
  initialize_grid(grid);

  // Выполняем симуляцию
  for (int generation = 0; generation < num_generations; ++generation) {
    // Выделяем память на устройстве
    int *d_grid;
    cudaMalloc((void**)&d_grid, GRID_WIDTH * GRID_HEIGHT * sizeof(int));

    // Копируем данные с хоста на устройство
    cudaMemcpy(d_grid, grid.data(), GRID_WIDTH * GRID_HEIGHT * sizeof(int), cudaMemcpyHostToDevice);

    // Определяем размер блоков и сетки
    dim3 block_size(16, 16);
    dim3 grid_size((GRID_WIDTH + block_size.x - 1) / block_size.x, (GRID_HEIGHT + block_size.y - 1) / block_size.y);

    // Запускаем ядро CUDA
    game_of_life_kernel<<<grid_size, block_size>>>(d_grid);

    // Ждем завершения выполнения ядра
    cudaDeviceSynchronize();

    // Копируем данные с устройства на хост
    cudaMemcpy(grid.data(), d_grid, GRID_WIDTH * GRID_HEIGHT * sizeof(int), cudaMemcpyDeviceToHost);

    // Сохраняем сетку в файл
    if (generation % 10 == 0) {
      save_grid(grid, output_filename + "_" + std::to_string(generation) + ".txt");
    }
  }

  // Освобождаем память на устройстве
  cudaFree(d_grid);
}


int main() {
  // Задаем параметры симуляции
  int num_generations = 100;
  std::string output_filename = "game_of_life";

  // Запускаем симуляцию
  simulate_game_of_life(num_generations, output_filename);

  std::cout << "Симуляция Игры Жизни завершена. Результаты сохранены в файле: " << output_filename << std::endl;

