        }
        is UiState.Success -> {
            // Скрыть ProgressBar и показать список
            progressBar.visibility = View.GONE
            adapter.submitList(state.posts)
        }
        is UiState.Error -> {
            // Скрыть ProgressBar и показать ошибку
            progressBar.visibility = View.GONE
            Toast.makeText(this, state.message, Toast.LENGTH_SHORT).show()
        }
    }
}
```

