float KNNGraphWrapper::evaluateGraph() {
    if (!d_ground_truth_indices_) {
        std::cerr << "[Error] Ground truth not generated!" << std::endl;
        return 0.0f;
    }

    if (verbose_) std::cout << "[Step] Evaluating Recall@" << k_ << "..." << std::endl;

    // Копируем результаты с GPU на CPU для сравнения
    std::vector<int> host_graph(num_points_ * k_);
    std::vector<int> host_gt(num_points_ * k_);

    cudaMemcpy(host_graph.data(), d_knn_indices_, host_graph.size() * sizeof(int), cudaMemcpyDeviceToHost);
    cudaMemcpy(host_gt.data(), d_ground_truth_indices_, host_gt.size() * sizeof(int), cudaMemcpyDeviceToHost);

    size_t correct_matches = 0;
    for (int i = 0; i < num_points_; ++i) {
        // Для простоты предполагаем сортировку и поиск пересечений.
        // В реальности нужно проверять наличие ID из GT в найденном графе.
        for (int j = 0; j < k_; ++j) {
            int neighbor_gt = host_gt[i * k_ + j];
            for (int m = 0; m < k_; ++m) {
                if (host_graph[i * k_ + m] == neighbor_gt) {
                    correct_matches++;
                    break;
                }
            }
        }
    }

    float recall = (float)correct_matches / (num_points_ * k_);
    if (verbose_) std::cout << "  -> Recall: " << recall << std::endl;
    return recall;
}

