    if (verbose_) std::cout << "[Info] Graph reset for subset size: " << subset_size_ << std::endl;
}

void KNNGraphWrapper::saveGraph(const std::string& filename) {
    std::vector<int> host_indices(num_points_ * k_);
    cudaMemcpy(host_indices.data(), d_knn_indices_, host_indices.size() * sizeof(int), cudaMemcpyDeviceToHost);

    std::ofstream out(filename, std::ios::binary);
    out.write(reinterpret_cast<char*>(&num_points_), sizeof(int));
    out.write(reinterpret_cast<char*>(&k_), sizeof(int));
    out.write(reinterpret_cast<char*>(host_indices.data()), host_indices.size() * sizeof(int));
    out.close();

    if (verbose_) std::cout << "[IO] Graph saved to " << filename << std::endl;
}

void KNNGraphWrapper::loadGraph(const std::string& filename) {
    std::ifstream in(filename, std::ios::binary);
    int loaded_n, loaded_k;
    in.read(reinterpret_cast<char*>(&loaded_n), sizeof(int));
    in.read(reinterpret_cast<char*>(&loaded_k), sizeof(int));

    if (loaded_n != num_points_ || loaded_k != k_) {
        throw std::runtime_error("Graph file dimensions mismatch");
    }

    std::vector<int> host_indices(num_points_ * k_);
    in.read(reinterpret_cast<char*>(host_indices.data()), host_indices.size() * sizeof(int));

    cudaMemcpy(d_knn_indices_, host_indices.data(), host_indices.size() * sizeof(int), cudaMemcpyHostToDevice);
    if (verbose_) std::cout << "[IO] Graph loaded from " << filename << std::endl;
}

void KNNGraphWrapper::setDebug(bool debug_mode) {
