}

void KNNGraphWrapper::checkCudaError(cudaError_t result, const char* msg) {
    if (result != cudaSuccess) {
        std::cerr << "CUDA Error: " << msg << " (" << cudaGetErrorString(result) << ")" << std::endl;
        throw std::runtime_error("CUDA Error");
    }
}
