	"net/url"
	"time"
)

// TransportBuilder — строитель для конфигурации HTTP-транспорта.
type TransportBuilder struct {
	ProxyURL        string            // URL прокси-сервера
	ProxyUsername   string            // Имя пользователя прокси (необязательно)
	ProxyPassword   string            // Пароль прокси (необязательно)
	DisableProxy    bool              // Флаг для полного отключения прокси
	ESUsername      string            // Имя пользователя Elasticsearch
	ESPassword      string            // Пароль Elasticsearch
	CACert          []byte            // PEM-кодированный CA сертификат для проверки HTTPS
	SkipTLSVerify   bool              // Если true, пропускает проверку сертификата (небезопасно)
	DefaultHeaders  map[string]string // Заголовки, добавляемые ко всем запросам
	GlobalTimeout   time.Duration     // Глобальный тайм-аут для соединения
	MaxIdleConns    int               // Максимальное кол-во idle соединений (оптимизация)
	IdleConnTimeout time.Duration     // Тайм-аут для idle соединений
}

// CustomTransport — обертка над http.Transport для добавления заголовков ко всем запросам.
// Стандартный http.Transport не имеет встроенного механизма "default headers".
type CustomTransport struct {
	BaseTransport http.RoundTripper
	Headers       map[string]string
	BasicAuth     string // Хранит заголовок Authorization
}

// RoundTrip выполняет HTTP-запрос, добавляя заголовки.
func (t *CustomTransport) RoundTrip(req *http.Request) (*http.Response, error) {
	// Клонируем запрос, чтобы безопасно модифицировать заголовки
	reqClone := req.Clone(req.Context())

	// 1. Добавление HTTP-заголовков
	for key, value := range t.Headers {
		reqClone.Header.Set(key, value)
	}

	// 2. Принятие учетных данных для аутентификации клиента в Elasticsearch
	// Если BasicAuth настроен, добавляем его
	if t.BasicAuth != "" && reqClone.Header.Get("Authorization") == "" {
		reqClone.Header.Set("Authorization", t.BasicAuth)
	}

	return t.BaseTransport.RoundTrip(reqClone)
}

// Build создает настроенный http.RoundTripper (интерфейс транспорта).
func (b *TransportBuilder) Build() (http.RoundTripper, error) {
	// Базовая конфигурация транспорта
	transport := &http.Transport{
		MaxIdleConns:    b.MaxIdleConns,
		IdleConnTimeout: b.IdleConnTimeout,
		// 6. Установка глобальных тайм-аутов на уровне TCP/Handshake
		TLSHandshakeTimeout:   10 * time.Second,
		ResponseHeaderTimeout: b.GlobalTimeout,
		ExpectContinueTimeout: 1 * time.Second,
	}

	if transport.MaxIdleConns == 0 {
		transport.MaxIdleConns = 100
	}

	// 1. & 2. Настройка прокси
	if b.DisableProxy {
		// Возможность отключения прокси, включая системные
		transport.Proxy = nil
	} else if b.ProxyURL != "" {
		parsedProxyURL, err := url.Parse(b.ProxyURL)
		if err != nil {
			return nil, fmt.Errorf("неверный URL прокси: %w", err)
		}

		// Настройка прокси с аутентификацией, если указаны данные
		if b.ProxyUsername != "" || b.ProxyPassword != "" {
			parsedProxyURL.User = url.UserPassword(b.ProxyUsername, b.ProxyPassword)
		}

		transport.Proxy = http.ProxyURL(parsedProxyURL)
	} else {
		// По умолчанию используется http.ProxyFromEnvironment, если ничего не задано
		transport.Proxy = http.ProxyFromEnvironment
	}

	// 4. Применение проверки сертификата (TLS)
	tlsConfig := &tls.Config{
		InsecureSkipVerify: b.SkipTLSVerify,
	}

	if len(b.CACert) > 0 {
		caCertPool := x509.NewCertPool()
		if ok := caCertPool.AppendCertsFromPEM(b.CACert); !ok {
			return nil, fmt.Errorf("не удалось распарсить CA сертификат")
		}
		tlsConfig.RootCAs = caCertPool
	}
	transport.TLSClientConfig = tlsConfig

	// Подготовка Basic Auth для Elasticsearch
	var basicAuthValue string
	if b.ESUsername != "" && b.ESPassword != "" {
		auth := b.ESUsername + ":" + b.ESPassword
		basicAuthValue = "Basic " + base64.StdEncoding.EncodeToString([]byte(auth))
	}

	// Оборачиваем транспорт для добавления заголовков
	finalTransport := &CustomTransport{
		BaseTransport: transport,
		Headers:       b.DefaultHeaders,
		BasicAuth:     basicAuthValue,
	}

	return finalTransport, nil
}

// Пример использования
func ExampleUsage() {
	builder := TransportBuilder{
		ProxyURL:       "http://myproxy:8080",
		ProxyUsername:  "proxy_user",
		ProxyPassword:  "proxy_pass",
		DisableProxy:   false,
		ESUsername:     "elastic",
		ESPassword:     "changeme",
		GlobalTimeout:  30 * time.Second,
		DefaultHeaders: map[string]string{"X-Custom-Header": "MyValue"},
		// CACert: []byte("...PEM content..."),
	}

	transport, err := builder.Build()
	if err != nil {
		panic(err)
	}

	client := &http.Client{
		Transport: transport,
		Timeout:   builder.GlobalTimeout, // Установка общего тайм-аута клиента
	}

	resp, err := client.Get("https://localhost:9200")
	if err != nil {
		fmt.Println("Ошибка запроса:", err)
		return
	}
	defer resp.Body.Close()
	fmt.Println("Статус ответа:", resp.Status)
}
```

### Основные моменты реализации:

1.  **Прокси (`ProxyURL`, `DisableProxy`):**
    *   Если `DisableProxy` установлен в `true`, мы явно устанавливаем `transport.Proxy = nil`, что игнорирует даже системные переменные окружения.
    *   Если задан URL, мы используем `http.ProxyURL`. Аутентификация добавляется через структуру `url.UserPassword`.

2.  **Аутентификация в ES (`ESUsername`, `ESPassword`):**
    *   `http.Transport` сам по себе не хранит состояние авторизации для всех запросов. Поэтому я создал обертку `CustomTransport`. Она формирует заголовок `Authorization: Basic ...` и внедряет его в каждый запрос в методе `RoundTrip`.

3.  **TLS/SSL (`CACert`):**
    *   Мы создаем `tls.Config`. Если передан CA сертификат, мы создаем `x509.NewCertPool`, добавляем туда сертификат и назначаем его в `RootCAs`. Это гарантирует, что клиент доверяет самоподписанным сертификатам вашего кластера ES.

4.  **Заголовки (`DefaultHeaders`):**
    *   Аналогично аутентификации, заголовки добавляются в методе `RoundTrip` структуры `CustomTransport`. Это самый надежный способ гарантировать их наличие во всех запросах.

5.  **Тайм-ауты (`GlobalTimeout`):**
    *   Тайм-аут применяется в двух местах:
        *   Внутри `http.Transport` (`ResponseHeaderTimeout`) для низкоуровневых настроек.
        *   В `http.Client` (в примере использования), что является "жестким" пределом времени на весь цикл запрос-ответ.

