#define BLOCK_SIZE_Y 32

// Макросы для проверки ошибок CUDA
#define CUDA_CHECK(call) \
    do { \
        cudaError_t err = call; \
        if (err != cudaSuccess) { \
            fprintf(stderr, "CUDA Error: %s at line %d\n", cudaGetErrorString(err), __LINE__); \
            exit(EXIT_FAILURE); \
        } \
    } while (0)

// --- Ядро CUDA: Логика Игры Жизнь ---
// grid_in: текущее состояние сетки
// grid_out: следующее состояние сетки
// width, height: размеры сетки
__global__ void GolKernel(const unsigned char* __restrict__ grid_in, 
                          unsigned char* __restrict__ grid_out, 
                          int width, int height) {

    // Вычисляем глобальные координаты потока
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    // Проверка выхода за границы сетки
    if (x >= width || y >= height) return;

    // Вычисление индекса текущей клетки в одномерном массиве
    int idx = y * width + x;
    int num_neighbors = 0;

    // Проход по соседям (3x3 область вокруг клетки)
    // Используем периодические граничные условия (тороидальная поверхность)
    for (int dy = -1; dy <= 1; ++dy) {
        for (int dx = -1; dx <= 1; ++dx) {
            if (dx == 0 && dy == 0) continue; // Пропускаем саму клетку

            int nx = (x + dx + width) % width;   // Обработка границ по X
            int ny = (y + dy + height) % height; // Обработка границ по Y

            if (grid_in[ny * width + nx]) {
                num_neighbors++;
            }
        }
    }

    // Применение правил Игры Жизнь
    unsigned char current_state = grid_in[idx];
    unsigned char next_state = 0;

    if (current_state == 1) {
        // Правило 1 и 3: Живая клетка с < 2 или > 3 соседями умирает
        // Правило 2: Живая клетка с 2 или 3 соседями выживает
        if (num_neighbors == 2 || num_neighbors == 3) {
            next_state = 1;
        }
    } else {
        // Правило 4: Мертвая клетка с ровно 3 соседями оживает
        if (num_neighbors == 3) {
            next_state = 1;
        }
    }

    grid_out[idx] = next_state;
}

// --- Вспомогательная функция CPU для верификации (Регрессионные тесты) ---
void GolCPUReference(const unsigned char* grid_in, unsigned char* grid_out, int width, int height) {
    for (int y = 0; y < height; ++y) {
        for (int x = 0; x < width; ++x) {
            int idx = y * width + x;
            int num_neighbors = 0;

            for (int dy = -1; dy <= 1; ++dy) {
                for (int dx = -1; dx <= 1; ++dx) {
                    if (dx == 0 && dy == 0) continue;
                    int nx = (x + dx + width) % width;
                    int ny = (y + dy + height) % height;
                    if (grid_in[ny * width + nx]) num_neighbors++;
                }
            }

            unsigned char current_state = grid_in[idx];
            unsigned char next_state = 0;
            if (current_state == 1) {
                if (num_neighbors == 2 || num_neighbors == 3) next_state = 1;
            } else {
                if (num_neighbors == 3) next_state = 1;
            }
            grid_out[idx] = next_state;
        }
    }
}

// --- Функция инициализации и запуска для MAPS-Multi ---
// Это условная обертка, соответствующая структуре типичного бенчмарка
extern "C" void RunGoLBenchmark(int width, int height, int iterations, bool verify) {
    size_t size = width * height * sizeof(unsigned char);

    // Выделение памяти на хосте
    unsigned char *h_grid_in = (unsigned char*)malloc(size);
    unsigned char *h_grid_out = (unsigned char*)malloc(size);
    unsigned char *h_ref_out = (unsigned char*)malloc(size);

    // Инициализация случайными данными
    srand(time(NULL));
    for (int i = 0; i < width * height; ++i) {
        h_grid_in[i] = rand() % 2;
    }

    // Выделение памяти на устройстве (GPU)
    unsigned char *d_grid_in, *d_grid_out;
    CUDA_CHECK(cudaMalloc(&d_grid_in, size));
    CUDA_CHECK(cudaMalloc(&d_grid_out, size));

    // Копирование данных на GPU
    CUDA_CHECK(cudaMemcpy(d_grid_in, h_grid_in, size, cudaMemcpyHostToDevice));

    // Настройка сетки запуска (Grid and Block dimensions)
    dim3 block(BLOCK_SIZE_X, BLOCK_SIZE_Y);
    dim3 grid((width + block.x - 1) / block.x, (height + block.y - 1) / block.y);

    printf("Starting GoL simulation on GPU: %dx%d grid, %d iterations\n", width, height, iterations);

    // Основной цикл симуляции
    // В реальном MAPS-Multi здесь может использоваться multi-GPU синхронизация
    // (например, обмен границами между GPU через cudaMemcpyPeer или MPI)
    for (int i = 0; i < iterations; ++i) {
        GolKernel<<<grid, block>>>(d_grid_in, d_grid_out, width, height);
        CUDA_CHECK(cudaGetLastError());

        // Меняем указатели местами для следующей итерации (double buffering)
        unsigned char* temp = d_grid_in;
        d_grid_in = d_grid_out;
        d_grid_out = temp;
    }

    // Копирование результата обратно на хост
    // Обратите внимание: после цикла актуальные данные находятся в d_grid_in из-за свопа
    CUDA_CHECK(cudaMemcpy(h_grid_out, d_grid_in, size, cudaMemcpyDeviceToHost));

    // --- Регрессионное тестирование ---
    if (verify) {
        printf("Verifying results...\n");

        // Для корректной проверки нужно прогнать CPU версию столько же итераций
        // Или сохранить начальное состояние. Здесь для простоты проверяем 1 итерацию 
        // или логику, если iterations=1.
        // *Внимание*: Для полной проверки многократных итераций CPU код будет медленным.

        // Сброс для верификации (для демонстрации проверяем 1 шаг от исходного)
        // В реальном тесте лучше сравнивать финальные состояния.
        unsigned char* temp_in = (unsigned char*)malloc(size);
        unsigned char* temp_out = (unsigned char*)malloc(size);
        memcpy(temp_in, h_grid_in, size);

        for(int i=0; i<iterations; ++i) {
            GolCPUReference(temp_in, temp_out, width, height);
            memcpy(temp_in, temp_out, size); // Swap
        }

        // Сравнение
        int errors = 0;
        for (int i = 0; i < width * height; ++i) {
            if (h_grid_out[i] != temp_out[i]) {
                errors++;
                if (errors < 10) { // Вывод первых 10 ошибок
                     printf("Mismatch at index %d: CPU=%d, GPU=%d\n", i, temp_out[i], h_grid_out[i]);
                }
            }
        }

        if (errors == 0) {
            printf("TEST PASSED: GPU results match CPU reference.\n");
        } else {
            printf("TEST FAILED: Found %d errors.\n", errors);
            // Здесь можно добавить логику сохранения изображения (BMP/PNG) при провале
        }

        free(temp_in);
        free(temp_out);
    }

    // Освобождение ресурсов
    free(h_grid_in);
    free(h_grid_out);
    free(h_ref_out);
    CUDA_CHECK(cudaFree(d_grid_in));
    CUDA_CHECK(cudaFree(d_grid_out));
}

// Точка входа для автономного запуска (если не запускается через MAPS harness)
int main(int argc, char** argv) {
    int width = 1024;
    int height = 1024;
    int iterations = 100;
    bool verify = true;

    if (argc > 1) width = atoi(argv[1]);
    if (argc > 2) height = atoi(argv[2]);
    if (argc > 3) iterations = atoi(argv[3]);

    RunGoLBenchmark(width, height, iterations, verify);
    return 0;
}
