float KnnGraphWrapper::evaluateGraph(const std::vector<int>& gt_indices, int num_queries) {
    if (verbose) std::cout << "[Eval] Evaluating Recall..." << std::endl;

    // Скачиваем текущий граф с GPU для сравнения
    std::vector<int> host_graph(d_num_points * d_k);
    CUDA_CHECK(cudaMemcpy(host_graph.data(), d_knn_graph, d_num_points * d_k * sizeof(int), cudaMemcpyDeviceToHost));

