void KnnGraphWrapper::resetGraphSubset(int new_size) {
    if (new_size > d_num_points) {
        std::cerr << "Error: New size cannot be larger than original dataset size." << std::endl;
        return;
    }
    if (verbose) std::cout << "[Reset] Resizing graph to " << new_size << " points." << std::endl;

    // Логика может варьироваться:
    // Либо мы просто обновляем d_num_points и игнорируем хвост буфера,
    // Либо мы переаллоцируем память, если хотим освободить ресурсы.
    d_num_points = new_size;

    // Перезапуск построения для подмножества
    // buildGraph(); 
}

void KnnGraphWrapper::saveGraph(const std::string& filename) {
    std::vector<int> host_graph(d_num_points * d_k);
    CUDA_CHECK(cudaMemcpy(host_graph.data(), d_knn_graph, d_num_points * d_k * sizeof(int), cudaMemcpyDeviceToHost));

    std::ofstream out(filename, std::ios::binary);
    out.write((char*)&d_num_points, sizeof(int));
    out.write((char*)&d_dim, sizeof(int));
    out.write((char*)&d_k, sizeof(int));
    out.write((char*)host_graph.data(), host_graph.size() * sizeof(int));
    out.close();
    if (verbose) std::cout << "[IO] Graph saved to " << filename << std::endl;
}

void KnnGraphWrapper::loadGraph(const std::string& filename) {
    std::ifstream in(filename, std::ios::binary);
    if (!in.is_open()) {
        std::cerr << "Error: Cannot open file " << filename << std::endl;
        return;
    }

    int f_n, f_d, f_k;
    in.read((char*)&f_n, sizeof(int));
    in.read((char*)&f_d, sizeof(int));
    in.read((char*)&f_k, sizeof(int));

    if (f_n != d_num_points || f_k != d_k) {
        std::cerr << "Warning: Graph file dimensions mismatch!" << std::endl;
    }

    std::vector<int> host_graph(f_n * f_k);
    in.read((char*)host_graph.data(), host_graph.size() * sizeof(int));

    CUDA_CHECK(cudaMemcpy(d_knn_graph, host_graph.data(), host_graph.size() * sizeof(int), cudaMemcpyHostToDevice));

    if (verbose) std::cout << "[IO] Graph loaded from " << filename << std::endl;
}

void KnnGraphWrapper::printDebugInfo(const std::string& stage) {
    size_t free_byte, total_byte;
    cudaMemGetInfo(&free_byte, &total_byte);
    double free_db = (double)free_byte / 1024.0 / 1024.0;
    double total_db = (double)total_byte / 1024.0 / 1024.0;
    std::cout << "[DEBUG][" << stage << "] GPU Memory: " << free_db << " MB free / " << total_db << " MB total." << std::endl;
}
