    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height) return;

    int idx = y * width + x;

    // Подсчёт живых соседей (обработка краёв — border wrap/periodic)
    int live_neighbors = 0;
    for (int j = -1; j <= 1; ++j) {
        for (int i = -1; i <= 1; ++i) {
            if (i==0 && j==0) continue;
            int nx = (x + i + width) % width;     // по модулю для wrap-around
            int ny = (y + j + height) % height;
            live_neighbors += curr_grid[ny * width + nx];
        }
    }

    // Применение правил Life
    unsigned char cell = curr_grid[idx];
    if (cell) {
        next_grid[idx] = (live_neighbors == 2 || live_neighbors == 3) ? 1 : 0;
    } else {
        next_grid[idx] = (live_neighbors == 3) ? 1 : 0;
    }
}
```

