  // await fs.copyFile('src/index.html', `${paths.public}/index.html`);
}

// === Копирование собранного в public/ ===
function publishBuild() {
  return gulp.src('build/**/*')
    .pipe(gulp.dest(paths.public));
}

// === Главные задачи ===
const build = gulp.series(
  gulp.parallel(cleanBuild, cleanPublic),
  gulp.parallel(generateParser, copyTemplates, copyAssets),
  createPublicStructure,
  publishBuild
