bool check_regression(const uint8_t* actual, const uint8_t* expected, int size, const char* fail_image) {
    bool ok = memcmp(actual, expected, size) == 0;
    if (!ok) save_grid_as_png(actual, width, height, fail_image);
    return ok;
}
