        // Копируем результаты обратно
        cudaMemcpy(graph_.indices.data(), d_indices, sizeof(int)*n_*k_, cudaMemcpyDeviceToHost);
        cudaMemcpy(graph_.distances.data(), d_distances, sizeof(float)*n_*k_, cudaMemcpyDeviceToHost);

        // Освобождаем устройство
        cudaFree(d_data);
        cudaFree(d_indices);
        cudaFree(d_distances);

        if(verbose_) std::cout << "[KNNWrapper] Graph build finished" << std::endl;
    }

    // Уточнение графа (например, повторное обновление или улучшение)
    void refineGraph(int iterations = 1) {
        if(verbose_) std::cout << "[KNNWrapper] Refining graph for " << iterations << " iterations" << std::endl;
        // Реализуйте алгоритм уточнения, возможна итеративная фильтрация соседей с использованием CUDA
