    }

    // Генерация эталонных данных KNN (например, точное KNN для валидации)
    void generateGroundTruth() {
        if(verbose_) std::cout << "[KNNWrapper] Generating ground truth with exact KNN (CPU-based)" << std::endl;

        // Простой точный KNN, очень медленный, только для эталона
        gt_indices_.resize(n_ * k_, -1);
        gt_distances_.resize(n_ * k_, 1e10f);

        for (int i = 0; i < n_; i++) {
            std::vector<std::pair<float,int>> dist_vec;
            for(int j=0; j<n_; j++) {
                if(i == j) continue;
                float dist = 0;
                for(int d = 0; d < dim_; d++) {
                    float diff = data_[i*dim_ + d] - data_[j*dim_ + d];
                    dist += diff*diff;
                }
                dist_vec.emplace_back(dist, j);
            }
            std::sort(dist_vec.begin(), dist_vec.end());
            for (int nn=0; nn < k_; nn++) {
                gt_distances_[i*k_ + nn] = dist_vec[nn].first;
                gt_indices_[i*k_ + nn] = dist_vec[nn].second;
            }
        }
        if(verbose_) std::cout << "[KNNWrapper] Ground truth generated" << std::endl;
    }

    // Оценка качества графа, например Recall по эталонным данным
    float evaluateGraph() {
        if(verbose_) std::cout << "[KNNWrapper] Evaluating graph recall" << std::endl;

        if(gt_indices_.empty()) {
            std::cerr << "Ground truth not generated" << std::endl;
            return 0.0f;
        }

        int hit = 0;
        int total = n_ * k_;

        for(int i = 0; i < n_; i++) {
            std::vector<int> gt_neighbors(gt_indices_.begin() + i*k_, gt_indices_.begin() + (i+1)*k_);
            std::vector<int> algo_neighbors(graph_.indices.begin() + i*k_, graph_.indices.begin() + (i+1)*k_);

            for(int x : algo_neighbors) {
                if(std::find(gt_neighbors.begin(), gt_neighbors.end(), x) != gt_neighbors.end()) {
                    hit++;
                }
            }
        }

        float recall = float(hit) / total;
        if(verbose_) std::cout << "[KNNWrapper] Recall@k = " << recall << std::endl;
        return recall;
    }

    // Запросы на графе
    KNNQuery query(int node_id) {
        assert(node_id >= 0 && node_id < n_);
        KNNQuery q(k_);
        for (int i=0; i<k_; i++) {
            q.neighbors[i] = graph_.indices[node_id*k_ + i];
            q.distances[i] = graph_.distances[node_id*k_ + i];
        }
        if(debug_) {
            std::cout << "Query for node " << node_id << " results:" << std::endl;
            for (int i=0; i<k_; i++) {
                std::cout << "  Neighbor " << q.neighbors[i] << " Dist " << q.distances[i] << std::endl;
            }
        }
        return q;
    }

    // Сохранение графа в бинарник
    bool saveGraph(const std::string& filename) {
        std::ofstream fout(filename, std::ios::binary);
        if(!fout.is_open()) {
            std::cerr << "Error saving graph to " << filename << std::endl;
            return false;
        }

        // Сохраняем n, k
        fout.write((char*)&graph_.n, sizeof(int));
        fout.write((char*)&graph_.k, sizeof(int));
        // Сохраняем indices и distances
        fout.write((char*)graph_.indices.data(), sizeof(int)*graph_.indices.size());
        fout.write((char*)graph_.distances.data(), sizeof(float)*graph_.distances.size());

        fout.close();

        if(verbose_) std::cout << "[KNNWrapper] Graph saved to " << filename << std::endl;
        return true;
    }

    // Загрузка графа из бинарника
    bool loadGraph(const std::string& filename) {
        std::ifstream fin(filename, std::ios::binary);
        if(!fin.is_open()) {
            std::cerr << "Error loading graph from " << filename << std::endl;
            return false;
        }

        int n, k;
        fin.read((char*)&n, sizeof(int));
        fin.read((char*)&k, sizeof(int));
        graph_ = KNNGraph(n, k);

        fin.read((char*)graph_.indices.data(), sizeof(int)*n*k);
        fin.read((char*)graph_.distances.data(), sizeof(float)*n*k);

        fin.close();

        if(verbose_) std::cout << "[KNNWrapper] Graph loaded from " << filename << std::endl;
        return true;
    }

    // Сброс графа для подмножества
    void resetGraphSubset(int subsetSize) {
        if(verbose_) std::cout << "[KNNWrapper] Resetting graph to subset of size " << subsetSize << std::endl;
        graph_.resetSubset(subsetSize);
    }

private:
    int n_ = 0;             // кол-во точек
    int dim_ = 0;           // размерность
    int k_;                 // число соседей
    bool verbose_ = false;
    bool debug_ = false;

    std::vector<float> data_;   // данные (n_ x dim_)
    KNNGraph graph_{0,0};       // граф kNN

