 * Ядро CUDA для вычисления следующего состояния клеток по правилам Игры Жизни.
 * 
 * @param input     Указатель на текущую сетку (0 или 1)
 * @param output    Указатель на выходной буфер для следующего состояния
 * @param width     Ширина сетки
 * @param height    Высота сетки
 */
__global__
void gameOfLifeKernel(const uint8_t* input, uint8_t* output, int width, int height)
{
    // Вычисляем координаты клетки, которую обработает конкретный поток
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height)
        return;

    int live_neighbors = 0;

    // Перебор соседних ячеек в диапазоне [-1, 1] по x и y
    for (int dy = -1; dy <= 1; ++dy)
    {
        for (int dx = -1; dx <= 1; ++dx)
        {
            if (dx == 0 && dy == 0) continue; // пропускаем текущую клетку

            int nx = x + dx;
            int ny = y + dy;

            // Проверяем, что сосед находится в пределах поля
            if (nx >= 0 && nx < width && ny >= 0 && ny < height)
            {
                live_neighbors += input[IDX(nx, ny, width)];
            }
        }
    }

    uint8_t current_cell = input[IDX(x, y, width)];
    uint8_t next_cell = 0;

    // Правила Игры Жизни
    if (current_cell == 1)
    {
        next_cell = (live_neighbors == 2 || live_neighbors == 3) ? 1 : 0;
    }
    else
    {
        next_cell = (live_neighbors == 3) ? 1 : 0;
    }

    output[IDX(x, y, width)] = next_cell;
}
