```cpp
bool KNNWrapper::KNNGraphWrapper::loadData(const std::string& filename) {
    // Чтение файла (пример, требуется реализовать)
    std::ifstream infile(filename);
    if (!infile.is_open()) {
        std::cerr << "Failed to open data file." << std::endl;
        return false;
    }

    // Предположим, что в файле указано число точек и размерность
    size_t num_points, dimensions;
    infile >> num_points >> dimensions;
    dataset_.num_points = num_points;
    dataset_.dimensions = dimensions;

    dataset_.data_host = new float[num_points * dimensions];

    for (size_t i = 0; i < num_points * dimensions; ++i) {
        infile >> dataset_.data_host[i];
    }

    // Копируем данные на устройство
    if (cudaMalloc(&dataset_.data_device, sizeof(float) * num_points * dimensions) != cudaSuccess) {
        std::cerr << "CUDA malloc failed." << std::endl;
        return false;
    }
    cudaMemcpy(dataset_.data_device, dataset_.data_host, sizeof(float) * num_points * dimensions, cudaMemcpyHostToDevice);

    printStatus("Data loaded and transferred to GPU");
    return true;
}
