```go
package elasticsearch

import (
	"context"
	"crypto/tls"
	"crypto/x509"
	"fmt"
	"net"
	"net/http"
	"net/url"
	"time"
)

// Transport представляет собой клиента HTTP, настроенного для взаимодействия с Elasticsearch
type Transport struct {
	client *http.Client
	headers http.Header
	baseURL string
}

// TransportBuilder служит для сборки клиента Transport с настройками
type TransportBuilder struct {
	endpoint             string
	proxyURL             *url.URL
	proxyUsername        string
	proxyPassword        string
	disableProxy         bool
	username             string
	password             string
	caCert               []byte
	headers              map[string]string
	timeout              time.Duration
}

// build создает и возвращает настроенный Transport для общения с Elasticsearch
func (b *TransportBuilder) build() (*Transport, error) {
	// Настраиваем транспорт
	transport := &http.Transport{
		TLSClientConfig: &tls.Config{},
		DialContext: (&net.Dialer{
			Timeout: b.timeout,
		}).DialContext,
	}

	// Настройка проверки сертификата (предоставленный сертификат)
	if len(b.caCert) > 0 {
		certPool := x509.NewCertPool()
		if ok := certPool.AppendCertsFromPEM(b.caCert); !ok {
			return nil, fmt.Errorf("failed to append CA certificate")
		}
		transport.TLSClientConfig.RootCAs = certPool
	}

	// Настройка прокси
	if b.disableProxy {
		transport.Proxy = nil // отключить системные и пользовательские прокси
	} else if b.proxyURL != nil {
		transport.Proxy = http.ProxyURL(b.proxyURL)
	}

	// Создаем клиент
	client := &http.Client{
		Transport: transport,
		Timeout:   b.timeout,
	}

	// Обертка для добавления базовой аутентификации для клиента Elasticsearch, если указана
	// Можно реализовать через создание клиента, который автоматически добавляет Authorization заголовок

	// Создаем по умолчанию заголовки запросов
	headers := make(http.Header)

	// Добавляем дополнительные заголовки
	for key, value := range b.headers {
		headers.Set(key, value)
	}

	// Если указаны учетные данные для Elasticsearch (клиента)
	if b.username != "" && b.password != "" {
		// Можно реализовать через автоматическую вставку заголовка Authorization
		headers.Set("Authorization", "Basic "+basicAuth(b.username, b.password))
	}

	return &Transport{
		client:  client,
		headers: headers,
		baseURL: b.endpoint,
	}, nil
}

// Вспомогательная функция для Base64 кодирования учетных данных
func basicAuth(username, password string) string {
	auth := username + ":" + password
	return encodeBase64(auth)
}

func encodeBase64(s string) string {
	return base64.StdEncoding.EncodeToString([]byte(s))
}
```

