package com.example.app.ui.main

import androidx.lifecycle.*
import com.example.app.data.PostRepository
import com.example.app.model.Post
import kotlinx.coroutines.launch

/**
 * ViewModel, который загружает список постов и публикует его через LiveData.
 *
 * @property repository репозиторий, который умеет возвращать список постов
 */
class MainViewModel(
    private val repository: PostRepository
) : ViewModel() {

    /**----------------------------------------------------------------------
     *  Список постов, который будет подписан UI
     *---------------------------------------------------------------------*/
    private val _posts = MutableLiveData<List<Post>>()
    val posts: LiveData<List<Post>> = _posts

    /**----------------------------------------------------------------------
     *  Сообщения об ошибке (можно подписаться в UI)
     *---------------------------------------------------------------------*/
    private val _error = MutableLiveData<String?>()
    val error: LiveData<String?> = _error

    init {
        // При создании ViewModel сразу загружаем данные
        loadPosts()
    }

    /**
     * Запускает асинхронную загрузку постов из репозитория.
     * Используем viewModelScope – корутиновый контекст,
     * который будет автоматически отменён при уничтожении ViewModel.
     */
    fun loadPosts() {
        viewModelScope.launch {
            try {
                val result = repository.getPosts()
                _posts.postValue(result)
                _error.postValue(null)    // очищаем возможную ошибку
            } catch (e: Exception) {
                _error.postValue(e.localizedMessage ?: "Неизвестная ошибка")
            }
        }
    }

    /**
     * (Опционально) Обновление списка при команде из UI
     */
    fun refreshPosts() {
        _posts.postValue(emptyList())   // очистка старого списка
        loadPosts()
    }
}
