#!/usr/bin/env node

/**
 *  WEB BUILD SYSTEM
 */
const path = require('path');
const fs = require('fs-extra');
const glob = require('glob');
const ejs = require('ejs');

// -----------------------------
// 1. Конфигурация путей
// -----------------------------
const ROOT   = __dirname;
const SRC    = path.join(ROOT, 'src');
const BUILD  = path.join(ROOT, 'build');
const DIST   = path.join(ROOT, 'dist');
const PUBLIC = path.join(ROOT, 'public');

// Папки для публикации
const PUBLIC_DIRS = [
  { name: 'css',  src: path.join(SRC, 'assets', 'css') },
  { name: 'js',   src: path.join(SRC, 'assets', 'js')  },
  { name: 'img',  src: path.join(SRC, 'assets', 'img') },
];

// -----------------------------
// 2. Утиль
// -----------------------------
async function log(msg) {
  console.log(`[${new Date().toISOString()}] ${msg}`);
}

async function safeMkdir(dir) {
  await fs.ensureDir(dir);
}

async function copy(src, dest) {
  await fs.copy(src, dest, { overwrite: true });
}

// -----------------------------
// 3. Генерация парсеров
// -----------------------------
async function generateParsers() {
  const srcDir = path.join(SRC, 'parsers');
  const outDir = path.join(BUILD, 'parsers');

  await safeMkdir(outDir);

  const grammarFiles = glob.sync('*.txt', { cwd: srcDir });
  for (const file of grammarFiles) {
    const srcPath = path.join(srcDir, file);
    const txt     = await fs.readFile(srcPath, 'utf8');

    // --- Пример простейшего генератора
    // Пусть входные файлы – простые инструкции,
    // и мы генерируем файл `Parser.js`.
    const parserModule = `
/**
 * Автоматически сгенерирован парсер: ${file}
 */
export function parse(input) {
  // Простейшая логика:
  const words = input.split(/\\s+/);
  return { words, raw: input };
}
`;

    const outPath = path.join(outDir, `${path.basename(file, '.txt')}.js`);
    await fs.writeFile(outPath, parserModule, 'utf8');
    log(`Парсер создан: ${outPath}`);
  }
}

// -----------------------------
// 4. Компиляция шаблонов
// -----------------------------
async function compileTemplates() {
  const srcDir = path.join(SRC, 'templates');
  const outDir = path.join(BUILD, 'templates');
  await safeMkdir(outDir);

  const templateFiles = glob.sync('*.ejs', { cwd: srcDir });

  for (const tmpl of templateFiles) {
    const tmplPath = path.join(srcDir, tmpl);
    const tpl      = await fs.readFile(tmplPath, 'utf8');

    // В шаблонах можно использовать любые переменные
    const data = { title: 'Статический сайт', year: new Date().getFullYear() };

    const rendered = ejs.render(tpl, data);
    const outPath  = path.join(outDir, `${path.basename(tmpl, '.ejs')}.html`);
    await fs.writeFile(outPath, rendered, 'utf8');
    log(`Шаблон скомпилирован: ${outPath}`);
  }
}

// -----------------------------
// 5. Финальная сборка (dist)
// -----------------------------
async function buildDist() {
  await safeMkdir(DIST);

  // Скопировать все сгенерированные файлы парсеров и шаблонов
  await copy(path.join(BUILD, 'parsers'), path.join(DIST, 'parsers'));
  await copy(path.join(BUILD, 'templates'), path.join(DIST, 'templates'));

  // --- Плюс можно добавить трансляцию JS/TS, минификацию и т.д.
  log(`Файлы собраны в ${DIST}`);
}

// -----------------------------
// 6. Создание публичной публицистической папки
// -----------------------------
async function preparePublic() {
  await safeMkdir(PUBLIC);

  for (const dirInfo of PUBLIC_DIRS) {
    const dest = path.join(PUBLIC, dirInfo.name);
    await safeMkdir(dest);
    if (await fs.pathExists(dirInfo.src)) {
      await copy(dirInfo.src, dest);
      log(`Скопировано ${dirInfo.name} → ${dest}`);
    } else {
      log(`WARN: исходный путь ${dirInfo.src} не найден`);
    }
  }
}

// -----------------------------
// 7. Запуск всего
// -----------------------------
(async () => {
  try {
    log('Начинаю сборку...');
    await generateParsers();
    await compileTemplates();
    await buildDist();
    await preparePublic();
    log('Сборка завершена успешно!');
  } catch (err) {
    console.error('❌ Ошибка сборки:', err);
    process.exit(1);
  }
})();
```

> **Ключевые моменты**  
> * `generateParsers` – находит все `*.txt` в `src/parsers` и генерирует
