## 2. Вспомогательные функции на C++ (чистый CUDA / MAPS‑Multi)

```cpp
/*********************************************************************
 *  Подготовка и запуск симуляции на нескольких GPU
 *
 *  Функция RunGameOfLifeMultiGPU берёт 2‑D массив (uint8_t* host_grid)
 *  длины width × height, и по заданному номеру итераций запускает
 *  многопроцессорную симуляцию.  После завершения функция возвращает
 *  обновлённый массив того же размера в host_grid (оно же на вход).
 *
 *  Для простоты код использует простую логику:
 *      - каждая GPU получает *приблизительно* равный кусок по строкам
 *      - для каждой части копируем данные + верхнюю/нижнюю halo‑row
 *      - запускаем kernel асинхронно на всех GPU (через stream‑ы)
 *      - копируем результаты обратно и объединяем
 *
 *  Никаких особых зависимостей от MAPS‑Multi, но вы свободно можете
 *  вызывать эту функцию из тест‑фреймворка.
 *********************************************************************/

#include <vector>
#include <cassert>
#include <algorithm>
#include <cuda_runtime.h>
#include <cstdio>
#include <cstring> // memcpy

// Обработчик ошибок CUDA
inline void CheckCUDA(cudaError err, const char* msg = "")
{
    if (err != cudaSuccess)
    {
        fprintf(stderr, "CUDA error: %s %s\n", msg, cudaGetErrorString(err));
        exit(EXIT_FAILURE);
    }
}

// Чистый PGM writer
void SavePGM(const char* filename, const uint8_t* data,
             int width, int height)
{
    FILE* f = fopen(filename, "wb");
    if (!f) return;
    fprintf(f, "P5\n%d %d\n255\n", width, height);
    fwrite(data, 1, width * height, f);
    fclose(f);
}

// -------------------------------------------------------------
// Основная API
// -------------------------------------------------------------
void RunGameOfLifeMultiGPU(uint8_t* host_grid,
                           int width,
                           int height,
                           int iter_count,
                           int device_offset = 0)   // если 0 – все доступные GPU
{
    // Узнаем число доступных GPU
    int devCount;
    CheckCUDA(cudaGetDeviceCount(&devCount));

    if (devCount == 0) {
        fprintf(stderr, "Не найдено ни одного GPU\n");
        exit(1);
    }

    // device_offset (если запуск с нескольких процессов)
    if (device_offset < 0) device_offset = 0;
    if (device_offset >= devCount) {
        fprintf(stderr, "device_offset %d выход за пределы количества GPU %d\n",
                device_offset, devCount);
        exit(1);
    }
    int useCount = devCount - device_offset;   // количество GPU, которые будем использовать

    // -------------------------------------------------------------
    // Выделяем память для каждой GPU
    // -------------------------------------------------------------
    struct GpuResources
    {
        int dev_id;
        cudaStream_t stream;
        uint8_t *dev_cur, *dev_next;
        size_t pitch;            // pitch для global memory
        size_t rows;             // количество строк, приходящих в этот GPU (без extra‑rows)
    };

    std::vector<GpuResources> resources(useCount);

    // Определяем план разбиения
    int base_rows_per_gpu = height / useCount;
    int remainder_rows = height % useCount;

    int global_row_start = 0;

    for (int i = 0; i < useCount; ++i)
    {
        GpuResources &r = resources[i];
        r.dev_id = i + device_offset;
        CheckCUDA(cudaSetDevice(r.dev_id));
        CheckCUDA(cudaStreamCreate(&r.stream));

        // Число строк, которое получит этот GPU
        r.rows = base_rows_per_gpu + (i < remainder_rows ? 1 : 0);
        // +2 для верхней и нижней halo‑rows
        size_t alloc_rows = r.rows + 2;

        // Выделяем память (pitch‑aligned)
        CheckCUDA(cudaMallocPitch((void**)&r.dev_cur, &r.pitch,
                                  (size_t)width, alloc_rows));
        CheckCUDA(cudaMallocPitch((void**)&r.dev_next, &r.pitch,
                                  (size_t)width, alloc_rows));

        // ----Копируем данных на GPU----
        // Копируем только body‑rows + дополнительные для halo
        size_t body_offset = r.rows;   // offset на начало body‑rows в dev_cur

        // Копируем центральный блок (с учётом границ):
        // 1) верхняя halo‑row (если i==0 – копируем last row из host, иначе из предыдущего GPU)
        // 2) body‑rows
        // 3) нижняя halo‑row (если i==useCount-1 – копируем first row из host, иначе из следующего GPU)

        // Для простоты копируем body‑rows + 1 extra‑row сверху и снизу
        // В случае первого/последнего GPU, “halo‑rows” будут просто копировать границы (periodic)
        size_t h_row_pitch = (size_t)width;   // host stride (no padding)
        size_t d_row_pitch = r.pitch;         // device stride

        // Вычисляем источник
        const uint8_t* src_ptr = host_grid + global_row_start * h_row_pitch;

        // Копируем body + верхнюю halo
        CheckCUDA(cudaMemcpy2DAsync(
            r.dev_cur + d_row_pitch,   // dest (skip 0-й halo)
            d_row_pitch,
            src_ptr,                  // src (top halo will be overwritten below)
            h_row_pitch,
            (size_t)width, // width in bytes
            r.rows + 1,    // height (body + top halo)
            cudaMemcpyHostToDevice,
            r.stream));

        // Копируем нижнюю halo
        // Мы будем просто скопировать last row из блока body (или глобально из последней строки)
        // Ниже, после kernel, просто обновим нижнюю halo в каждом GPU при помощи memcpys.

        // ----------- Перебор для каждого GPU -----------

        // Обновляем global_row_start для следующего GPU
        global_row_start += r.rows;
    }

    // -------------------------------------------------------------
    // Приступаем к запуска iter_count шагов
    // -------------------------------------------------------------
    for (int step=0; step<iter_count; ++step)
    {
        // 1) Запускаем kernel на каждом GPU
        for (int i=0; i<useCount; ++i)
        {
            GpuResources &r = resources[i];
            CheckCUDA(cudaSetDevice(r.dev_id));

            dim3 block(TILE_X, TILE_Y);
            dim3 grid( (width + TILE_X - 1)/TILE_X,
                       (r.rows + TILE_Y - 1)/TILE_Y );

            GolKernel<<<grid, block, 0, r.stream>>>(
                r.dev_cur, r.dev_next,
                width,
                (int)r.rows + 2,   // height, включая halo
                r.pitch);

            CheckCUDA(cudaGetLastError());
        }

        // 2) Копируем границы (haloes) между GPU (с учётом периодичности)
        // Для начала будем простым «обновлением» границ внутри каждого GPU: нижняя halo записываем из последней body‑row
        for (int i=0; i<useCount; ++i)
        {
            GpuResources &r = resources[i];
            CheckCUDA(cudaSetDevice(r.dev_id));

            // копируем нижнее тело в нижний halo от r.dev_next
            CheckCUDA(cudaMemcpyAsync(
                r.dev_next + (r.rows + 1) * r.pitch,   // нижний halo
                r.dev_next + r.rows * r.pitch,         // last body row
                width,
                cudaMemcpyDeviceToDevice,
                r.stream));

            // Копируем верхнее тело в верхний halo из предыдущего GPU (или последнего)
            int prev_gpu = (i==0 ? useCount-1 : i-1);
            GpuResources &prev = resources[prev_gpu];

            CheckCUDA(cudaMemcpyAsync(
                r.dev_next,               // верхняя halo, offset 0
                prev.dev_next + prev.rows * prev.pitch, // последний body row у prev
                width,
                cudaMemcpyDeviceToDevice,
                r.stream));
        }

        // 3) Меняем указатели (теперь старое = новое)
        for (int i=0; i<useCount; ++i)
        {
            GpuResources &r = resources[i];
            CheckCUDA(cudaStreamSynchronize(r.stream));
            std::swap(r.dev_cur, r.dev_next);
        }

        // 4) На последнем шаге можно собирать результирующее изображение
        if (step == iter_count-1)
        {
            // Сохраняем данные в host_grid
            int dst_row_offset = 0;
            for (int i=0; i<useCount; ++i)
            {
                GpuResources &r = resources[i];
                CheckCUDA(cudaSetDevice(r.dev_id));

                // Копируем body‑rows из r.dev_cur (без halo) на host
                Size_t bytes_to_copy = r.rows * (size_t)width;
                CheckCUDA(cudaMemcpy2DAsync(
                    host_grid + dst_row_offset * width,
                    (size_t)width,
                    r.dev_cur + r.pitch,    // skip 0 halo
                    r.pitch,
                    (size_t)width,
                    r.rows,
                    cudaMemcpyDeviceToHost,
                    r.stream));

                dst_row_offset += r.rows;
            }
            // Синхронно дождёмся всех потоков
            for (auto &r : resources) CheckCUDA(cudaStreamSynchronize(r.stream));
        }
    }

    // -------------------------------------------------------------
    // Очистка
    // -------------------------------------------------------------
    for (auto &r : resources) {
        cudaSetDevice(r.dev_id);
        cudaFree(r.dev_cur);
        cudaFree(r.dev_next);
        cudaStreamDestroy(r.stream);
    }
}
