Реализация (основные части):

```cpp
#include "knn_graph.h"
#include <fstream>
#include <iostream>
#include <cassert>
#include <cuda_runtime.h>
#include <cuda_profiler_api.h>

// Вспомогательные CUDA функции
#include "cuda_utils.cuh"
#include "knn_kernels.cuh"

KNNGraph::KNNGraph(const Config& config) : config_(config) {
    num_points_ = config.num_points;
    dim_ = config.dim;
    k_ = config.k;
    verbose_ = config.enable_verbose;
    debug_enabled_ = config.enable_debug;

    // Инициализация CUDA
    cudaSetDevice(0); // или парсинг из config.device
    cudaDeviceProp prop;
    cudaGetDeviceProperties(&prop, 0);

    if (verbose_) {
        std::cout << "Using CUDA device: " << prop.name << std::endl;
    }
}

KNNGraph::~KNNGraph() {
    freeMemory();
}

void KNNGraph::loadDataset(const float* data, size_t n, size_t d) {
    assert(n > 0 && d > 0);

    num_points_ = n;
    dim_ = d;
    host_data_.assign(data, data + n * d);
    data_loaded_ = true;

    if (verbose_) {
        std::cout << "Loaded " << n << " points of dimension " << d << std::endl;
    }
}

void KNNGraph::buildGraph() {
    if (!data_loaded_) {
        throw std::runtime_error("Data not loaded");
    }

    validateInput();
    allocateMemory();

    // Копируем данные на GPU
    cudaMemcpy(device_data_, host_data_.data(),
               num_points_ * dim_ * sizeof(float),
               cudaMemcpyHostToDevice);

    if (verbose_) {
        std::cout << "Building KNN graph with k=" << k_ 
                  << " on " << num_points_ << " points..." << std::endl;
    }

    // Вызываем ядро построения графа
    cudaBuildKNNGraph(device_data_);

    // Копируем результаты обратно
    cudaMemcpy(host_graph_.data(), device_graph_,
               num_points_ * k_ * sizeof(int),
               cudaMemcpyDeviceToHost);
    cudaMemcpy(host_distances_.data(), device_distances_,
               num_points_ * k_ * sizeof(float),
               cudaMemcpyDeviceToHost);

    graph_built_ = true;

    if (debug_enabled_) {
        debugPrintGraph();
    }
}

void KNNGraph::knnQuery(const float* queries, size_t nq,
                        int* indices, float* distances, size_t query_k) {
    if (!graph_built_) {
        throw std::runtime_error("Graph not built");
    }

    // Выделяем память на устройстве для запросов и результатов
    float* d_queries;
    int* d_result_indices;
    float* d_result_distances;

    cudaMalloc(&d_queries, nq * dim_ * sizeof(float));
    cudaMalloc(&d_result_indices, nq * query_k * sizeof(int));
    cudaMalloc(&d_result_distances, nq * query_k * sizeof(float));

    // Копируем запросы на устройство
    cudaMemcpy(d_queries, queries,
               nq * dim_ * sizeof(float),
               cudaMemcpyHostToDevice);

    if (verbose_) {
        std::cout << "Performing " << nq << " queries with k=" 
                  << query_k << "..." << std::endl;
    }

    // Выполняем запрос
    cudaKnnQuery(d_queries, nq, d_result_indices,
                 d_result_distances, query_k);

    // Копируем результаты
    cudaMemcpy(indices, d_result_indices,
               nq * query_k * sizeof(int),
               cudaMemcpyDeviceToHost);
    cudaMemcpy(distances, d_result_distances,
               nq * query_k * sizeof(float),
               cudaMemcpyDeviceToHost);

    // Освобождаем временную память
    cudaFree(d_queries);
    cudaFree(d_result_indices);
    cudaFree(d_result_distances);

    if (debug_enabled_) {
        std::cout << "Query results (first 5):" << std::endl;
        for (size_t i = 0; i < std::min(nq, size_t(5)); ++i) {
            std::cout << "Query " << i << ": ";
            for (size_t j = 0; j < query_k; ++j) {
                std::cout << indices[i * query_k + j] << "(" 
                          << distances[i * query_k + j] << ") ";
            }
            std::cout << std::endl;
        }
    }
}

void KNNGraph::saveGraph(const std::string& graph_file,
                         const std::string& data_file) {
    if (!graph_built_) {
        throw std::runtime_error("Graph not built");
    }

    // Сохраняем граф
    std::ofstream fout(graph_file, std::ios::binary);
    if (!fout) {
        throw std::runtime_error("Cannot open file: " + graph_file);
    }

    // Записываем заголовок
    fout.write(reinterpret_cast<const char*>(&num_points_), sizeof(size_t));
    fout.write(reinterpret_cast<const char*>(&dim_), sizeof(size_t));
    fout.write(reinterpret_cast<const char*>(&k_), sizeof(size_t));

    // Записываем данные графа
    fout.write(reinterpret_cast<const char*>(host_graph_.data()),
               num_points_ * k_ * sizeof(int));
    fout.write(reinterpret_cast<const char*>(host_distances_.data()),
               num_points_ * k_ * sizeof(float));

    fout.close();

    // Сохраняем данные, если указан файл
    if (!data_file.empty()) {
        std::ofstream dout(data_file, std::ios::binary);
        dout.write(reinterpret_cast<const char*>(host_data_.data()),
                   num_points_ * dim_ * sizeof(float));
        dout.close();
    }

    if (verbose_) {
        std::cout << "Graph saved to " << graph_file << std::endl;
    }
}

void KNNGraph::resetToFirstN(size_t n) {
    if (n > num_points_) {
        throw std::runtime_error("Subset size exceeds total points");
    }

    if (n == 0) n = num_points_;

    // Обновляем граф для первых n точек
    std::vector<int> new_graph(n * k_);
    std::vector<float> new_distances(n * k_);

    for (size_t i = 0; i < n; ++i) {
        for (size_t j = 0; j < k_; ++j) {
            size_t idx = i * k_ + j;
            int neighbor = host_graph_[idx];

            // Если сосед вне нового диапазона, заменяем ближайшим валидным
            if (neighbor >= static_cast<int>(n)) {
                // Поиск следующего валидного соседа
                for (size_t t = 0; t < k_; ++t) {
                    int alt = host_graph_[i * k_ + t];
                    if (alt < static_cast<int>(n) && alt != neighbor) {
                        neighbor = alt;
                        break;
                    }
                }
            }
            new_graph[idx] = neighbor;
            new_distances[idx] = host_distances_[idx];
        }
    }

    // Обновляем данные
    host_graph_.swap(new_graph);
    host_distances_.swap(new_distances);
    num_points_ = n;

    // Обновляем данные на устройстве
    cudaMemcpy(device_graph_, host_graph_.data(),
               n * k_ * sizeof(int), cudaMemcpyHostToDevice);
    cudaMemcpy(device_distances_, host_distances_.data(),
               n * k_ * sizeof(float), cudaMemcpyHostToDevice);

    if (verbose_) {
        std::cout << "Graph reset to first " << n << " points" << std::endl;
    }
}

void KNNGraph::allocateMemory() {
    freeMemory(); // На всякий случай

    // Выделяем память на хосте
    host_graph_.resize(num_points_ * k_);
    host_distances_.resize(num_points_ * k_);

    // Выделяем память на устройстве
    cudaMalloc(&device_data_, num_points_ * dim_ * sizeof(float));
    cudaMalloc(&device_graph_, num_points_ * k_ * sizeof(int));
    cudaMalloc(&device_distances_, num_points_ * k_ * sizeof(float));

    if (verbose_) {
        size_t total_mb = (num_points_ * dim_ * sizeof(float) +
                          num_points_ * k_ * (sizeof(int) + sizeof(float))) 
                          / (1024 * 1024);
        std::cout << "Allocated " << total_mb << " MB on GPU" << std::endl;
    }
}

void KNNGraph::freeMemory() {
    if (device_data_) cudaFree(device_data_);
    if (device_graph_) cudaFree(device_graph_);
    if (device_distances_) cudaFree(device_distances_);

    device_data_ = nullptr;
    device_graph_ = nullptr;
    device_distances_ = nullptr;
}
```

