            CUDA_CHECK(cudaSetDevice(dev));

            // Конфигурация запуска ядра
            dim3 block_size(BLOCK_SIZE, BLOCK_SIZE);
            dim3 grid_size(
                (width + block_size.x - 1) / block_size.x,
                (device_heights[dev] + block_size.y - 1) / block_size.y
            );

            // Запускаем ядро
            int current_idx = dev * 2 + (iter % 2);
            int next_idx = dev * 2 + ((iter + 1) % 2);

            // Используем оптимизированное ядро с разделяемой памятью
            game_of_life_shared_kernel<<<grid_size, block_size>>>(
                d_grids[current_idx],
                d_grids[next_idx],
                width,
                device_heights[dev],
                pitches[dev]
            );

            CUDA_CHECK(cudaGetLastError());

            // Обмен граничными строками между устройствами
            if (num_devices > 1) {
                int prev_dev = (dev - 1 + num_devices) % num_devices;
                int next_dev = (dev + 1) % num_devices;

                // Обмен с предыдущим устройством (верхняя граница)
                if (dev > 0) {
                    // Копируем нижнюю строку предыдущего устройства в верхнюю строку текущего
                    // (здесь требуется синхронизация и копирование между устройствами)
                }

                // Обмен со следующим устройством (нижняя граница)
                if (dev < num_devices - 1) {
                    // Копируем верхнюю строку следующего устройства в нижнюю строку текущего
                }
            }
        }

        // Синхронизируем все устройства
        for (int dev = 0; dev < num_devices; dev++) {
            CUDA_CHECK(cudaSetDevice(dev));
            CUDA_CHECK(cudaDeviceSynchronize());
        }
    }

    // Копируем результаты обратно на хост
    for (int dev = 0; dev < num_devices; dev++) {
        CUDA_CHECK(cudaSetDevice(dev));

        int final_idx = dev * 2 + (iterations % 2);

        for (int row = 0; row < device_heights[dev]; row++) {
            int dst_row = start_rows[dev] + row;
            CUDA_CHECK(cudaMemcpy2D(
                h_grid + dst_row * width,
                width * sizeof(unsigned char),
                (unsigned char*)d_grids[final_idx] + row * pitches[dev],
                pitches[dev],
                width * sizeof(unsigned char),
                1,
                cudaMemcpyDeviceToHost
            ));
        }

        // Освобождаем память устройства
        CUDA_CHECK(cudaFree(d_grids[dev * 2]));
        CUDA_CHECK(cudaFree(d_grids[dev * 2 + 1]));
    }
}

/**
 * Функция для выполнения регрессионного теста
 */
bool regression_test(
    const unsigned char* grid,
    const unsigned char* reference,
    int width,
    int height,
    const std::string& test_name
) {
    bool passed = true;
    int error_count = 0;
    const int max_errors_to_report = 10;

    for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
            int idx = i * width + j;
            if (grid[idx] != reference[idx]) {
                error_count++;
                if (error_count <= max_errors_to_report) {
                    std::cerr << "Error at (" << i << "," << j << "): "
                              << "expected " << (int)reference[idx]
                              << ", got " << (int)grid[idx] << std::endl;
                }
                passed = false;
            }
        }
    }

    if (!passed) {
        std::cerr << "Regression test '" << test_name << "' failed with "
                  << error_count << " errors" << std::endl;
    } else {
        std::cout << "Regression test '" << test_name << "' passed" << std::endl;
    }

    return passed;
}

/**
 * Сохраняет изображение сетки в формате PGM
 */
void save_grid_as_image(
    const unsigned char* grid,
    int width,
    int height,
    const std::string& filename
) {
    std::ofstream file(filename, std::ios::binary);
    if (!file.is_open()) {
        std::cerr << "Error: Cannot open file " << filename << std::endl;
        return;
    }

    // Заголовок PGM
    file << "P5\n" << width << " " << height << "\n255\n";

    // Данные изображения (живые клетки = белые, мертвые = черные)
    for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
            unsigned char pixel = grid[i * width + j] * 255;
            file.write((char*)&pixel, 1);
        }
    }

    file.close();
    std::cout << "Image saved to " << filename << std::endl;
}

/**
 * Инициализирует сетку паттерном (например, планер)
 */
void initialize_glider_pattern(unsigned char* grid, int width, int height) {
    // Очищаем сетку
    memset(grid, 0, width * height * sizeof(unsigned char));

    // Центр сетки
    int center_x = width / 2;
    int center_y = height / 2;

    // Создаем паттерн планера
    if (center_x > 1 && center_y > 1 && center_x < width - 2 && center_y < height - 2) {
        grid[(center_y - 1) * width + center_x] = 1;
        grid[center_y * width + (center_x + 1)] = 1;
        grid[(center_y + 1) * width + (center_x - 1)] = 1;
        grid[(center_y + 1) * width + center_x] = 1;
        grid[(center_y + 1) * width + (center_x + 1)] = 1;
    }
}

/**
 * Инициализирует случайную сетку
 */
void initialize_random_pattern(unsigned char* grid, int width, int height, float density = 0.3f) {
    for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
            grid[i * width + j] = (rand() / (float)RAND_MAX < density) ? 1 : 0;
        }
    }
}

/**
 * Основная функция для запуска симуляции
 */
void run_game_of_life_simulation(const GridConfig& config) {
    // Выделяем память для сетки на хосте
    unsigned char* h_grid = new unsigned char[config.width * config.height];
    unsigned char* h_reference = nullptr;

    // Инициализируем сетку
    if (config.iterations < 100) {
        initialize_glider_pattern(h_grid, config.width, config.height);
    } else {
        initialize_random_pattern(h_grid, config.width, config.height, 0.3f);
    }

    // Для регрессионного теста создаем копию начального состояния
    if (config.iterations == 0) {
        h_reference = new unsigned char[config.width * config.height];
        memcpy(h_reference, h_grid, config.width * config.height * sizeof(unsigned char));
    }

    std::cout << "Starting Game of Life simulation..." << std::endl;
    std::cout << "Grid size: " << config.width << "x" << config.height << std::endl;
    std::cout << "Iterations: " << config.iterations << std::endl;
    std::cout << "GPU devices: " << config.device_count << std::endl;

    // Запускаем симуляцию
    game_of_life_multi_gpu(
        h_grid,
        config.width,
        config.height,
        config.iterations,
        config.device_count
    );

    // Выполняем регрессионный тест, если необходимо
    if (h_reference != nullptr) {
        bool test_passed = regression_test(
            h_grid,
            h_reference,
            config.width,
            config.height,
            "zero_iterations_test"
        );

        if (!test_passed) {
            // Сохраняем изображения для отладки
            save_grid_as_image(h_reference, config.width, config.height, "reference.pgm");
            save_grid_as_image(h_grid, config.width, config.height, "result.pgm");
        }

        delete[] h_reference;
    }

    // Сохраняем финальное состояние
    save_grid_as_image(h_grid, config.width, config.height, "final_state.pgm");

    // Освобождаем память
    delete[] h_grid;
}

// Точка входа для тестирования
int main(int argc, char** argv) {
    // Получаем количество доступных GPU
    int device_count;
