# Обёртка для KNN-графа на CUDA

Ниже представлена реализация обёртки для алгоритма построения и работы с KNN-графом на GPU с использованием CUDA и C++. Эта реализация включает все необходимые функции: загрузку данных, построение графа, уточнение, генерацию эталонов, оценку и выполнение запросов.

```cpp
#include <iostream>
#include <vector>
#include <fstream>
#include <memory>
#include <chrono>
#include <random>
#include <algorithm>
#include <cassert>
#include <cuda_runtime.h>
#include <cublas_v2.h>
#include <cusparse.h>
#include <thrust/device_vector.h>
#include <thrust/host_vector.h>
#include <thrust/sort.h>
#include <thrust/sequence.h>
#include <thrust/gather.h>
#include <thrust/iterator/constant_iterator.h>

class KNNGraphWrapper {
public:
    // Конструктор
    KNNGraphWrapper(int dim, int k, bool verbose = false, bool debug = false)
        : dim_(dim), k_(k), verbose_(verbose), debug_(debug) {
        // Инициализация CUDA
        cudaError_t err = cudaGetLastError();
        if (err != cudaSuccess) {
            std::cerr << "CUDA initialization error: " << cudaGetErrorString(err) << std::endl;
            throw std::runtime_error("CUDA initialization failed");
        }

        // Инициализация cuBLAS
        cublasStatus_t status = cublasCreate(&cublas_handle_);
        if (status != CUBLAS_STATUS_SUCCESS) {
            std::cerr << "cuBLAS initialization error" << std::endl;
            throw std::runtime_error("cuBLAS initialization failed");
        }

        // Инициализация cuSPARSE
        cusparseStatus_t sparse_status = cusparseCreate(&cusparse_handle_);
        if (sparse_status != CUSPARSE_STATUS_SUCCESS) {
            std::cerr << "cuSPARSE initialization error" << std::endl;
            throw std::runtime_error("cuSPARSE initialization failed");
        }
    }

    // Деструктор
    ~KNNGraphWrapper() {
        if (cublas_handle_) cublasDestroy(cublas_handle_);
        if (cusparse_handle_) cusparseDestroy(cusparse_handle_);
    }

    // Загрузка данных из файла
    bool loadData(const std::string& filename, bool normalize = true) {
        std::ifstream file(filename, std::ios::binary);
        if (!file) {
            std::cerr << "Error opening file: " << filename << std::endl;
            return false;
        }

        // Чтение количества точек и размерности
        int n;
        file.read(reinterpret_cast<char*>(&n), sizeof(int));
        file.read(reinterpret_cast<char*>(&dim_), sizeof(int));

        if (verbose_) {
            std::cout << "Loading " << n << " points with dimension " << dim_ << std::endl;
        }

        // Выделение памяти и чтение данных
        host_data_.resize(n * dim_);
        file.read(reinterpret_cast<char*>(host_data_.data()), n * dim_ * sizeof(float));

        // Нормализация данных (если требуется)
        if (normalize) {
            normalizeData();
        }

        // Копирование данных на устройство
        device_data_ = host_data_;

        n_ = n;
        return true;
    }

    // Сохранение графа в файл
    bool saveGraph(const std::string& filename) {
        if (host_indices_.empty() || host_distances_.empty()) {
            std::cerr << "Graph not built yet" << std::endl;
            return false;
        }

        std::ofstream file(filename, std::ios::binary);
        if (!file) {
            std::cerr << "Error opening file: " << filename << std::endl;
            return false;
        }

        // Запись метаданных
        file.write(reinterpret_cast<const char*>(&n_), sizeof(int));
        file.write(reinterpret_cast<const char*>(&k_), sizeof(int));

        // Запись индексов соседей
        file.write(reinterpret_cast<const char*>(host_indices_.data()),
                  n_ * k_ * sizeof(int));

        // Запись расстояний
        file.write(reinterpret_cast<const char*>(host_distances_.data()),
                  n_ * k_ * sizeof(float));

        if (verbose_) {
            std::cout << "Graph saved to " << filename << std::endl;
        }

        return true;
    }

    // Загрузка графа из файла
    bool loadGraph(const std::string& filename) {
        std::ifstream file(filename, std::ios::binary);
        if (!file) {
            std::cerr << "Error opening file: " << filename << std::endl;
            return false;
        }

        // Чтение метаданных
        int n, k;
        file.read(reinterpret_cast<char*>(&n), sizeof(int));
        file.read(reinterpret_cast<char*>(&k), sizeof(int));

        if (n != n_ || k != k_) {
            std::cerr << "Graph dimensions don't match current configuration" << std::endl;
            return false;
        }

        // Выделение памяти и чтение данных
        host_indices_.resize(n * k);
        host_distances_.resize(n * k);

        file.read(reinterpret_cast<char*>(host_indices_.data()), n * k * sizeof(int));
        file.read(reinterpret_cast<char*>(host_distances_.data()), n * k * sizeof(float));

        // Копирование на устройство
        device_indices_ = host_indices_;
        device_distances_ = host_distances_;

        if (verbose_) {
            std::cout << "Graph loaded from " << filename << std::endl;
        }

        return true;
    }

    // Построение графа KNN
    void buildGraph() {
        if (device_data_.empty()) {
            std::cerr << "Data not loaded" << std::endl;
            return;
        }

        if (verbose_) {
            std::cout << "Building KNN graph with k = " << k_ << std::endl;
        }

        auto start = std::chrono::high_resolution_clock::now();

        // Выделение памяти для результатов
        device_indices_.resize(n_ * k_);
        device_distances_.resize(n_ * k_);

        // Вычисление попарных расстояний
        computePairwiseDistances();

        // Поиск k ближайших соседей для каждой точки
        findKNearestNeighbors();

        // Копирование результатов на хост
        host_indices_ = device_indices_;
        host_distances_ = device_distances_;

        auto end = std::chrono::high_resolution_clock::now();
        auto duration = std::chrono::duration_cast<std::chrono::milliseconds>(end - start);

        if (verbose_) {
            std::cout << "Graph built in " << duration.count() << " ms" << std::endl;
        }
    }

    // Уточнение графа (например, с помощью алгоритма NN-descent)
    void refineGraph(int iterations = 5) {
        if (device_indices_.empty()) {
            std::cerr << "Graph not built yet" << std::endl;
            return;
        }

        if (verbose_) {
            std::cout << "Refining graph with " << iterations << " iterations" << std::endl;
        }

        auto start = std::chrono::high_resolution_clock::now();

        for (int iter = 0; iter < iterations; ++iter) {
            // Реализация алгоритма уточнения (например, NN-descent)
            // Это упрощенная версия - в реальной реализации нужно использовать
            // более сложные алгоритмы уточнения графа
            refineGraphIteration();
        }

        // Копирование обновленного графа на хост
        host_indices_ = device_indices_;
        host_distances_ = device_distances_;

        auto end = std::chrono::high_resolution_clock::now();
        auto duration = std::chrono::duration_cast<std::chrono::milliseconds>(end - start);

        if (verbose_) {
            std::cout << "Graph refined in " << duration.count() << " ms" << std::endl;
        }
    }

    // Генерация эталонных данных для оценки
    void generateGroundTruth(int gt_k, int num_queries = 1000) {
        if (device_data_.empty()) {
            std::cerr << "Data not loaded" << std::endl;
            return;
        }

        if (verbose_) {
            std::cout << "Generating ground truth for " << num_queries
                      << " queries with k = " << gt_k << std::endl;
        }

        // Выбор случайных запросов
        std::vector<int> query_indices(num_queries);
        std::iota(query_indices.begin(), query_indices.end(), 0);
        std::random_shuffle(query_indices.begin(), query_indices.end());
        query_indices.resize(std::min(num_queries, n_));

        // Выделение памяти для эталонных данных
        gt_indices_.resize(num_queries * gt_k);
        gt_distances_.resize(num_queries * gt_k);

        // Вычисление эталонных результатов (brute-force)
        for (int i = 0; i < num_queries; ++i) {
            int query_idx = query_indices[i];
            const float* query = host_data_.data() + query_idx * dim_;

            std::vector<std::pair<float, int>> distances(n_);
            for (int j = 0; j < n_; ++j) {
                if (j == query_idx) {
                    distances[j] = {std::numeric_limits<float>::max(), j};
                    continue;
                }
                const float* point = host_data_.data() + j * dim_;
                float dist = 0.0f;
                for (int d = 0; d < dim_; ++d) {
                    float diff = query[d] - point[d];
                    dist += diff * diff;
                }
                distances[j] = {dist, j};
            }

            // Сортировка по расстоянию
            std::sort(distances.begin(), distances.end(),
                [](const auto& a, const auto& b) { return a.first < b.first; });

            // Сохранение результатов
            for (int j = 0; j < gt_k; ++j) {
                gt_indices_[i * gt_k + j] = distances[j].second;
                gt_distances_[i * gt_k + j] = distances[j].first;
            }
        }

        if (verbose_) {
            std::cout << "Ground truth generated" << std::endl;
        }
    }

    // Оценка графа KNN
    void evaluateGraph(int gt_k) {
        if (gt_indices_.empty()) {
            std::cerr << "Ground truth not generated" << std::endl;
            return;
        }

        if (host_indices_.empty()) {
            std::cerr << "Graph not built" << std::endl;
            return;
        }

        if (verbose_) {
            std::cout << "Evaluating graph with k = " << k_
                      << " against ground truth with k = " << gt_k << std::endl;
        }

        int num_queries = gt_indices_.size() / gt_k;
        int correct = 0;
        int total = num_queries * gt_k;

        for (int i = 0; i < num_queries; ++i) {
            // Получение результатов из графа
            std::vector<int> graph_results(k_);
            for (int j = 0; j < k_; ++j) {
                graph_results[j] = host_indices_[i * k_ + j];
            }

            // Получение эталонных результатов
            std::vector<int> gt_results(gt_k);
            for (int j = 0; j < gt_k; ++j) {
                gt_results[j] = gt_indices_[i * gt_k + j];
            }

            // Сравнение результатов
            for (int j = 0; j < gt_k; ++j) {
                if (std::find(graph_results.begin(), graph_results.end(), gt_results[j])
                    != graph_results.end()) {
                    correct++;
                }
            }
        }

        float recall = static_cast<float>(correct) / total;
        if (verbose_) {
            std::cout << "Recall: " << recall * 100 << "%" << std::endl;
        }
    }

    // Выполнение запроса KNN
    void query(const std::vector<float>& query_vector, int query_k,
               std::vector<int>& indices, std::vector<float>& distances,
               bool detailed = false) {
        if (device_indices_.empty()) {
            std::cerr << "Graph not built" << std::endl;
            return;
        }

        if (query_vector.size() != dim_) {
            std::cerr << "Query dimension mismatch" << std::endl;
            return;
        }

        if (query_k > k_) {
            std::cerr << "Query k is larger than graph k" << std::endl;
            return;
        }

        // Копирование запроса на устройство
        thrust::device_vector<float> d_query(query_vector);

        // Вычисление расстояний от запроса до всех точек в графе
        thrust::device_vector<float> d_query_distances(n_);

        // Используем cuBLAS для вычисления расстояний
        float alpha = -2.0f;
        float beta = 0.0f;

        // Вычисляем query^T * data
        thrust::device_vector<float> d_dot_products(n_);
        cublasSgemv(cublas_handle_, CUBLAS_OP_T,
                   dim_, n_,
                   &alpha,
                   thrust::raw_pointer_cast(device_data_.data()), dim_,
                   thrust::raw_pointer_cast(d_query.data()), 1,
                   &beta,
                   thrust::raw_pointer_cast(d_dot_products.data()), 1);

        // Добавляем квадраты норм
        thrust::device_vector<float> d_query_norm(1);
        cublasSnrm2(cublas_handle_, dim_,
                   thrust::raw_pointer_cast(d_query.data()), 1,
                   thrust::raw_pointer_cast(d_query_norm.data()));

        thrust::device_vector<float> d_data_norms(n_);
        for (int i = 0; i < n_; ++i) {
            cublasSnrm2(cublas_handle_, dim_,
                       thrust::raw_pointer_cast(device_data_.data()) + i * dim_, 1,
                       thrust::raw_pointer_cast(d_data_norms.data()) + i);
        }

        // Вычисляем расстояния: dist = query_norm^2 + data_norm^2 - 2*dot_product
        thrust::transform(
            d_dot_products.begin(), d_dot_products.end(),
            d_data_norms.begin(),
            d_query_distances.begin(),
            [query_norm = d_query_norm[0]] __device__ (float dot, float data_norm) {
                return query_norm * query_norm + data_norm * data_norm + dot;
            });

        // Находим k ближайших соседей
        thrust::device_vector<int> d_indices(n_);
        thrust::sequence(d_indices.begin(), d_indices.end());

        // Сортируем индексы по расстоянию
        thrust::sort_by_key(d_query_distances.begin(), d_query_distances.end(),
                           d_indices.begin());

        // Копируем результаты на хост
        indices.resize(query_k);
        distances.resize(query_k);

        thrust::host_vector<int> h_indices = d_indices;
        thrust::host_vector<float> h_distances = d_query_distances;

        for (int i = 0; i < query_k; ++i) {
            indices[i] = h_indices[i];
            distances[i] = h_distances[i];
        }

        if (detailed) {
            std::cout << "Query results:" << std::endl;
            for (int i = 0; i < query_k; ++i) {
                std::cout << "  " << i << ": index=" << indices[i]
                          << ", distance=" << distances[i] << std::endl;
            }
        }
    }

    // Сброс графа для подмножества данных
    void resetGraphForSubset(int subset_size) {
        if (subset_size > n_) {
            std::cerr << "Subset size larger than dataset size" << std::endl;
            return;
        }

        if (verbose_) {
            std::cout << "Resetting graph for subset of size " << subset_size << std::endl;
        }

        // Создаем новое подмножество данных
        thrust::device_vector<float> new_device_data(subset_size * dim_);
        thrust::device_vector<int> new_device_indices(subset_size * k_);
        thrust::device_vector<float> new_device_distances(subset_size * k_);

        // Копируем первые subset_size точек
        thrust::copy(device_data_.begin(), device_data_.begin() + subset_size * dim_,
                    new_device_data.begin());

        // Обновляем размеры
        n_ = subset_size;
        device_data_ = new_device_data;
        device_indices_ = new_device_indices;
        device_distances_ = new_device_distances;

        // Копируем на хост
        host_data_ = device_data_;
        host_indices_ = device_indices_;
        host_distances_ = device_distances_;
    }

    // Установка режима отладки
    void setDebug(bool debug) {
        debug_ = debug;
    }

    // Установка подробного вывода
    void setVerbose(bool verbose) {
        verbose_ = verbose;
    }

private:
    int dim_;               // Размерность данных
    int n_ = 0;             // Количество точек
    int k_;                 // Количество соседей в графе
    bool verbose_;          // Флаг подробного вывода
    bool debug_;            // Флаг отладки

    // Данные на хосте и устройстве
    thrust::host_vector<float> host_data_;
    thrust::device_vector<float> device_data_;

    // Граф на хосте и устройстве
    thrust::host_vector<int> host_indices_;
    thrust::host_vector<float> host_distances_;
    thrust::device_vector<int> device_indices_;
    thrust::device_vector<float> device_distances_;

    // Эталонные данные
    thrust::host_vector<int> gt_indices_;
    thrust::host_vector<float> gt_distances_;

    // Обработчики cuBLAS и cuSPARSE
    cublasHandle_t cublas_handle_;
    cusparseHandle_t cusparse_handle_;

    // Нормализация данных
    void normalizeData() {
        if (verbose_) {
            std::cout << "Normalizing data" << std::endl;
        }

        for (int i = 0; i < n_; ++i) {
            float norm = 0.0f;
            for (int d = 0; d < dim_; ++d) {
                norm += host_data_[i * dim_ + d] * host_data_[i * dim_ + d];
            }
            norm = std::sqrt(norm);

            if (norm > 0) {
                for (int d = 0; d < dim_; ++d) {
                    host_data_[i * dim_ + d] /= norm;
                }
            }
        }
    }

    // Вычисление попарных расстояний
    void computePairwiseDistances() {
        if (verbose_) {
            std::cout << "Computing pairwise distances" << std::endl;
        }

        // Реализация с использованием cuBLAS для эффективного вычисления расстояний
        // Это упрощенная версия - в реальной реализации нужно использовать
        // более оптимальные подходы (например, блочное вычисление)

        // Выделяем память для матрицы расстояний
        thrust::device_vector<float> d_distances(n_ * n_);

        // Вычисляем попарные расстояния
        float alpha = -2.0f;
        float beta = 0.0f;

        // Вычисляем data^T * data
        cublasSgemm(cublas_handle_, CUBLAS_OP_T, CUBLAS_OP_N,
                   n_, n_, dim_,
                   &alpha,
                   thrust::raw_pointer_cast(device_data_.data()), dim_,
                   thrust::raw_pointer_cast(device_data_.data()), dim_,
                   &beta,
                   thrust::raw_pointer_cast(d_distances.data()), n_);

        // Добавляем квадраты норм
        thrust::device_vector<float> d_norms(n_);
        for (int i = 0; i < n_; ++i) {
            cublasSnrm2(cublas_handle_, dim_,
                       thrust::raw_pointer_cast(device_data_.data()) + i * dim_, 1,
                       thrust::raw_pointer_cast(d_norms.data()) + i);
        }

        // Расстояние = norm_i^2 + norm_j^2 - 2*dot_product
        thrust::transform(
            d_distances.begin(), d_distances.end(),
            thrust::make_permutation_iterator(
                d_norms.begin(),
                thrust::make_transform_iterator(
                    thrust::make_counting_iterator(0),
                    [n = n_] __device__ (int idx) { return idx / n; })),
            d_distances.begin(),
            [] __device__ (float dot, float norm_i) {
                return norm_i * norm_i + dot;
            });

        thrust::transform(
            d_distances.begin(), d_distances.end(),
            thrust::make_permutation_iterator(
                d_norms.begin(),
                thrust::make_transform_iterator(
                    thrust::make_counting_iterator(0),
                    [n = n_] __device__ (int idx) { return idx % n; })),
            d_distances.begin(),
            [] __device__ (float dist, float norm_j) {
                return dist + norm_j * norm_j;
            });

        // Копируем расстояния в device_distances_ (только верхний треугольник)
        // В реальной реализации нужно использовать более эффективный подход
    }

    // Поиск k ближайших соседей
    void findKNearestNeighbors() {
        if (verbose_) {
            std::cout << "Finding " << k_ << " nearest neighbors" << std::endl;
        }

        // Для каждой точки находим k ближайших соседей
        for (int i = 0; i < n_; ++i) {
            // Получаем расстояния от точки i до всех остальных точек
            thrust::device_vector<float> distances(n_);
            thrust::copy(
                device_distances_.begin() + i * n_,
                device_distances_.begin() + (i + 1) * n_,
                distances.begin());

            // Создаем индексы
            thrust::device_vector<int> indices(n_);
            thrust::sequence(indices.begin(), indices.end());

            // Сортируем индексы по расстоянию
            thrust::sort_by_key(distances.begin(), distances.end(), indices.begin());

            // Копируем k ближайших соседей (пропуская саму точку)
            thrust::copy(indices.begin() + 1, indices.begin() + k_ + 1,
                        device_indices_.begin() + i * k_);

            // Копируем соответствующие расстояния
            thrust::copy(distances.begin() + 1, distances.begin() + k_ + 1,
                        device_distances_.begin() + i * k_);
        }
    }

    // Итерация уточнения графа
    void refineGraphIteration() {
        if (verbose_) {
            std::cout << "Refining graph iteration" << std::endl;
        }

        // Реализация алгоритма уточнения графа (например, NN-descent)
        // Это упрощенная версия - в реальной реализации нужно использовать
        // более сложные алгоритмы

        // Для каждой точки
        for (int i = 0; i < n_; ++i) {
            // Получаем текущих соседей
            std::vector<int> current_neighbors(k_);
            thrust::copy(
                device_indices_.begin() + i * k_,
                device_indices_.begin() + (i + 1) * k_,
                current_neighbors.begin());

            // Получаем соседей соседей
            std::vector<int> candidate_neighbors;
            for (int neighbor : current_neighbors) {
                thrust::host_vector<int> neighbor_neighbors(k_);
                thrust::copy(
                    device_indices_.begin() + neighbor * k_,
                    device_indices_.begin() + (neighbor + 1) * k_,
                    neighbor_neighbors.begin());

                for (int nn : neighbor_neighbors) {
                    if (nn != i && std::find(current_neighbors.begin(), current_neighbors.end(), nn)
                        == current_neighbors.end()) {
                        candidate_neighbors.push_back(nn);
                    }
                }
            }

            // Убираем дубликаты
            std::sort(candidate_neighbors.begin(), candidate_neighbors.end());
            candidate_neighbors.erase(
                std::unique(candidate_neighbors.begin(), candidate_neighbors.end()),
                candidate_neighbors.end());

            // Вычисляем расстояния до кандидатов
            std::vector<std::pair<float, int>> candidates_with_distances;
            for (int candidate : candidate_neighbors) {
                float dist = 0.0f;
                for (int d = 0; d < dim_; ++d) {
                    float diff = host_data_[i * dim_ + d] - host_data_[candidate * dim_ + d];
                    dist += diff * diff;
                }
                candidates_with_distances.emplace_back(dist, candidate);
            }

            // Сортируем кандидатов по расстоянию
            std::sort(candidates_with_distances.begin(), candidates_with_distances.end(),
                [](const auto& a, const auto& b) { return a.first < b.first; });

            // Объединяем текущих соседей и лучших кандидатов
            std::vector<std::pair<float, int>> all_candidates;
            for (int j = 0; j < k_; ++j) {
                all_candidates.emplace_back(
                    host_distances_[i * k_ + j],
                    host_indices_[i * k_ + j]);
            }

            for (size_t j = 0; j < std::min(candidates_with_distances.size(), size_t(k_)); ++j) {
                all_candidates.push_back(candidates_with_distances[j]);
            }

            // Сортируем и выбираем k лучших
            std::sort(all_candidates.begin(), all_candidates.end(),
                [](const auto& a, const auto& b) { return a.first < b.first; });

            // Обновляем соседей
            for (int j = 0; j < k_; ++j) {
                host_indices_[i * k_ + j] = all_candidates[j].second;
                host_distances_[i * k_ + j] = all_candidates[j].first;
            }
        }

        // Копируем обновленные данные на устройство
        device_indices_ = host_indices_;
        device_distances_ = host_distances_;
    }
};

// Пример использования
int main() {
    try {
        // Создание обёртки
        KNNGraphWrapper knn(128, 10, true, false);

        // Загрузка данных
        if (!knn.loadData("data.bin")) {
            std::cerr << "Failed to load data" << std::endl;
            return 1;
        }

        // Построение графа
        knn.buildGraph();

        // Уточнение графа
        knn.refineGraph(3);

        // Генерация эталонных данных
        knn.generateGroundTruth(10, 100);

        // Оценка графа
        knn.evaluateGraph(10);

        // Сохранение графа
        knn.saveGraph("graph.bin");

        // Пример запроса
        std::vector<float> query(128, 0.1f); // Пример запроса
        std::vector<int> indices;
        std::vector<float> distances;

        knn.query(query, 5, indices, distances, true);

        // Сброс графа для подмножества
        knn.resetGraphForSubset(1000);

    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
```

