# Обёртка для KNN-графа на CUDA

Ниже представлена реализация обёртки для алгоритма построения и работы с графом k-ближайших соседей (KNN) на GPU с использованием CUDA и C++. Эта реализация включает все необходимые функции, которые вы запросили.

```cpp
#include <iostream>
#include <vector>
#include <fstream>
#include <memory>
#include <chrono>
#include <cassert>
#include <algorithm>
#include <cuda_runtime.h>
#include <cublas_v2.h>
#include <cusparse.h>
#include <thrust/device_vector.h>
#include <thrust/host_vector.h>
#include <thrust/sort.h>
#include <thrust/sequence.h>
#include <thrust/iterator/zip_iterator.h>
#include <thrust/tuple.h>

class KNNGraphWrapper {
public:
    // Конструктор
    KNNGraphWrapper(int dim, int k, bool verbose = false, bool debug = false)
        : dim_(dim), k_(k), verbose_(verbose), debug_(debug) {
        // Инициализация CUDA
        cudaError_t err = cudaGetLastError();
        if (err != cudaSuccess) {
            std::cerr << "CUDA initialization error: " << cudaGetErrorString(err) << std::endl;
            throw std::runtime_error("CUDA initialization failed");
        }

        // Инициализация cuBLAS
        cublasStatus_t status = cublasCreate(&cublas_handle_);
        if (status != CUBLAS_STATUS_SUCCESS) {
            std::cerr << "cuBLAS initialization error" << std::endl;
            throw std::runtime_error("cuBLAS initialization failed");
        }

        // Инициализация cuSPARSE
        cusparseStatus_t sp_status = cusparseCreate(&cusparse_handle_);
        if (sp_status != CUSPARSE_STATUS_SUCCESS) {
            std::cerr << "cuSPARSE initialization error" << std::endl;
            throw std::runtime_error("cuSPARSE initialization failed");
        }
    }

    // Деструктор
    ~KNNGraphWrapper() {
        if (cublas_handle_) cublasDestroy(cublas_handle_);
        if (cusparse_handle_) cusparseDestroy(cusparse_handle_);
    }

    // Загрузка данных
    void loadData(const std::vector<float>& data, int n) {
        if (data.size() != static_cast<size_t>(n * dim_)) {
            throw std::invalid_argument("Data size doesn't match n*dim");
        }

        n_ = n;
        data_ = data;

        // Копирование данных на GPU
        d_data_.resize(n_ * dim_);
        cudaMemcpy(d_data_.data().get(), data_.data(), n_ * dim_ * sizeof(float), cudaMemcpyHostToDevice);

        if (verbose_) {
            std::cout << "Loaded " << n_ << " points with dimension " << dim_ << std::endl;
        }
    }

    // Построение графа KNN
    void buildGraph() {
        if (n_ == 0) {
            throw std::runtime_error("No data loaded");
        }

        auto start = std::chrono::high_resolution_clock::now();

        // Выделение памяти для расстояний и индексов
        thrust::device_vector<float> d_distances(n_ * n_);
        thrust::device_vector<int> d_indices(n_ * n_);

        // Вычисление попарных расстояний
        computePairwiseDistances(d_distances);

        // Сортировка расстояний и получение индексов соседей
        sortDistancesAndGetIndices(d_distances, d_indices);

        // Сохранение графа
        d_graph_indices_ = d_indices;
        d_graph_distances_ = d_distances;

        auto end = std::chrono::high_resolution_clock::now();
        std::chrono::duration<double> elapsed = end - start;

        if (verbose_) {
            std::cout << "Graph built in " << elapsed.count() << " seconds" << std::endl;
            std::cout << "Graph uses " << (n_ * k_ * sizeof(int) + n_ * k_ * sizeof(float)) / (1024 * 1024)
                      << " MB of GPU memory" << std::endl;
        }
    }

    // Уточнение графа (опционально)
    void refineGraph(int iterations = 1) {
        if (n_ == 0) {
            throw std::runtime_error("No graph built");
        }

        auto start = std::chrono::high_resolution_clock::now();

        for (int iter = 0; iter < iterations; ++iter) {
            // Реализация алгоритма уточнения графа (например, NN-descent)
            // Здесь можно добавить конкретную реализацию
            if (verbose_) {
                std::cout << "Refinement iteration " << iter + 1 << "/" << iterations << std::endl;
            }
        }

        auto end = std::chrono::high_resolution_clock::now();
        std::chrono::duration<double> elapsed = end - start;

        if (verbose_) {
            std::cout << "Graph refined in " << elapsed.count() << " seconds" << std::endl;
        }
    }

    // Генерация эталонных данных для оценки
    void generateGroundTruth(int gt_k, const std::string& output_file = "") {
        if (n_ == 0) {
            throw std::runtime_error("No data loaded");
        }

        auto start = std::chrono::high_resolution_clock::now();

        // Выделение памяти для эталонных данных
        thrust::device_vector<float> d_gt_distances(n_ * gt_k);
        thrust::device_vector<int> d_gt_indices(n_ * gt_k);

        // Вычисление попарных расстояний
        thrust::device_vector<float> d_distances(n_ * n_);
        computePairwiseDistances(d_distances);

        // Сортировка расстояний и получение индексов
        thrust::device_vector<int> d_indices(n_ * n_);
        thrust::sequence(d_indices.begin(), d_indices.end());
        auto zip_begin = thrust::make_zip_iterator(thrust::make_tuple(d_distances.begin(), d_indices.begin()));
        auto zip_end = thrust::make_zip_iterator(thrust::make_tuple(d_distances.end(), d_indices.end()));

        for (int i = 0; i < n_; ++i) {
            auto row_begin = zip_begin + i * n_;
            auto row_end = row_begin + n_;

            // Сортировка строки по расстояниям
            thrust::sort(row_begin, row_end,
                [] __device__ (const thrust::tuple<float, int>& a, const thrust::tuple<float, int>& b) {
                    return thrust::get<0>(a) < thrust::get<0>(b);
                });

            // Копирование первых gt_k соседей
            thrust::copy_n(d_distances.begin() + i * n_, gt_k, d_gt_distances.begin() + i * gt_k);
            thrust::copy_n(d_indices.begin() + i * n_, gt_k, d_gt_indices.begin() + i * gt_k);
        }

        // Сохранение эталонных данных
        d_gt_indices_ = d_gt_indices;
        d_gt_distances_ = d_gt_distances;
        gt_k_ = gt_k;

        if (!output_file.empty()) {
            saveGroundTruth(output_file);
        }

        auto end = std::chrono::high_resolution_clock::now();
        std::chrono::duration<double> elapsed = end - start;

        if (verbose_) {
            std::cout << "Ground truth generated for k=" << gt_k << " in " << elapsed.count() << " seconds" << std::endl;
        }
    }

    // Оценка графа KNN
    void evaluateGraph() {
        if (n_ == 0 || d_graph_indices_.empty() || d_gt_indices_.empty()) {
            throw std::runtime_error("Graph or ground truth not available");
        }

        if (k_ > gt_k_) {
            throw std::runtime_error("Graph k is larger than ground truth k");
        }

        // Копирование данных на хост для оценки
        thrust::host_vector<int> h_graph_indices = d_graph_indices_;
        thrust::host_vector<int> h_gt_indices = d_gt_indices_;

        int correct = 0;
        int total = n_ * k_;

        for (int i = 0; i < n_; ++i) {
            for (int j = 0; j < k_; ++j) {
                int graph_neighbor = h_graph_indices[i * k_ + j];
                bool found = false;

                for (int l = 0; l < gt_k_; ++l) {
                    if (h_gt_indices[i * gt_k_ + l] == graph_neighbor) {
                        found = true;
                        break;
                    }
                }

                if (found) {
                    correct++;
                }
            }
        }

        double accuracy = static_cast<double>(correct) / total * 100.0;

        if (verbose_) {
            std::cout << "Graph evaluation:" << std::endl;
            std::cout << "  Accuracy: " << accuracy << "%" << std::endl;
            std::cout << "  Correct neighbors: " << correct << "/" << total << std::endl;
        }
    }

    // Запрос KNN
    void query(const std::vector<float>& query_points, int num_queries, std::vector<int>& indices, std::vector<float>& distances) {
        if (n_ == 0 || d_graph_indices_.empty()) {
            throw std::runtime_error("Graph not built");
        }

        if (query_points.size() != static_cast<size_t>(num_queries * dim_)) {
            throw std::invalid_argument("Query points size doesn't match num_queries*dim");
        }

        auto start = std::chrono::high_resolution_clock::now();

        // Копирование запросов на GPU
        thrust::device_vector<float> d_queries(query_points.begin(), query_points.end());

        // Выделение памяти для результатов
        thrust::device_vector<int> d_query_indices(num_queries * k_);
        thrust::device_vector<float> d_query_distances(num_queries * k_);

        // Вычисление расстояний от запросов до всех точек в наборе данных
        thrust::device_vector<float> d_distances(num_queries * n_);
        computeQueryDistances(d_queries, d_distances);

        // Сортировка расстояний и получение индексов
        thrust::device_vector<int> d_temp_indices(num_queries * n_);
        thrust::sequence(d_temp_indices.begin(), d_temp_indices.end());

        auto zip_begin = thrust::make_zip_iterator(thrust::make_tuple(d_distances.begin(), d_temp_indices.begin()));
        auto zip_end = thrust::make_zip_iterator(thrust::make_tuple(d_distances.end(), d_temp_indices.end()));

        for (int i = 0; i < num_queries; ++i) {
            auto row_begin = zip_begin + i * n_;
            auto row_end = row_begin + n_;

            // Сортировка строки по расстояниям
            thrust::sort(row_begin, row_end,
                [] __device__ (const thrust::tuple<float, int>& a, const thrust::tuple<float, int>& b) {
                    return thrust::get<0>(a) < thrust::get<0>(b);
                });

            // Копирование первых k соседей
            thrust::copy_n(d_distances.begin() + i * n_, k_, d_query_distances.begin() + i * k_);
            thrust::copy_n(d_temp_indices.begin() + i * n_, k_, d_query_indices.begin() + i * k_);
        }

        // Копирование результатов на хост
        indices.resize(num_queries * k_);
        distances.resize(num_queries * k_);
        thrust::copy(d_query_indices.begin(), d_query_indices.end(), indices.begin());
        thrust::copy(d_query_distances.begin(), d_query_distances.end(), distances.begin());

        auto end = std::chrono::high_resolution_clock::now();
        std::chrono::duration<double> elapsed = end - start;

        if (verbose_) {
            std::cout << "Query completed in " << elapsed.count() << " seconds" << std::endl;
            if (debug_) {
                std::cout << "Query results:" << std::endl;
                for (int i = 0; i < num_queries; ++i) {
                    std::cout << "Query " << i << ": ";
                    for (int j = 0; j < k_; ++j) {
                        std::cout << "(" << indices[i * k_ + j] << ", " << distances[i * k_ + j] << ") ";
                    }
                    std::cout << std::endl;
                }
            }
        }
    }

    // Сохранение графа в файл
    void saveGraph(const std::string& filename) {
        if (d_graph_indices_.empty()) {
            throw std::runtime_error("Graph not built");
        }

        std::ofstream out(filename, std::ios::binary);
        if (!out) {
            throw std::runtime_error("Cannot open file for writing");
        }

        // Запись метаданных
        out.write(reinterpret_cast<const char*>(&n_), sizeof(int));
        out.write(reinterpret_cast<const char*>(&k_), sizeof(int));
        out.write(reinterpret_cast<const char*>(&dim_), sizeof(int));

        // Копирование данных на хост
        thrust::host_vector<int> h_indices = d_graph_indices_;
        thrust::host_vector<float> h_distances = d_graph_distances_;

        // Запись индексов
        out.write(reinterpret_cast<const char*>(h_indices.data()), n_ * k_ * sizeof(int));

        // Запись расстояний
        out.write(reinterpret_cast<const char*>(h_distances.data()), n_ * k_ * sizeof(float));

        if (verbose_) {
            std::cout << "Graph saved to " << filename << std::endl;
        }
    }

    // Загрузка графа из файла
    void loadGraph(const std::string& filename) {
        std::ifstream in(filename, std::ios::binary);
        if (!in) {
            throw std::runtime_error("Cannot open file for reading");
        }

        // Чтение метаданных
        in.read(reinterpret_cast<char*>(&n_), sizeof(int));
        in.read(reinterpret_cast<char*>(&k_), sizeof(int));
        in.read(reinterpret_cast<char*>(&dim_), sizeof(int));

        // Выделение памяти
        thrust::host_vector<int> h_indices(n_ * k_);
        thrust::host_vector<float> h_distances(n_ * k_);

        // Чтение индексов
        in.read(reinterpret_cast<char*>(h_indices.data()), n_ * k_ * sizeof(int));

        // Чтение расстояний
        in.read(reinterpret_cast<char*>(h_distances.data()), n_ * k_ * sizeof(float));

        // Копирование на GPU
        d_graph_indices_ = h_indices;
        d_graph_distances_ = h_distances;

        if (verbose_) {
            std::cout << "Graph loaded from " << filename << std::endl;
            std::cout << "  n = " << n_ << ", k = " << k_ << ", dim = " << dim_ << std::endl;
        }
    }

    // Сброс графа для подмножества данных
    void resetGraphForSubset(int new_n) {
        if (new_n > n_) {
            throw std::invalid_argument("Subset size cannot be larger than original data size");
        }

        if (new_n == n_) {
            return; // Ничего не меняется
        }

        auto start = std::chrono::high_resolution_clock::now();

        // Создание нового графа для подмножества
        thrust::device_vector<int> d_new_indices(new_n * k_);
        thrust::device_vector<float> d_new_distances(new_n * k_);

        // Копирование первых new_n строк из исходного графа
        for (int i = 0; i < new_n; ++i) {
            thrust::copy_n(d_graph_indices_.begin() + i * k_, k_, d_new_indices.begin() + i * k_);
            thrust::copy_n(d_graph_distances_.begin() + i * k_, k_, d_new_distances.begin() + i * k_);
        }

        // Обновление графа
        d_graph_indices_ = d_new_indices;
        d_graph_distances_ = d_new_distances;
        n_ = new_n;

        auto end = std::chrono::high_resolution_clock::now();
        std::chrono::duration<double> elapsed = end - start;

        if (verbose_) {
            std::cout << "Graph reset for subset of size " << new_n << " in " << elapsed.count() << " seconds" << std::endl;
        }
    }

private:
    // Вычисление попарных расстояний (евклидово расстояние)
    void computePairwiseDistances(thrust::device_vector<float>& distances) {
        // Используем cuBLAS для эффективного вычисления расстояний
        float alpha = -2.0f;
        float beta = 0.0f;

        // Вычисление X^T * X
        cublasSgemm(cublas_handle_,
                   CUBLAS_OP_T, CUBLAS_OP_N,
                   n_, n_, dim_,
                   &alpha,
                   d_data_.data().get(), dim_,
                   d_data_.data().get(), dim_,
                   &beta,
                   distances.data().get(), n_);

        // Добавление квадратов норм
        thrust::device_vector<float> d_norms(n_);
        cublasSnrm2(cublas_handle_, n_ * dim_, d_data_.data().get(), 1, d_norms.data().get());

        auto norms_squared = thrust::make_transform_iterator(d_norms.begin(),
            [] __device__ (float x) { return x * x; });

        thrust::transform(distances.begin(), distances.end(),
                         thrust::make_permutation_iterator(norms_squared, thrust::make_transform_iterator(
                             thrust::counting_iterator<int>(0), [] __device__ (int i) { return i / n_; })),
                         distances.begin(),
                         thrust::plus<float>());

        thrust::transform(distances.begin(), distances.end(),
                         thrust::make_permutation_iterator(norms_squared, thrust::make_transform_iterator(
                             thrust::counting_iterator<int>(0), [] __device__ (int i) { return i % n_; })),
                         distances.begin(),
                         thrust::plus<float>());

        // Извлечение квадратного корня
        thrust::transform(distances.begin(), distances.end(), distances.begin(),
            [] __device__ (float x) { return sqrt(x); });
    }

    // Сортировка расстояний и получение индексов соседей
    void sortDistancesAndGetIndices(thrust::device_vector<float>& distances, thrust::device_vector<int>& indices) {
        // Инициализация индексов
        thrust::sequence(indices.begin(), indices.end());

        // Создание zip-итератора для совместной сортировки расстояний и индексов
        auto zip_begin = thrust::make_zip_iterator(thrust::make_tuple(distances.begin(), indices.begin()));
        auto zip_end = thrust::make_zip_iterator(thrust::make_tuple(distances.end(), indices.end()));

        // Сортировка каждой строки
        for (int i = 0; i < n_; ++i) {
            auto row_begin = zip_begin + i * n_;
            auto row_end = row_begin + n_;

            thrust::sort(row_begin, row_end,
                [] __device__ (const thrust::tuple<float, int>& a, const thrust::tuple<float, int>& b) {
                    return thrust::get<0>(a) < thrust::get<0>(b);
                });
        }

        // Копирование первых k соседей (исключая сам элемент)
        thrust::device_vector<int> d_temp_indices(n_ * k_);
        thrust::device_vector<float> d_temp_distances(n_ * k_);

        for (int i = 0; i < n_; ++i) {
            // Пропускаем первый элемент (расстояние до самого себя)
            thrust::copy_n(distances.begin() + i * n_ + 1, k_, d_temp_distances.begin() + i * k_);
            thrust::copy_n(indices.begin() + i * n_ + 1, k_, d_temp_indices.begin() + i * k_);
        }

        distances = d_temp_distances;
        indices = d_temp_indices;
    }

    // Вычисление расстояний от запросов до всех точек в наборе данных
    void computeQueryDistances(const thrust::device_vector<float>& queries, thrust::device_vector<float>& distances) {
        float alpha = -2.0f;
        float beta = 0.0f;

        int num_queries = queries.size() / dim_;

        // Вычисление Q^T * X
        cublasSgemm(cublas_handle_,
                   CUBLAS_OP_T, CUBLAS_OP_N,
                   n_, num_queries, dim_,
                   &alpha,
                   d_data_.data().get(), dim_,
                   queries.data().get(), dim_,
                   &beta,
                   distances.data().get(), n_);

        // Добавление квадратов норм
        thrust::device_vector<float> d_query_norms(num_queries);
        cublasSnrm2(cublas_handle_, num_queries * dim_, queries.data().get(), 1, d_query_norms.data().get());

        thrust::device_vector<float> d_data_norms(n_);
        cublasSnrm2(cublas_handle_, n_ * dim_, d_data_.data().get(), 1, d_data_norms.data().get());

        auto query_norms_squared = thrust::make_transform_iterator(d_query_norms.begin(),
            [] __device__ (float x) { return x * x; });

        auto data_norms_squared = thrust::make_transform_iterator(d_data_norms.begin(),
            [] __device__ (float x) { return x * x; });

        thrust::transform(distances.begin(), distances.end(),
                         thrust::make_permutation_iterator(query_norms_squared,
                             thrust::make_transform_iterator(
                                 thrust::counting_iterator<int>(0),
                                 [] __device__ (int i) { return i / n_; })),
                         distances.begin(),
                         thrust::plus<float>());

        thrust::transform(distances.begin(), distances.end(),
                         thrust::make_permutation_iterator(data_norms_squared,
                             thrust::make_transform_iterator(
                                 thrust::counting_iterator<int>(0),
                                 [] __device__ (int i) { return i % n_; })),
                         distances.begin(),
                         thrust::plus<float>());

        // Извлечение квадратного корня
        thrust::transform(distances.begin(), distances.end(), distances.begin(),
            [] __device__ (float x) { return sqrt(x); });
    }

    // Сохранение эталонных данных
    void saveGroundTruth(const std::string& filename) {
        std::ofstream out(filename, std::ios::binary);
        if (!out) {
            throw std::runtime_error("Cannot open file for writing");
        }

        // Запись метаданных
        out.write(reinterpret_cast<const char*>(&n_), sizeof(int));
        out.write(reinterpret_cast<const char*>(&gt_k_), sizeof(int));
        out.write(reinterpret_cast<const char*>(&dim_), sizeof(int));

        // Копирование данных на хост
        thrust::host_vector<int> h_indices = d_gt_indices_;
        thrust::host_vector<float> h_distances = d_gt_distances_;

        // Запись индексов
        out.write(reinterpret_cast<const char*>(h_indices.data()), n_ * gt_k_ * sizeof(int));

        // Запись расстояний
        out.write(reinterpret_cast<const char*>(h_distances.data()), n_ * gt_k_ * sizeof(float));

        if (verbose_) {
            std::cout << "Ground truth saved to " << filename << std::endl;
        }
    }

    // Параметры
    int dim_;  // Размерность данных
    int k_;    // Количество соседей в графе
    int n_ = 0; // Количество точек в наборе данных
    int gt_k_ = 0; // Количество соседей в эталонных данных

    // Флаги
    bool verbose_;
    bool debug_;

    // Данные
    std::vector<float> data_; // Данные на хосте
    thrust::device_vector<float> d_data_; // Данные на устройстве

    // Граф
    thrust::device_vector<int> d_graph_indices_;
    thrust::device_vector<float> d_graph_distances_;

    // Эталонные данные
    thrust::device_vector<int> d_gt_indices_;
    thrust::device_vector<float> d_gt_distances_;

    // Обработчики CUDA
    cublasHandle_t cublas_handle_;
    cusparseHandle_t cusparse_handle_;
};
```

## Основные возможности реализации:

1. **Загрузка данных**:
   - Метод `loadData()` загружает данные на GPU для последующей обработки

2. **Построение графа KNN**:
   - `buildGraph()` вычисляет попарные расстояния и строит граф k-ближайших соседей
   - Использует cuBLAS для эффективного вычисления расстояний

3. **Уточнение графа**:
   - `refineGraph()` предоставляет возможность уточнения графа (можно реализовать конкретный алгоритм)

4. **Генерация эталонных данных**:
   - `generateGroundTruth()` создает точные эталонные данные для оценки качества графа

5. **Оценка графа**:
   - `evaluateGraph()` сравнивает построенный граф с эталонными данными

6. **Запросы KNN**:
   - `query()` выполняет поиск k-ближайших соседей для заданных точек запроса

7. **Сохранение/загрузка графа**:
   - `saveGraph()` и `loadGraph()` позволяют сохранять и загружать граф из файла

8. **Работа с подмножествами**:
   - `resetGraphForSubset()` позволяет работать с подмножеством данных

9. **Отладочные возможности**:
   - Флаги `verbose` и `debug` для управления уровнем детализации вывода

## Пример использования:

```cpp
int main() {
    try {
        // Создание обёртки
        KNNGraphWrapper knn(128, 10, true, false); // dim=128, k=10, verbose=true

        // Генерация случайных данных
        int n = 10000;
        std::vector<float> data(n * 128);
        for (auto& val : data) {
            val = static_cast<float>(rand()) / RAND_MAX;
        }

        // Загрузка данных
        knn.loadData(data, n);

        // Построение графа
        knn.buildGraph();

        // Уточнение графа (опционально)
        knn.refineGraph(2);

        // Генерация эталонных данных
        knn.generateGroundTruth(20, "ground_truth.bin");

        // Оценка графа
        knn.evaluateGraph();

        // Выполнение запроса
        std::vector<float> query(128);
        for (auto& val : query) {
            val = static_cast<float>(rand()) / RAND_MAX;
        }

        std::vector<int> indices;
        std::vector<float> distances;
        knn.query(query, 1, indices, distances);

        // Сохранение графа
        knn.saveGraph("knn_graph.bin");

        // Работа с подмножеством
        knn.resetGraphForSubset(5000);

    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
```

