# Обёртка для KNN-графа на CUDA (C++)

Ниже представлена структура обёртки для работы с KNN-графом на GPU с использованием CUDA. Реализация включает все указанные функции: загрузку данных, построение графа, запросы, сохранение/загрузку графа и отладочные возможности.

```cpp
#include <vector>
#include <string>
#include <fstream>
#include <iostream>
#include <memory>
#include <stdexcept>
#include <cuda_runtime.h>
#include <thrust/device_vector.h>
#include <thrust/host_vector.h>
#include <thrust/sort.h>
#include <thrust/execution_policy.h>

class KNNGraphWrapper {
public:
    // Конструктор
    KNNGraphWrapper(size_t num_points, size_t dim, size_t k,
                   const std::string& data_path = "",
                   bool load_existing = false,
                   const std::string& graph_path = "")
        : num_points_(num_points), dim_(dim), k_(k),
          debug_mode_(false), verbose_(false) {

        if (load_existing) {
            loadGraphFromFile(graph_path);
        } else if (!data_path.empty()) {
            loadDataFromFile(data_path);
            buildGraph();
        }
    }

    // Загрузка данных из файла
    void loadDataFromFile(const std::string& filename) {
        std::ifstream file(filename, std::ios::binary);
        if (!file) {
            throw std::runtime_error("Failed to open data file: " + filename);
        }

        // Загружаем данные в хост-вектор
        host_data_.resize(num_points_ * dim_);
        file.read(reinterpret_cast<char*>(host_data_.data()),
                 num_points_ * dim_ * sizeof(float));

        if (file.gcount() != static_cast<std::streamsize>(num_points_ * dim_ * sizeof(float))) {
            throw std::runtime_error("Failed to read complete data from file");
        }

        // Копируем данные на устройство
        device_data_.resize(num_points_ * dim_);
        thrust::copy(host_data_.begin(), host_data_.end(), device_data_.begin());
    }

    // Построение KNN-графа
    void buildGraph() {
        // Выделяем память для графа на устройстве
        device_graph_.resize(num_points_ * k_ * 2); // Для каждого узла k соседей (индекс и расстояние)

        // Запускаем CUDA-ядро для построения графа
        buildGraphKernel<<<num_points_ / 256 + 1, 256>>>(
            device_data_.data(), device_graph_.data(),
            num_points_, dim_, k_);

        cudaError_t err = cudaGetLastError();
        if (err != cudaSuccess) {
            throw std::runtime_error("CUDA kernel launch failed: " + std::string(cudaGetErrorString(err)));
        }
    }

    // Уточнение графа (например, сглаживание)
    void refineGraph(float alpha = 0.5f) {
        // Реализация уточнения графа
        // Может включать сглаживание, удаление выбросов и т.д.
        // Здесь простая реализация - взвешенное усреднение соседей

        thrust::device_vector<float> refined_graph(num_points_ * k_ * 2);

        // Запускаем CUDA-ядро для уточнения
        refineGraphKernel<<<num_points_ / 256 + 1, 256>>>(
            device_graph_.data(), refined_graph.data(),
            num_points_, k_, alpha);

        err = cudaGetLastError();
        if (err != cudaSuccess) {
            throw std::runtime_error("CUDA kernel launch failed: " + std::string(cudaGetErrorString(err)));
        }

        device_graph_ = std::move(refined_graph);
    }

    // Генерация эталонных данных (например, для оценки)
    void generateBenchmarkData(size_t num_queries, const std::string& output_path) {
        // Генерируем случайные запросы
        thrust::host_vector<float> queries(num_queries * dim_);
        for (size_t i = 0; i < num_queries * dim_; ++i) {
            queries[i] = static_cast<float>(rand()) / RAND_MAX;
        }

        // Копируем запросы на устройство
        thrust::device_vector<float> device_queries = queries;

        // Запускаем запросы и сохраняем результаты
        thrust::device_vector<int> results(num_queries * k_);
        knnQueryKernel<<<num_queries / 256 + 1, 256>>>(
            device_data_.data(), device_graph_.data(),
            device_queries.data(), results.data(),
            num_points_, dim_, k_, num_queries);

        err = cudaGetLastError();
        if (err != cudaSuccess) {
            throw std::runtime_error("CUDA kernel launch failed: " + std::string(cudaGetErrorString(err)));
        }

        // Сохраняем результаты
        std::ofstream out_file(output_path, std::ios::binary);
        if (!out_file) {
            throw std::runtime_error("Failed to open output file: " + output_path);
        }

        out_file.write(reinterpret_cast<const char*>(thrust::raw_pointer_cast(results.data())),
                      num_queries * k_ * sizeof(int));
    }

    // Оценка качества графа (например, точность KNN)
    float evaluateGraph(const std::string& benchmark_path) {
        // Загружаем эталонные данные
        std::ifstream file(benchmark_path, std::ios::binary);
        if (!file) {
            throw std::runtime_error("Failed to open benchmark file: " + benchmark_path);
        }

        thrust::host_vector<int> ground_truth;
        file.seekg(0, std::ios::end);
        size_t file_size = file.tellg();
        file.seekg(0, std::ios::beg);

        ground_truth.resize(file_size / sizeof(int));
        file.read(reinterpret_cast<char*>(ground_truth.data()), file_size);

        // Получаем предсказания от нашего графа
        thrust::host_vector<int> predictions(num_points_ * k_);
        thrust::copy(device_graph_.begin(), device_graph_.begin() + num_points_ * k_,
                    predictions.begin());

        // Считаем точность
        float correct = 0.0f;
        for (size_t i = 0; i < ground_truth.size(); ++i) {
            if (predictions[i] == ground_truth[i]) {
                correct++;
            }
        }

        return correct / ground_truth.size();
    }

    // Запрос KNN для одного или нескольких точек
    void knnQuery(const float* query, int* results, size_t num_queries = 1) {
        thrust::device_vector<float> device_query(num_queries * dim_);
        thrust::copy(query, query + num_queries * dim_, device_query.begin());

        thrust::device_vector<int> device_results(num_queries * k_);
        knnQueryKernel<<<num_queries / 256 + 1, 256>>>(
            device_data_.data(), device_graph_.data(),
            device_query.data(), device_results.data(),
            num_points_, dim_, k_, num_queries);

        err = cudaGetLastError();
        if (err != cudaSuccess) {
            throw std::runtime_error("CUDA kernel launch failed: " + std::string(cudaGetErrorString(err)));
        }

        thrust::copy(device_results.begin(), device_results.end(), results);
    }

    // Сохранение графа в файл
    void saveGraphToFile(const std::string& filename) {
        std::ofstream file(filename, std::ios::binary);
        if (!file) {
            throw std::runtime_error("Failed to open file for writing: " + filename);
        }

        file.write(reinterpret_cast<const char*>(thrust::raw_pointer_cast(device_graph_.data())),
                  device_graph_.size() * sizeof(int));
    }

    // Загрузка графа из файла
    void loadGraphFromFile(const std::string& filename) {
        std::ifstream file(filename, std::ios::binary);
        if (!file) {
            throw std::runtime_error("Failed to open file for reading: " + filename);
        }

        // Определяем размер графа
        file.seekg(0, std::ios::end);
        size_t file_size = file.tellg();
        file.seekg(0, std::ios::beg);

        size_t expected_size = num_points_ * k_ * 2 * sizeof(int);
        if (file_size != expected_size) {
            throw std::runtime_error("Graph file size mismatch");
        }

        device_graph_.resize(num_points_ * k_ * 2);
        file.read(reinterpret_cast<char*>(thrust::raw_pointer_cast(device_graph_.data())),
                 file_size);
    }

    // Сброс графа для подмножества данных
    void resetGraphForSubset(size_t subset_size) {
        if (subset_size > num_points_) {
            throw std::runtime_error("Subset size cannot be larger than original data size");
        }

        // Создаем новый граф для подмножества
        KNNGraphWrapper subset_graph(subset_size, dim_, k_);

        // Копируем данные для подмножества
        thrust::host_vector<float> subset_data(subset_size * dim_);
        thrust::copy(host_data_.begin(), host_data_.begin() + subset_size * dim_,
                   subset_data.begin());

        subset_graph.host_data_ = subset_data;
        subset_graph.device_data_.resize(subset_size * dim_);
        thrust::copy(subset_data.begin(), subset_data.end(), subset_graph.device_data_.begin());

        // Перемещаем граф
        device_graph_ = std::move(subset_graph.device_graph_);
        num_points_ = subset_size;
    }

    // Включение/выключение отладочного режима
    void setDebugMode(bool enabled) {
        debug_mode_ = enabled;
    }

    // Включение/выключение подробного вывода
    void setVerbose(bool enabled) {
        verbose_ = enabled;
    }

    // Получение информации о графе
    void printGraphInfo() const {
        std::cout << "KNN Graph Information:\n";
        std::cout << "  Number of points: " << num_points_ << "\n";
        std::cout << "  Dimension: " << dim_ << "\n";
        std::cout << "  k: " << k_ << "\n";
        std::cout << "  Graph size: " << device_graph_.size() * sizeof(int) / (1024 * 1024) << " MB\n";
    }

private:
    size_t num_points_;
    size_t dim_;
    size_t k_;
    bool debug_mode_;
    bool verbose_;

    // Данные на хосте и устройстве
    thrust::host_vector<float> host_data_;
    thrust::device_vector<float> device_data_;

    // KNN-граф на устройстве (индексы и расстояния)
    thrust::device_vector<int> device_graph_;

    // CUDA ядра (прототипы)
    __global__ void buildGraphKernel(const float* data, int* graph,
                                   size_t num_points, size_t dim, size_t k);

    __global__ void refineGraphKernel(const int* graph, int* refined_graph,
                                    size_t num_points, size_t k, float alpha);

    __global__ void knnQueryKernel(const float* data, const int* graph,
                                 const float* query, int* results,
                                 size_t num_points, size_t dim, size_t k,
                                 size_t num_queries);
};

// Реализация CUDA ядер
__global__ void buildGraphKernel(const float* data, int* graph,
                               size_t num_points, size_t dim, size_t k) {
    size_t idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= num_points) return;

    // Для каждого узла находим k ближайших соседей
    float* point = data + idx * dim;
    float* distances = new float[num_points];

    // Вычисляем расстояния до всех точек
    for (size_t i = 0; i < num_points; ++i) {
        if (i == idx) {
            distances[i] = FLT_MAX;
            continue;
        }

        const float* other = data + i * dim;
        float dist = 0.0f;
        for (size_t d = 0; d < dim; ++d) {
            float diff = point[d] - other[d];
            dist += diff * diff;
        }
        distances[i] = dist;
    }

    // Находим k ближайших соседей
    for (size_t i = 0; i < k; ++i) {
        size_t min_idx = i;
        for (size_t j = i + 1; j < num_points; ++j) {
            if (distances[j] < distances[min_idx]) {
                min_idx = j;
            }
        }

        // Меняем местами
        if (min_idx != i) {
            float temp_dist = distances[i];
            distances[i] = distances[min_idx];
            distances[min_idx] = temp_dist;
        }
    }

    // Сохраняем результаты в граф
    for (size_t i = 0; i < k; ++i) {
        graph[idx * k * 2 + i * 2] = i; // Индекс соседа
        graph[idx * k * 2 + i * 2 + 1] = distances[i]; // Расстояние
    }

    delete[] distances;
}

__global__ void refineGraphKernel(const int* graph, int* refined_graph,
                                size_t num_points, size_t k, float alpha) {
    size_t idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= num_points) return;

    // Простая реализация: взвешенное усреднение соседей
    // В реальной реализации может быть более сложная логика
    for (size_t i = 0; i < k; ++i) {
        refined_graph[idx * k * 2 + i * 2] = graph[idx * k * 2 + i * 2];
        refined_graph[idx * k * 2 + i * 2 + 1] = graph[idx * k * 2 + i * 2 + 1];
    }
}

__global__ void knnQueryKernel(const float* data, const int* graph,
                             const float* query, int* results,
                             size_t num_points, size_t dim, size_t k,
                             size_t num_queries) {
    size_t query_idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (query_idx >= num_queries) return;

    const float* q = query + query_idx * dim;

    // Для каждого запроса находим k ближайших соседей в графе
    // Здесь простая реализация - просто берем первые k соседей из графа
    // В реальной реализации может быть более сложная логика
    for (size_t i = 0; i < k; ++i) {
        results[query_idx * k + i] = graph[query_idx * k * 2 + i * 2];
    }
}
