package transport

import (
	"crypto/tls"
	"crypto/x509"
	"net/http"
	"time"

	"github.com/elastic/go-elasticsearch/v8"
)

// TransportBuilder - структура для настройки HTTP-транспорта
type TransportBuilder struct {
	// Настройки прокси
	proxyURL      string
	proxyUsername string
	proxyPassword string
	noProxy       bool

	// Учетные данные для аутентификации в Elasticsearch
	username string
	password string

	// Настройки TLS
	customCA []byte
	insecure bool

	// Дополнительные заголовки
	headers map[string]string

	// Тайм-ауты
	timeout time.Duration
}

// Transport - интерфейс для HTTP-транспорта
type Transport interface {
	Do(ctx context.Context, req *http.Request) (*http.Response, error)
}

// Build создает HTTP-транспорт с заданными настройками
func (b *TransportBuilder) Build() (*elasticsearch.Client, error) {
	// Настройка транспорта
	transport := &http.Transport{
		Proxy: b.proxyFunc(),
		DialContext: func(ctx context.Context, network, addr string) (net.Conn, error) {
			d := net.Dialer{}
			return d.DialContext(ctx, network, addr)
		},
		TLSClientConfig: b.tlsConfig(),
	}

	// Настройка клиента Elasticsearch
	esConfig := elasticsearch.Config{
		Addresses: []string{"http://localhost:9200"}, // по умолчанию
		Username: b.username,
		Password: b.password,
		Transport: transport,
		Timeout:   b.timeout,
	}

	// Добавление заголовков
	if b.headers != nil {
		esConfig.Headers = b.headers
	}

	// Создание клиента
	client, err := elasticsearch.NewClient(esConfig)
	if err != nil {
		return nil, err
	}

	return client, nil
}

// proxyFunc возвращает функцию проксирования
func (b *TransportBuilder) proxyFunc() func(*http.Request) (*url.URL, error) {
	if b.noProxy {
		return http.ProxyFromEnvironment // или nil, если нужно отключить все прокси
	}

	if b.proxyURL == "" {
		return http.ProxyFromEnvironment // использовать системные настройки
	}

	proxyURL, err := url.Parse(b.proxyURL)
	if err != nil {
		return http.ProxyFromEnvironment // fallback
	}

	return func(req *http.Request) (*url.URL, error) {
		if b.proxyUsername != "" && b.proxyPassword != "" {
			proxyURL.User = url.UserPassword(b.proxyUsername, b.proxyPassword)
		}
		return proxyURL, nil
	}
}

// tlsConfig возвращает конфигурацию TLS
func (b *TransportBuilder) tlsConfig() *tls.Config {
	config := &tls.Config{}

	if b.insecure {
		config.InsecureSkipVerify = true
		return config
	}

	if b.customCA != nil {
		caCertPool := x509.NewCertPool()
		caCertPool.AppendCertsFromPEM(b.customCA)
		config.RootCAs = caCertPool
	}

	return config
}
```

