# Реализация Игры Жизни с использованием CUDA и MAPS-Multi

Ниже представлена реализация Игры Жизни с использованием CUDA для выполнения на нескольких GPU через фреймворк MAPS-Multi. Решение включает оптимизированное CUDA-ядро, вспомогательные функции и механизм регрессионного тестирования.

## Основные компоненты реализации

### 1. CUDA-ядро для Игры Жизни

```cpp
#include <cuda_runtime.h>
#include <device_launch_parameters.h>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <vector>
#include <algorithm>

// Определение границ сетки для избежания вылетов за пределы
#define BLOCK_SIZE 16
#define TILE_SIZE (BLOCK_SIZE + 2)

// Ядро для вычисления количества живых соседей
__global__ void countNeighborsKernel(const unsigned char* input,
                                    unsigned char* output,
                                    int width, int height) {
    // Определяем индексы в блоке
    int tx = threadIdx.x;
    int ty = threadIdx.y;
    int bx = blockIdx.x;
    int by = blockIdx.y;

    // Определяем индексы в глобальной сетке
    int row = by * blockDim.y + ty;
    int col = bx * blockDim.x + tx;

    // Проверяем границы
    if (row >= height || col >= width) return;

    // Создаем локальную копию блока с отступом
    extern __shared__ unsigned char sharedTile[];
    unsigned char* tile = sharedTile + TILE_SIZE * (ty + 1) + (tx + 1);

    // Загружаем данные в shared memory
    int inputRow = row * width;
    int inputCol = col;

    // Загрузка текущего элемента
    tile[ty][tx] = input[inputRow + inputCol];

    // Загрузка элементов из соседних блоков
    if (tx == 0) {
        tile[ty][tx - 1] = (col > 0) ? input[inputRow + inputCol - 1] : 0;
    }
    if (tx == blockDim.x - 1) {
        tile[ty][tx + 1] = (col < width - 1) ? input[inputRow + inputCol + 1] : 0;
    }
    if (ty == 0) {
        tile[ty - 1][tx] = (row > 0) ? input[(inputRow - width) + inputCol] : 0;
    }
    if (ty == blockDim.y - 1) {
        tile[ty + 1][tx] = (row < height - 1) ? input[(inputRow + width) + inputCol] : 0;
    }

    // Загрузка угловых элементов
    if (tx == 0 && ty == 0) {
        tile[ty - 1][tx - 1] = (row > 0 && col > 0) ? input[(inputRow - width) + inputCol - 1] : 0;
        tile[ty - 1][tx + 1] = (row > 0 && col < width - 1) ? input[(inputRow - width) + inputCol + 1] : 0;
    }
    if (tx == 0 && ty == blockDim.y - 1) {
        tile[ty + 1][tx - 1] = (row < height - 1 && col > 0) ? input[(inputRow + width) + inputCol - 1] : 0;
        tile[ty + 1][tx + 1] = (row < height - 1 && col < width - 1) ? input[(inputRow + width) + inputCol + 1] : 0;
    }
    if (tx == blockDim.x - 1 && ty == 0) {
        tile[ty - 1][tx - 1] = (row > 0 && col > 0) ? input[(inputRow - width) + inputCol - 1] : 0;
        tile[ty - 1][tx + 1] = (row > 0 && col < width - 1) ? input[(inputRow - width) + inputCol + 1] : 0;
    }
    if (tx == blockDim.x - 1 && ty == blockDim.y - 1) {
        tile[ty + 1][tx - 1] = (row < height - 1 && col > 0) ? input[(inputRow + width) + inputCol - 1] : 0;
        tile[ty + 1][tx + 1] = (row < height - 1 && col < width - 1) ? input[(inputRow + width) + inputCol + 1] : 0;
    }

    // Синхронизация потоков
    __syncthreads();

    // Подсчет живых соседей
    int neighbors = 0;
    for (int dy = -1; dy <= 1; dy++) {
        for (int dx = -1; dx <= 1; dx++) {
            if (dx == 0 && dy == 0) continue;
            neighbors += tile[ty + dy][tx + dx];
        }
    }

    // Применяем правила Игры Жизни
    if (input[inputRow + inputCol] == 1) {
        // Клетка жива
        output[inputRow + inputCol] = (neighbors == 2 || neighbors == 3) ? 1 : 0;
    } else {
        // Клетка мертва
        output[inputRow + inputCol] = (neighbors == 3) ? 1 : 0;
    }
}

// Ядро для инициализации случайной сетки
__global__ void initRandomKernel(unsigned char* grid, int width, int height, unsigned int seed) {
    int row = blockIdx.y * blockDim.y + threadIdx.y;
    int col = blockIdx.x * blockDim.x + threadIdx.x;

    if (row < height && col < width) {
        // Простой алгоритм псевдослучайного числа на основе seed
        unsigned int x = seed + row * 1664525 + col * 1013904223;
        grid[row * width + col] = (x % 256) > 128 ? 1 : 0;
    }
}
