### 2. Вспомогательные функции для работы с MAPS-Multi

```cpp
#include <maps/maps.h>
#include <maps/device.h>
#include <maps/stream.h>
#include <vector>
#include <fstream>
#include <sstream>
#include <iomanip>

// Структура для хранения состояния сетки
struct GridState {
    std::vector<unsigned char> data;
    int width;
    int height;
    int generation;
};

// Функция для выполнения одной итерации Игры Жизни на GPU
void runGameOfLifeStep(MAPS::Device& device,
                       const GridState& input,
                       GridState& output,
                       unsigned int seed = 0) {
    // Создаем буферы на устройстве
    MAPS::Buffer inputBuffer(device, input.data.size() * sizeof(unsigned char));
    MAPS::Buffer outputBuffer(device, output.data.size() * sizeof(unsigned char));

    // Копируем данные на устройство
    device.memcpy(inputBuffer, input.data.data(), input.data.size() * sizeof(unsigned char));

    // Определяем размер блока и сетки
    dim3 blockSize(BLOCK_SIZE, BLOCK_SIZE);
    dim3 gridSize((input.width + blockSize.x - 1) / blockSize.x,
                  (input.height + blockSize.y - 1) / blockSize.y);

    // Выполняем ядро
    device.launch(countNeighborsKernel,
                 gridSize, blockSize,
                 TILE_SIZE * TILE_SIZE * sizeof(unsigned char),
                 inputBuffer, outputBuffer,
                 input.width, input.height);

    // Копируем результат обратно на хост
    device.memcpy(output.data.data(), outputBuffer, output.data.size() * sizeof(unsigned char));

    // Увеличиваем счетчик поколений
    output.generation = input.generation + 1;
}

// Функция для инициализации случайной сетки
void initRandomGrid(MAPS::Device& device,
                    GridState& grid,
                    unsigned int seed = 0) {
    // Создаем буфер на устройстве
    MAPS::Buffer buffer(device, grid.data.size() * sizeof(unsigned char));

    // Определяем размер блока и сетки
    dim3 blockSize(16, 16);
    dim3 gridSize((grid.width + blockSize.x - 1) / blockSize.x,
                  (grid.height + blockSize.y - 1) / blockSize.y);

    // Выполняем ядро инициализации
    device.launch(initRandomKernel,
                 gridSize, blockSize,
                 0,
                 buffer, grid.width, grid.height, seed);

    // Копируем результат обратно на хост
    device.memcpy(grid.data.data(), buffer, grid.data.size() * sizeof(unsigned char));
}

// Функция для сохранения сетки в файл
void saveGridToFile(const GridState& grid, const std::string& filename) {
    std::ofstream outFile(filename, std::ios::binary);
    if (!outFile) {
        throw std::runtime_error("Failed to open file for writing");
    }

    outFile.write(reinterpret_cast<const char*>(grid.data.data()),
                 grid.data.size() * sizeof(unsigned char));
    outFile.close();
}

// Функция для загрузки сетки из файла
void loadGridFromFile(GridState& grid, const std::string& filename) {
    std::ifstream inFile(filename, std::ios::binary);
    if (!inFile) {
        throw std::runtime_error("Failed to open file for reading");
    }

    inFile.read(reinterpret_cast<char*>(grid.data.data()),
               grid.data.size() * sizeof(unsigned char));
    inFile.close();
}

// Функция для сравнения двух сеток
bool compareGrids(const GridState& grid1, const GridState& grid2) {
    if (grid1.width != grid2.width || grid1.height != grid2.height) {
        return false;
    }

    for (size_t i = 0; i < grid1.data.size(); ++i) {
        if (grid1.data[i] != grid2.data[i]) {
            return false;
        }
    }
    return true;
}

// Функция для выполнения регрессионного теста
bool runRegressionTest(MAPS::Device& device,
                      const std::string& inputFile,
                      const std::string& expectedOutputFile,
                      int steps = 10) {
    // Загружаем начальное состояние
    GridState input;
    input.width = 100;
    input.height = 100;
    input.data.resize(input.width * input.height);
    input.generation = 0;

    loadGridFromFile(input, inputFile);

    // Создаем выходное состояние
    GridState output = input;

    // Выполняем несколько итераций
    for (int i = 0; i < steps; ++i) {
        runGameOfLifeStep(device, input, output);
        input = output;
    }

    // Загружаем ожидаемый результат
    GridState expected;
    expected.width = 100;
    expected.height = 100;
    expected.data.resize(expected.width * expected.height);
    expected.generation = steps;

    loadGridFromFile(expected, expectedOutputFile);

    // Сравниваем результаты
    bool result = compareGrids(output, expected);

    if (!result) {
        // Сохраняем фактический результат для анализа
        saveGridToFile(output, "actual_output.bin");
    }

    return result;
}
