        _isLoading.value = true
        val postList = repository.getPosts() // Предполагается, что возвращается List<Post>
        _posts.value = postList
        _isLoading.value = false
    }
}
```

# Обёртка для KNN-графа на CUDA (C++)

Вот реализация обёртки для работы с KNN-графом на GPU с использованием CUDA. Этот код включает все запрашиваемые функции: загрузку данных, построение графа, запросы, сохранение/загрузку графа и отладочные возможности.

```cpp
#include <iostream>
#include <vector>
#include <fstream>
#include <string>
#include <algorithm>
#include <cuda_runtime.h>
#include <thrust/device_vector.h>
#include <thrust/host_vector.h>
#include <thrust/sort.h>
#include <thrust/execution_policy.h>
#include <thrust/iterator/counting_iterator.h>
#include <thrust/iterator/zip_iterator.h>
#include <thrust/tuple.h>
#include <thrust/functional.h>
#include <chrono>
#include <random>
#include <cmath>

class KNNGraphWrapper {
private:
    // Параметры графа
    int num_points;
    int knn_degree;
    float epsilon; // для приближённого поиска
    bool debug_mode;

    // Хранилище данных
    thrust::device_vector<float> d_points; // [num_points * dim]
    thrust::device_vector<int> d_graph;    // [num_points * knn_degree]
    thrust::device_vector<float> d_distances; // [num_points * knn_degree]

    // Параметры для запросов
    int query_batch_size;
    bool verbose_queries;

    // Вспомогательные функции
    __device__ float euclidean_distance(const float* a, const float* b, int dim) {
        float dist = 0.0f;
        for (int i = 0; i < dim; ++i) {
            float diff = a[i] - b[i];
            dist += diff * diff;
        }
        return sqrtf(dist);
    }

    // CUDA ядро для построения KNN-графа
    template<typename T>
    __global__ void build_knn_kernel(const T* points, int num_points, int dim,
                                    int knn_degree, T* graph, T* distances) {
        int idx = blockIdx.x * blockDim.x + threadIdx.x;
        if (idx >= num_points) return;

        // Для каждого узла находим K ближайших соседей
        T* node = points + idx * dim;
        T* neighbors = graph + idx * knn_degree;
        T* neighbor_dists = distances + idx * knn_degree;

        // Инициализируем расстояния большим значением
        for (int i = 0; i < knn_degree; ++i) {
            neighbor_dists[i] = FLT_MAX;
            neighbors[i] = -1;
        }

        // Сравниваем с другими узлами
        for (int i = 0; i < num_points; ++i) {
            if (i == idx) continue;

            T* other = points + i * dim;
            T dist = euclidean_distance(node, other, dim);

            // Обновляем K ближайших соседей
            for (int j = 0; j < knn_degree; ++j) {
                if (dist < neighbor_dists[j]) {
                    // Сдвигаем элементы
                    for (int k = knn_degree - 1; k > j; --k) {
                        neighbor_dists[k] = neighbor_dists[k-1];
                        neighbors[k] = neighbors[k-1];
                    }
                    neighbor_dists[j] = dist;
                    neighbors[j] = i;
                    break;
                }
            }
        }
    }

    // CUDA ядро для запроса KNN
    template<typename T>
    __global__ void query_knn_kernel(const T* points, const T* graph, const T* distances,
                                    int num_points, int dim, int knn_degree,
                                    const T* query, int query_dim,
                                    int* results, T* result_dists,
                                    int query_idx, int max_results) {
        int idx = blockIdx.x * blockDim.x + threadIdx.x;
        if (idx >= max_results) return;

        // Проверяем, что размерность совпадает
        if (dim != query_dim) {
            results[idx] = -1;
            result_dists[idx] = FLT_MAX;
            return;
        }

        // Находим ближайших соседей в графе
        T* query_point = query;
        T min_dist = FLT_MAX;
        int best_neighbor = -1;

        // Проверяем все соседи в графе
        for (int i = 0; i < knn_degree; ++i) {
            int neighbor_idx = graph[query_idx * knn_degree + i];
            T dist = distances[query_idx * knn_degree + i];

            if (dist < min_dist) {
                min_dist = dist;
                best_neighbor = neighbor_idx;
            }
        }

        // Если нашли соседа, проверяем его соседей
        if (best_neighbor != -1) {
            T* neighbor_point = points + best_neighbor * dim;
            T dist = euclidean_distance(query_point, neighbor_point, dim);

            if (dist < min_dist) {
                min_dist = dist;
                best_neighbor = -1; // Значит, лучший сосед не в графе
            }
        }

        // Если не нашли в графе, делаем полный поиск
        if (best_neighbor == -1) {
            for (int i = 0; i < num_points; ++i) {
                T* other = points + i * dim;
                T dist = euclidean_distance(query_point, other, dim);

                if (dist < min_dist) {
                    min_dist = dist;
                    best_neighbor = i;
                }
            }
        }

        results[idx] = best_neighbor;
        result_dists[idx] = min_dist;
    }

    // CUDA ядро для уточнения графа
    template<typename T>
    __global__ void refine_graph_kernel(const T* points, int num_points, int dim,
                                       int knn_degree, T* graph, T* distances) {
        int idx = blockIdx.x * blockDim.x + threadIdx.x;
        if (idx >= num_points) return;

        T* node = points + idx * dim;
        T* neighbors = graph + idx * knn_degree;
        T* neighbor_dists = distances + idx * knn_degree;

        // Для каждого соседа проверяем его соседей
        for (int i = 0; i < knn_degree; ++i) {
            int neighbor_idx = neighbors[i];
            T* neighbor = points + neighbor_idx * dim;

            // Проверяем соседей соседа
            for (int j = 0; j < knn_degree; ++j) {
                int candidate_idx = graph[neighbor_idx * knn_degree + j];
                T candidate_dist = distances[neighbor_idx * knn_degree + j];

                // Если кандидат ближе к нашему узлу, чем текущий сосед
                if (candidate_idx != idx && candidate_idx != -1) {
                    T dist = euclidean_distance(node, points + candidate_idx * dim, dim);
                    if (dist < neighbor_dists[knn_degree - 1]) {
                        // Вставляем кандидата в список соседей
                        for (int k = knn_degree - 1; k > 0; --k) {
                            if (dist < neighbor_dists[k-1]) {
                                neighbor_dists[k] = neighbor_dists[k-1];
                                neighbors[k] = neighbors[k-1];
                            } else {
                                break;
                            }
                        }
                        neighbor_dists[0] = dist;
                        neighbors[0] = candidate_idx;
                    }
                }
            }
        }
    }

public:
    // Конструктор
    KNNGraphWrapper(int num_points, int dim, int knn_degree,
                   float epsilon = 0.0f, bool debug = false,
                   int query_batch = 100, bool verbose = false)
        : num_points(num_points), knn_degree(knn_degree),
          epsilon(epsilon), debug_mode(debug),
          query_batch_size(query_batch), verbose_queries(verbose) {

        // Выделяем память на GPU
        d_points.resize(num_points * dim);
        d_graph.resize(num_points * knn_degree);
        d_distances.resize(num_points * knn_degree);
    }

    // Загрузка данных из файла
    bool load_data(const std::string& filename, int dim) {
        std::ifstream file(filename, std::ios::binary);
        if (!file) {
            std::cerr << "Error opening file: " << filename << std::endl;
            return false;
        }

        // Проверяем размерность
        if (dim != d_points.size() / num_points) {
            std::cerr << "Dimension mismatch. Expected: " << d_points.size() / num_points
                      << ", Got: " << dim << std::endl;
            return false;
        }

        // Загружаем данные
        file.read(reinterpret_cast<char*>(thrust::raw_pointer_cast(d_points.data())),
                 d_points.size() * sizeof(float));

        if (file.gcount() != d_points.size() * sizeof(float)) {
            std::cerr << "Error reading data from file" << std::endl;
            return false;
        }

        return true;
    }

    // Сохранение данных в файл
    bool save_data(const std::string& filename) {
        std::ofstream file(filename, std::ios::binary);
        if (!file) {
            std::cerr << "Error opening file for writing: " << filename << std::endl;
            return false;
        }

        file.write(reinterpret_cast<const char*>(thrust::raw_pointer_cast(d_points.data())),
                 d_points.size() * sizeof(float));

        if (file.fail()) {
            std::cerr << "Error writing data to file" << std::endl;
            return false;
        }

        return true;
    }

    // Построение KNN-графа
    void build_knn_graph(int dim) {
        if (d_points.size() != num_points * dim) {
            std::cerr << "Dimension mismatch. Expected: " << dim << std::endl;
            return;
        }

        // Запускаем CUDA ядро
        dim3 blocks((num_points + 255) / 256);
        dim3 threads(256);

        build_knn_kernel<<<blocks, threads>>>(thrust::raw_pointer_cast(d_points.data()),
                                             num_points, dim,
                                             knn_degree,
                                             thrust::raw_pointer_cast(d_graph.data()),
                                             thrust::raw_pointer_cast(d_distances.data()));

        cudaError_t err = cudaGetLastError();
        if (err != cudaSuccess) {
            std::cerr << "CUDA error in build_knn_kernel: " << cudaGetErrorString(err) << std::endl;
        }

        if (debug_mode) {
            std::cout << "KNN graph built successfully" << std::endl;
        }
    }

    // Уточнение графа
    void refine_graph() {
        dim3 blocks((num_points + 255) / 256);
        dim3 threads(256);

        refine_graph_kernel<<<blocks, threads>>>(thrust::raw_pointer_cast(d_points.data()),
                                               num_points, d_points.size() / num_points,
                                               knn_degree,
                                               thrust::raw_pointer_cast(d_graph.data()),
                                               thrust::raw_pointer_cast(d_distances.data()));

        cudaError_t err = cudaGetLastError();
        if (err != cudaSuccess) {
            std::cerr << "CUDA error in refine_graph_kernel: " << cudaGetErrorString(err) << std::endl;
        }

        if (debug_mode) {
            std::cout << "Graph refined successfully" << std::endl;
        }
    }

    // Сохранение графа в файл
    bool save_graph(const std::string& filename) {
        std::ofstream file(filename, std::ios::binary);
        if (!file) {
            std::cerr << "Error opening file for writing graph: " << filename << std::endl;
            return false;
        }

        // Сохраняем граф и расстояния
        file.write(reinterpret_cast<const char*>(thrust::raw_pointer_cast(d_graph.data())),
                 d_graph.size() * sizeof(int));
        file.write(reinterpret_cast<const char*>(thrust::raw_pointer_cast(d_distances.data())),
                 d_distances.size() * sizeof(float));

        if (file.fail()) {
            std::cerr << "Error writing graph to file" << std::endl;
            return false;
        }

        return true;
    }

    // Загрузка графа из файла
    bool load_graph(const std::string& filename) {
        std::ifstream file(filename, std::ios::binary);
        if (!file) {
            std::cerr << "Error opening file for reading graph: " << filename << std::endl;
            return false;
        }

        // Загружаем граф
        file.read(reinterpret_cast<char*>(thrust::raw_pointer_cast(d_graph.data())),
                 d_graph.size() * sizeof(int));
        file.read(reinterpret_cast<char*>(thrust::raw_pointer_cast(d_distances.data())),
                 d_distances.size() * sizeof(float));

        if (file.gcount() != (d_graph.size() + d_distances.size()) * sizeof(float)) {
            std::cerr << "Error reading graph from file" << std::endl;
            return false;
        }

        return true;
    }

    // Запрос KNN для одного запроса
    std::pair<int, float> query_knn(const float* query, int dim, int max_results = 1) {
        thrust::device_vector<int> d_results(max_results);
        thrust::device_vector<float> d_result_dists(max_results);

        // Запускаем CUDA ядро
        dim3 blocks((max_results + 255) / 256);
        dim3 threads(256);

        // Копируем запрос на GPU
        thrust::device_vector<float> d_query(query, query + dim);

        query_knn_kernel<<<blocks, threads>>>(thrust::raw_pointer_cast(d_points.data()),
                                            thrust::raw_pointer_cast(d_graph.data()),
                                            thrust::raw_pointer_cast(d_distances.data()),
                                            num_points, d_points.size() / num_points,
                                            knn_degree,
                                            thrust::raw_pointer_cast(d_query.data()),
                                            dim,
                                            thrust::raw_pointer_cast(d_results.data()),
                                            thrust::raw_pointer_cast(d_result_dists.data()),
                                            0, // query_idx (в данном случае не используется)
                                            max_results);

        cudaError_t err = cudaGetLastError();
        if (err != cudaSuccess) {
            std::cerr << "CUDA error in query_knn_kernel: " << cudaGetErrorString(err) << std::endl;
        }

        // Копируем результаты обратно
        thrust::host_vector<int> h_results(d_results);
        thrust::host_vector<float> h_result_dists(d_result_dists);

        if (verbose_queries) {
            std::cout << "Query results (" << max_results << "):" << std::endl;
            for (int i = 0; i < max_results; ++i) {
                std::cout << "  " << i+1 << ": neighbor=" << h_results[i]
                          << ", distance=" << h_result_dists[i] << std::endl;
            }
        }

        return {h_results[0], h_result_dists[0]};
    }

    // Запрос KNN для пакета запросов
    std::vector<std::pair<int, float>> query_knn_batch(const std::vector<float*>& queries,
                                                     int dim, int max_results = 1) {
        std::vector<std::pair<int, float>> results;

        for (const auto& query : queries) {
            auto result = query_knn(query, dim, max_results);
            results.emplace_back(result);
        }

        return results;
    }

    // Генерация эталонных данных
    void generate_ground_truth(const std::string& filename) {
        thrust::host_vector<float> h_points = d_points;
        thrust::host_vector<int> h_graph = d_graph;
        thrust::host_vector<float> h_distances = d_distances;

        std::ofstream file(filename);
        if (!file) {
            std::cerr << "Error opening file for ground truth: " << filename << std::endl;
            return;
        }

        int dim = d_points.size() / num_points;

        for (int i = 0; i < num_points; ++i) {
            // Находим реальных K ближайших соседей
            std::vector<std::pair<float, int>> true_neighbors;

            for (int j = 0; j < num_points; ++j) {
                if (i == j) continue;

                float dist = 0.0f;
                for (int k = 0; k < dim; ++k) {
                    float diff = h_points[i * dim + k] - h_points[j * dim + k];
                    dist += diff * diff;
                }
                dist = sqrtf(dist);

                true_neighbors.emplace_back(dist, j);
            }

            // Сортируем по расстоянию
            std::sort(true_neighbors.begin(), true_neighbors.end());

            // Записываем в файл
            file << "Point " << i << ":\n";
            for (int k = 0; k < knn_degree; ++k) {
                if (k < true_neighbors.size()) {
                    file << "  " << k+1 << ": neighbor=" << true_neighbors[k].second
                         << ", distance=" << true_neighbors[k].first << "\n";
                }
            }
            file << "\n";
        }

        if (debug_mode) {
            std::cout << "Ground truth generated and saved to " << filename << std::endl;
        }
    }

    // Оценка точности графа
    float evaluate_graph_accuracy(const std::string& ground_truth_file) {
        thrust::host_vector<int> h_graph = d_graph;
        thrust::host_vector<float> h_distances = d_distances;

        std::ifstream file(ground_truth_file);
        if (!file) {
            std::cerr << "Error opening ground truth file: " << ground_truth_file << std::endl;
            return -1.0f;
        }

        int dim = d_points.size() / num_points;
        int correct = 0;
        int total = 0;

        for (int i = 0; i < num_points; ++i) {
            // Читаем эталонные соседи
            std::vector<int> true_neighbors;
            std::vector<float> true_distances;

            std::string line;
            while (std::getline(file, line)) {
                if (line.find("Point " + std::to_string(i) + ":") != std::string::npos) {
                    break;
                }
            }

            while (std::getline(file, line)) {
                if (line.empty()) break;

                size_t colon_pos = line.find(':');
                if (colon_pos == std::string::npos) continue;

                int neighbor_idx = std::stoi(line.substr(colon_pos + 1, line.find(',') - colon_pos - 2));
                float distance = std::stof(line.substr(line.find('=') + 1));

                true_neighbors.push_back(neighbor_idx);
                true_distances.push_back(distance);
            }

            // Сравниваем с нашими соселями
            for (int k = 0; k < knn_degree; ++k) {
                int our_neighbor = h_graph[i * knn_degree + k];
                float our_distance = h_distances[i * knn_degree + k];

                // Проверяем, есть ли этот сосед в эталонных данных
                bool found = false;
                for (int m = 0; m < knn_degree; ++m) {
                    if (true_neighbors[m] == our_neighbor) {
                        // Проверяем, что расстояние близко
                        if (std::abs(our_distance - true_distances[m]) < epsilon) {
                            correct++;
                            found = true;
                            break;
                        }
                    }
                }

                if (!found) {
                    // Проверяем, есть ли более близкий сосед в эталонных данных
                    for (int m = 0; m < knn_degree; ++m) {
                        if (true_neighbors[m] != our_neighbor) {
                            float true_dist = true_distances[m];
                            if (true_dist < our_distance) {
                                // Наш сосед хуже, чем есть в эталонных данных
                                break;
                            }
                        }
                    }
                }

                total++;
            }
        }

        float accuracy = static_cast<float>(correct) / total;
        if (debug_mode) {
            std::cout << "Graph accuracy: " << accuracy * 100 << "%" << std::endl;
        }

        return accuracy;
    }

    // Сброс графа для подмножества
    void reset_graph_for_subset(int subset_size) {
        if (subset_size > num_points) {
            std::cerr << "Subset size cannot be larger than total points" << std::endl;
            return;
        }

        // Создаем случайный подмножество индексов
        thrust::host_vector<int> indices(num_points);
        thrust::counting_iterator<int> seq(0);
        thrust::copy(seq, seq + num_points, indices.begin());

        // Перемешиваем индексы
        std::random_device rd;
        std::mt19937 g(rd());
        std::shuffle(indices.begin(), indices.end(), g);

        // Берем первые subset_size индексов
        thrust::device_vector<int> d_subset_indices(indices.begin(), indices.begin() + subset_size);

        // Сбрасываем граф для выбранных точек
        dim3 blocks((subset_size + 255) / 256);
        dim3 threads(256);

        // Просто перезапускаем построение графа для подмножества
        // В реальной реализации можно было бы оптимизировать это
        build_knn_kernel<<<blocks, threads>>>(thrust::raw_pointer_cast(d_points.data()),
                                             subset_size, d_points.size() / num_points,
                                             knn_degree,
                                             thrust::raw_pointer_cast(d_graph.data()),
                                             thrust::raw_pointer_cast(d_distances.data()));

        cudaError_t err = cudaGetLastError();
        if (err != cudaSuccess) {
            std::cerr << "CUDA error in reset_graph_for_subset: " << cudaGetErrorString(err) << std::endl;
        }

        if (debug_mode) {
            std::cout << "Graph reset for subset of " << subset_size << " points" << std::endl;
        }
    }

    // Установка параметров отладки
    void set_debug_mode(bool enable) {
        debug_mode = enable;
    }

    // Установка подробного режима запросов
    void set_verbose_queries(bool enable) {
        verbose_queries = enable;
    }

    // Получение информации о графе
    void print_graph_info() {
        std::cout << "KNN Graph Information:" << std::endl;
        std::cout << "  Number of points: " << num_points << std::endl;
        std::cout << "  KNN degree: " << knn_degree << std::endl;
        std::cout << "  Epsilon: " << epsilon << std::endl;
        std::cout << "  Debug mode: " << (debug_mode ? "ON" : "OFF") << std::endl;
        std::cout << "  Query batch size: " << query_batch_size << std::endl;
        std::cout << "  Verbose queries: " << (verbose_queries ? "ON" : "OFF") << std::endl;
    }
};

