```go
package transport

import (
	"crypto/tls"
	"crypto/x509"
	"net"
	"net/http"
	"time"

	"github.com/elastic/go-elasticsearch/v8"
)

// TransportBuilder - структура для настройки HTTP-транспорта
type TransportBuilder struct {
	// Поля для хранения конфигурации
	proxyURL          string
	proxyUsername     string
	proxyPassword     string
	disableProxy      bool
	clientUsername    string
	clientPassword    string
	customHeaders     map[string]string
	globalTimeout     time.Duration
	tlsConfig         *tls.Config
}

// Transport - интерфейс для HTTP-транспорта (можно заменить на конкретную реализацию)
type Transport interface {
	RoundTrip(*http.Request) (*http.Response, error)
}

// Build создает HTTP-транспорт для Elasticsearch с заданными настройками
func (b *TransportBuilder) Build() (*elasticsearch.Client, error) {
	// Создаем базовую конфигурацию клиента
	cfg := elasticsearch.Config{
		Addresses: []string{"http://localhost:9200"}, // По умолчанию
		Username:  b.clientUsername,
		Password:  b.clientPassword,
		Headers:   b.customHeaders,
		Timeout:   b.globalTimeout,
	}

	// Настройка прокси
	if !b.disableProxy {
		proxyURL := b.proxyURL
		if proxyURL == "" {
			// Используем системные прокси по умолчанию
			proxyURL = "http://proxy.example.com:8080" // Пример, можно заменить на логику определения системных прокси
		}

		transport := &http.Transport{
			Proxy: http.ProxyURL(parseProxyURL(proxyURL)),
			DialContext: func(ctx context.Context, network, addr string) (net.Conn, error) {
				d := net.Dialer{}
				if b.proxyUsername != "" && b.proxyPassword != "" {
					d.Dial = func(network, addr string) (net.Conn, error) {
						conn, err := d.Dial(network, addr)
						if err != nil {
							return nil, err
						}
						// Здесь можно добавить аутентификацию прокси, если нужно
						return conn, nil
					}
				}
				return d.DialContext(ctx, network, addr)
			},
		}

		// Настройка TLS, если есть
		if b.tlsConfig != nil {
			transport.TLSClientConfig = b.tlsConfig
		}

		cfg.Transport = transport
	} else {
		// Если прокси отключен, используем стандартный транспорт
		transport := &http.Transport{
			TLSClientConfig: b.tlsConfig,
		}
		cfg.Transport = transport
	}

	// Создаем клиент
	client, err := elasticsearch.NewClient(cfg)
	if err != nil {
		return nil, err
	}

	return client, nil
}

// parseProxyURL - вспомогательная функция для парсинга URL прокси
func parseProxyURL(proxyURL string) *url.URL {
	u, err := url.Parse(proxyURL)
	if err != nil {
		// Логируем ошибку и возвращаем nil, чтобы использовать системные настройки
		return nil
	}
	return u
}

// SetProxy - устанавливает настройки прокси
func (b *TransportBuilder) SetProxy(proxyURL, username, password string) {
	b.proxyURL = proxyURL
	b.proxyUsername = username
	b.proxyPassword = password
}

// DisableProxy - отключает использование прокси
func (b *TransportBuilder) DisableProxy() {
	b.disableProxy = true
}

// SetClientAuth - устанавливает учетные данные для аутентификации в Elasticsearch
func (b *TransportBuilder) SetClientAuth(username, password string) {
	b.clientUsername = username
	b.clientPassword = password
}

// SetCustomHeaders - добавляет HTTP-заголовки
func (b *TransportBuilder) SetCustomHeaders(headers map[string]string) {
	b.customHeaders = headers
}

// SetGlobalTimeout - устанавливает глобальный тайм-аут
func (b *TransportBuilder) SetGlobalTimeout(timeout time.Duration) {
	b.globalTimeout = timeout
}

// SetTLSConfig - устанавливает конфигурацию TLS
func (b *TransportBuilder) SetTLSConfig(tlsConfig *tls.Config) {
	b.tlsConfig = tlsConfig
}

// Пример создания TLS конфигурации с проверкой сертификата
func NewTLSConfig(caCert []byte) (*tls.Config, error) {
	certPool := x509.NewCertPool()
	if !certPool.AppendCertsFromPEM(caCert) {
		return nil, errors.New("failed to add CA certificate")
	}

	return &tls.Config{
		RootCAs: certPool,
	}, nil
}
```

