#include <opencv2/opencv.hpp>

// Функция для сравнения результатов
bool compareResults(const unsigned char* expected, const unsigned char* actual,
                   int width, int height, int tolerance = 0) {
    for (int y = 0; y < height; y++) {
        for (int x = 0; x < width; x++) {
            if (abs(expected[y * width + x] - actual[y * width + x]) > tolerance) {
                return false;
            }
        }
    }
    return true;
}

