void saveImage(const unsigned char* data, int width, int height,
              const std::string& filename) {
    cv::Mat image(height, width, CV_8UC1, const_cast<unsigned char*>(data));
    cv::imwrite(filename, image);
}

// Пример использования регрессионного теста
void runRegressionTest(int width, int height, int generations) {
    // Создаем тестовые данные
    unsigned char* testInput = new unsigned char[width * height];
    unsigned char* testOutput = new unsigned char[width * height];
    unsigned char* expectedOutput = new unsigned char[width * height];

    // Инициализируем тестовые данные (например, глитчер)
    for (int y = 0; y < height; y++) {
