        }
    }

    // Запускаем симуляцию
    simulateGameOfLife(testInput, testOutput, width, height, generations);

    // Сохраняем результаты для визуальной проверки
    saveImage(testInput, width, height, "test_input.png");
    saveImage(testOutput, width, height, "test_output.png");

